<body>
    <div id="app-container">
        <div id="controls-container">
            <div class="control-row">
                <label for="slider-points">collocation points</label>
                <input type="range" id="slider-points" min="5" max="30" step="1" value="20">
                <span id="label-points">20</span>
            </div>
            <div class="control-row">
                <label for="slider-time">t</label>
                <input type="range" id="slider-time" min="0.001" max="0.2" step="0.001" value="0.05">
                <span id="label-time">0.050</span>
            </div>
        </div>
        <div id="plot-container"></div>
    </div>

    <script>
        // Function to calculate temperature using Fourier series
        function calculateTemperature(x, t) {
            let sum = 0;
            const numTerms = 100;

            for (let n = 1; n <= numTerms; n++) {
                const term = (1 / (n * n)) * Math.sin(n * Math.PI / 2) * 
                           Math.sin(n * Math.PI * x) * Math.exp(-2 * n * n * Math.PI * Math.PI * t);
                sum += term;
            }

            return (8 / (Math.PI * Math.PI)) * sum;
        }

        // Function to calculate Chebyshev-Gauss-Lobatto points
        function getChebyshevPoints(numPoints) {
            const points = [];
            const N = numPoints - 1;

            for (let j = 0; j <= N; j++) {
                const x = (Math.cos(j * Math.PI / N) + 1) / 2;
                points.push(x);
            }

            return points;
        }

        // Function to update the plot
        function updatePlot() {
            const numPoints = parseInt(document.getElementById('slider-points').value);
            const t = parseFloat(document.getElementById('slider-time').value);

            // Generate analytical solution data
            const xAnalytical = [];
            const yAnalytical = [];
            const numAnalyticalPoints = 200;

            for (let i = 0; i <= numAnalyticalPoints; i++) {
                const x = i / numAnalyticalPoints;
                xAnalytical.push(x);
                yAnalytical.push(calculateTemperature(x, t));
            }

            // Generate numerical solution data (Chebyshev points)
            const xNumerical = getChebyshevPoints(numPoints);
            const yNumerical = xNumerical.map(x => calculateTemperature(x, t));

            // Define traces
            const analyticalTrace = {
                x: xAnalytical,
                y: yAnalytical,
                type: 'scatter',
                mode: 'lines',
                line: { color: 'blue', width: 3 }
            };

            const numericalTrace = {
                x: xNumerical,
                y: yNumerical,
                type: 'scatter',
                mode: 'markers',
                marker: { color: 'red', size: 8 }
            };

            // Define layout
            const layout = {
                xaxis: {
                    title: 'x',
                    range: [0, 1],
                    tickmode: 'linear',
                    dtick: 0.2,
                    gridcolor: '#e0e0e0'
                },
                yaxis: {
                    title: 'T',
                    range: [-0.1, 1.1],
                    tickmode: 'linear',
                    dtick: 0.2,
                    gridcolor: '#e0e0e0'
                },
                showlegend: false,
                margin: { t: 20, r: 20, b: 40, l: 40 }
            };

            // Update plot
            Plotly.react('plot-container', [analyticalTrace, numericalTrace], layout);
        }

        // Event listeners for sliders
        document.getElementById('slider-points').addEventListener('input', function() {
            document.getElementById('label-points').textContent = this.value;
            updatePlot();
        });

        document.getElementById('slider-time').addEventListener('input', function() {
            document.getElementById('label-time').textContent = parseFloat(this.value).toFixed(3);
            updatePlot();
        });

        // Initial plot on page load
        updatePlot();
    </script>
</body>
</html>
