        body {
            margin: 0;
            padding: 0;
            font-family: Arial, sans-serif;
            background-color: #f0f0f0;
        }

        #app-container {
            display: flex;
            height: 100vh;
            max-width: 1200px;
            margin: 0 auto;
            background-color: white;
            box-shadow: 0 0 10px rgba(0,0,0,0.1);
        }

        #control-panel {
            width: 300px;
            padding: 20px;
            background-color: #f8f8f8;
            border-right: 1px solid #ddd;
            overflow-y: auto;
        }

        #visualization-panel {
            flex: 1;
            padding: 20px;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        h3 {
            margin-top: 20px;
            margin-bottom: 15px;
            color: #333;
        }

        .slider-container {
            margin-bottom: 15px;
        }

        .slider-container label {
            display: inline-block;
            width: 30px;
            font-weight: bold;
            color: #555;
        }

        .slider-container input[type="range"] {
            width: 180px;
            vertical-align: middle;
        }

        .slider-container span {
            display: inline-block;
            width: 50px;
            text-align: right;
            color: #333;
        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <h3>Means</h3>
            <div>
                <div class="slider-container">
                    <label>W</label>
                    <input type="range" id="slider-mean-w" min="-10" max="10" step="0.1" value="0">
                    <span id="value-mean-w">0</span>
                </div>
                <div class="slider-container">
                    <label>X</label>
                    <input type="range" id="slider-mean-x" min="-10" max="10" step="0.1" value="0">
                    <span id="value-mean-x">0</span>
                </div>
                <div class="slider-container">
                    <label>Y</label>
                    <input type="range" id="slider-mean-y" min="-10" max="10" step="0.1" value="0">
                    <span id="value-mean-y">0</span>
                </div>
                <div class="slider-container">
                    <label>Z</label>
                    <input type="range" id="slider-mean-z" min="-10" max="10" step="0.1" value="0">
                    <span id="value-mean-z">0</span>
                </div>
            </div>

            <h3>Standard Deviations</h3>
            <div>
                <div class="slider-container">
                    <label>W</label>
                    <input type="range" id="slider-sd-w" min="1" max="10" step="0.1" value="1">
                    <span id="value-sd-w">1</span>
                </div>
                <div class="slider-container">
                    <label>X</label>
                    <input type="range" id="slider-sd-x" min="1" max="10" step="0.1" value="1">
                    <span id="value-sd-x">1</span>
                </div>
                <div class="slider-container">
                    <label>Y</label>
                    <input type="range" id="slider-sd-y" min="1" max="10" step="0.1" value="1">
                    <span id="value-sd-y">1</span>
                </div>
                <div class="slider-container">
                    <label>Z</label>
                    <input type="range" id="slider-sd-z" min="1" max="10" step="0.1" value="1">
                    <span id="value-sd-z">1</span>
                </div>
            </div>
        </div>

        <div id="visualization-panel"></div>
    </div>

    <script>
        let means = {w: 0, x: 0, y: 0, z: 0};
        let sds = {w: 1, x: 1, y: 1, z: 1};
        let anovaResults = {};

        function setup() {
            let canvas = createCanvas(600, 600);
            canvas.parent('visualization-panel');
            noLoop();

            // Set up event listeners
            const groups = ['w', 'x', 'y', 'z'];
            groups.forEach(group => {
                document.getElementById(`slider-mean-${group}`).addEventListener('input', updateAndRedraw);
                document.getElementById(`slider-sd-${group}`).addEventListener('input', updateAndRedraw);
            });

            // Initial calculation and draw
            updateAndRedraw();
        }

        function updateAndRedraw() {
            // Read all slider values
            const groups = ['w', 'x', 'y', 'z'];
            groups.forEach(group => {
                means[group] = parseFloat(document.getElementById(`slider-mean-${group}`).value);
                sds[group] = parseFloat(document.getElementById(`slider-sd-${group}`).value);

                // Update display values
                document.getElementById(`value-mean-${group}`).textContent = means[group].toFixed(1);
                document.getElementById(`value-sd-${group}`).textContent = sds[group].toFixed(1);
            });

            // Perform ANOVA calculations
            calculateANOVA();

            // Redraw
            redraw();
        }

        function calculateANOVA() {
            const k = 4; // number of groups
            const n = 10; // number of subjects per group

            // Calculate Grand Mean
            const groupMeans = [means.w, means.x, means.y, means.z];
            const grandMean = groupMeans.reduce((sum, mean) => sum + mean, 0) / k;

            // Calculate SS Between
            const ssBetween = n * groupMeans.reduce((sum, mean) => sum + Math.pow(mean - grandMean, 2), 0);

            // Calculate MS Between
            const msBetween = ssBetween / (k - 1);

            // Calculate SS Within
            const groupSDs = [sds.w, sds.x, sds.y, sds.z];
            const ssWithin = groupSDs.reduce((sum, sd) => sum + (Math.pow(sd, 2) * (n - 1)), 0);

            // Calculate MS Within
            const msWithin = ssWithin / (n * k - k);

            // Calculate F-Ratio
            const fRatio = msWithin > 0 ? msBetween / msWithin : 0;

            anovaResults = {
                grandMean: grandMean,
                msBetween: msBetween,
                msWithin: msWithin,
                fRatio: fRatio
            };
        }

        function draw() {
            background(255);

            // Draw Group Data Plot (top half)
            drawGroupDataPlot();

            // Draw ANOVA Results Plot (bottom half)
            drawANOVAResultsPlot();
        }

        function drawGroupDataPlot() {
            push();
            translate(50, 50);

            const plotWidth = 500;
            const plotHeight = 200;

            // Draw F-ratio text
            fill(0);
            textAlign(CENTER);
            textSize(16);
            let fRatioText = anovaResults.fRatio < 0.01 ? 
                anovaResults.fRatio.toFixed(4) : 
                anovaResults.fRatio.toFixed(2);
            text(`F ratio = ${fRatioText}`, plotWidth / 2, -20);

            // Draw axes
            stroke(0);
            strokeWeight(2);
            line(0, 0, 0, plotHeight);
            line(0, plotHeight, plotWidth, plotHeight);

            // Y-axis labels
            textAlign(RIGHT);
            textSize(12);
            for (let i = -10; i <= 10; i += 5) {
                let y = map(i, -10, 10, plotHeight, 0);
                text(i, -10, y + 5);
                stroke(200);
                strokeWeight(1);
                line(0, y, plotWidth, y);
                stroke(0);
                strokeWeight(2);
            }

            // X-axis labels
            textAlign(CENTER);
            const groups = ['W', 'X', 'Y', 'Z'];
            const groupPositions = [100, 200, 300, 400];
            groups.forEach((group, i) => {
                text(group, groupPositions[i], plotHeight + 20);
            });

            // Draw grand mean line
            stroke(0);
            strokeWeight(1);
            drawingContext.setLineDash([5, 5]);
            let grandMeanY = map(anovaResults.grandMean, -10, 10, plotHeight, 0);
            line(0, grandMeanY, plotWidth, grandMeanY);
            drawingContext.setLineDash([]);

            // Draw group bars
            const groupKeys = ['w', 'x', 'y', 'z'];
            groupKeys.forEach((key, i) => {
                let x = groupPositions[i];
                let mean = means[key];
                let sd = sds[key];

                let topY = map(mean + sd, -10, 10, plotHeight, 0);
                let bottomY = map(mean - sd, -10, 10, plotHeight, 0);
                let meanY = map(mean, -10, 10, plotHeight, 0);

                // Draw bar
                fill(245, 127, 41); // Orange
                noStroke();
                rectMode(CENTER);
                rect(x, (topY + bottomY) / 2, 60, bottomY - topY);

                // Draw mean line
                stroke(0);
                strokeWeight(2);
                line(x - 30, meanY, x + 30, meanY);
            });

            pop();
        }

        function drawANOVAResultsPlot() {
            push();
            translate(50, 350);

            const plotWidth = 500;
            const plotHeight = 150;

            // Title
            fill(0);
            textAlign(CENTER);
            textSize(16);
            text('Analysis of the F-Ratio', plotWidth / 2, -20);

            // Draw axes
            stroke(0);
            strokeWeight(2);
            line(0, 0, 0, plotHeight);
            line(0, plotHeight, plotWidth, plotHeight);

            // X-axis labels
            textAlign(CENTER);
            textSize(12);
            for (let i = 0; i <= 200; i += 50) {
                let x = map(i, 0, 200, 0, plotWidth);
                text(i, x, plotHeight + 20);
                if (i > 0) {
                    stroke(200);
                    strokeWeight(1);
                    line(x, 0, x, plotHeight);
                    stroke(0);
                    strokeWeight(2);
                }
            }

            // Draw bars
            const barHeight = 30;
            const barSpacing = 50;
            const bars = [
                {label: 'MS Between', value: anovaResults.msBetween, color: [70, 145, 165]},
                {label: 'MS Within', value: anovaResults.msWithin, color: [212, 157, 60]},
                {label: 'F-Ratio', value: anovaResults.fRatio, color: [140, 58, 141]}
            ];

            textAlign(RIGHT);
            bars.forEach((bar, i) => {
                let y = i * barSpacing + 20;

                // Label
                fill(0);
                text(bar.label, -10, y + barHeight/2 + 5);

                // Bar
                let barWidth = map(min(bar.value, 200), 0, 200, 0, plotWidth);
                fill(bar.color[0], bar.color[1], bar.color[2]);
                noStroke();
                rect(0, y, barWidth, barHeight);
            });

            pop();
        }
    </script>
</body>
</html>
