<body>
    <div class="main-container">
        <div id="control-panel">
            <h3 style="color: blue;">GEV distribution 1</h3>
            <div class="slider-container">
                <label>location μ<sub>1</sub></label>
                <input type="range" id="slider-mu1" min="2" max="6" step="0.01" value="3.87">
                <input type="text" id="display-mu1" readonly value="3.87">
            </div>
