    <script>
        // Get all slider elements
        const sliders = {
            mu1: document.getElementById('slider-mu1'),
            sigma1: document.getElementById('slider-sigma1'),
            xi1: document.getElementById('slider-xi1'),
            mu2: document.getElementById('slider-mu2'),
            sigma2: document.getElementById('slider-sigma2'),
            xi2: document.getElementById('slider-xi2'),
            pmax: document.getElementById('slider-pmax')
        };

        // Get all display elements
        const displays = {
            mu1: document.getElementById('display-mu1'),
            sigma1: document.getElementById('display-sigma1'),
            xi1: document.getElementById('display-xi1'),
            mu2: document.getElementById('display-mu2'),
            sigma2: document.getElementById('display-sigma2'),
            xi2: document.getElementById('display-xi2'),
            pmax: document.getElementById('display-pmax')
        };

        // GEV PDF calculation
        function gevPDF(x, mu, sigma, xi) {
            if (Math.abs(xi) < 1e-10) {
                // Gumbel distribution (xi = 0)
                const z = (x - mu) / sigma;
                const t = Math.exp(-z);
                return (1 / sigma) * t * Math.exp(-t);
            } else {
                // GEV distribution (xi != 0)
                const z = (x - mu) / sigma;
                const t_val = 1 + xi * z;
                if (t_val <= 0) return 0;
                const t = Math.pow(t_val, -1 / xi);
                return (1 / sigma) * Math.pow(t, xi + 1) * Math.exp(-t);
            }
        }

        // GEV Return Level calculation
        function gevReturnLevel(p, mu, sigma, xi) {
            const prob = 1 - 1 / p;
            if (Math.abs(xi) < 1e-10) {
                // Gumbel distribution (xi = 0)
                return mu - sigma * Math.log(-Math.log(prob));
            } else {
                // GEV distribution (xi != 0)
                return mu - (sigma / xi) * (1 - Math.pow(-Math.log(prob), -xi));
            }
        }

        // Update function
        function update() {
            // Update display values
            Object.keys(sliders).forEach(key => {
                displays[key].value = sliders[key].value;
            });

            // Get current values
            const params = {
                mu1: parseFloat(sliders.mu1.value),
                sigma1: parseFloat(sliders.sigma1.value),
                xi1: parseFloat(sliders.xi1.value),
                mu2: parseFloat(sliders.mu2.value),
                sigma2: parseFloat(sliders.sigma2.value),
                xi2: parseFloat(sliders.xi2.value),
                pmax: parseFloat(sliders.pmax.value)
            };

            // Update PDF plot
            updatePDFPlot(params);

            // Update Return Level plot
            updateReturnLevelPlot(params);
        }

        function updatePDFPlot(params) {
            const xValues = [];
            const yValues1 = [];
            const yValues2 = [];

            // Generate x values from 2.5 to 7
            for (let x = 2.5; x <= 7; x += 0.01) {
                xValues.push(x);
                yValues1.push(gevPDF(x, params.mu1, params.sigma1, params.xi1));
                yValues2.push(gevPDF(x, params.mu2, params.sigma2, params.xi2));
            }

            const trace1 = {
                x: xValues,
                y: yValues1,
                type: 'scatter',
                mode: 'lines',
                name: 'GEV 1',
                line: { color: 'blue' },
                fill: 'tozeroy',
                fillcolor: 'rgba(0, 0, 255, 0.2)'
            };

            const trace2 = {
                x: xValues,
                y: yValues2,
                type: 'scatter',
                mode: 'lines',
                name: 'GEV 2',
                line: { color: 'red' },
                fill: 'tozeroy',
                fillcolor: 'rgba(255, 0, 0, 0.2)'
            };

            const layout = {
                title: 'PDFs of GEV Distributions 1 and 2',
                xaxis: { range: [2.5, 7], zeroline: false },
                yaxis: { zeroline: false },
                showlegend: true,
                legend: { x: 0.7, y: 1 }
            };

            Plotly.newPlot('plot-pdf', [trace1, trace2], layout);
        }

        function updateReturnLevelPlot(params) {
            const pValues = [];
            const yValues1 = [];
            const yValues2 = [];

            // Generate return periods from 1 to pmax
            for (let p = 1; p <= params.pmax; p += Math.max(1, params.pmax / 200)) {
                pValues.push(p);
                yValues1.push(gevReturnLevel(p, params.mu1, params.sigma1, params.xi1));
                yValues2.push(gevReturnLevel(p, params.mu2, params.sigma2, params.xi2));
            }

            const trace1 = {
                x: pValues,
                y: yValues1,
                type: 'scatter',
                mode: 'lines',
                name: 'GEV 1',
                line: { color: 'blue' }
            };

            const trace2 = {
                x: pValues,
                y: yValues2,
                type: 'scatter',
                mode: 'lines',
                name: 'GEV 2',
                line: { color: 'red' }
            };

            const layout = {
                xaxis: { 
                    title: 'Return Period (years)',
                    range: [0, params.pmax]
                },
                yaxis: { title: 'Maximum Sea Level (meters)' },
                showlegend: true,
                legend: { x: 0.1, y: 1 },
                margin: { t: 20 }
            };

            Plotly.newPlot('plot-return-level', [trace1, trace2], layout);
        }

        // Add event listeners
        Object.keys(sliders).forEach(key => {
            sliders[key].addEventListener('input', update);
        });

        // Initial plot
        update();
    </script>
</body>
</html>
