    <script>
        // Wait for MathJax to load
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']],
                displayMath: [['\\[', '\\]']]
            }
        };

        // Global variables
        let currentSignal = 'triangle';
        let a = 2.0;
        let b = 4.0;
        let T = 2.0;

        // Signal definitions
        const signals = {
            triangle: [
                [0, 0], [3, 4], [6, 0]
            ],
            tophat: [
                [1, 0], [1, 4], [5, 4], [5, 0]
            ],
            stairstep: [
                [1, 0], [1, 1], [2, 1], [2, 4], [4, 4], [4, 1], [5, 1], [5, 0]
            ],
            rampdown: [
                [0, 4], [6, 0]
            ],
            uh: {
                part1: [[1, 0], [1, 4], [1.5, 4], [1.5, 1], [2.5, 1], [2.5, 4], [3, 4], [3, 0]],
                part2: [[4, 0], [4, 4], [4.5, 4], [4.5, 2], [5.5, 2], [5.5, 4], [6, 4], [6, 0]]
            }
        };

        // Initialize event listeners
        document.addEventListener('DOMContentLoaded', function() {
            // Signal buttons
            const buttons = ['rampdown', 'tophat', 'stairstep', 'triangle', 'uh'];
            buttons.forEach(btn => {
                document.getElementById(`btn-${btn}`).addEventListener('click', function() {
                    document.querySelectorAll('#control-panel button').forEach(b => b.classList.remove('active'));
                    this.classList.add('active');
                    currentSignal = btn;
                    updatePlots();
                });
            });

            // Sliders
            document.getElementById('slider-a').addEventListener('input', function() {
                a = parseFloat(this.value);
                T = b / a;
                document.getElementById('slider-a-value').textContent = a.toFixed(1);
                document.getElementById('slider-t-value').textContent = T.toFixed(1);
                document.getElementById('slider-t').value = T;
                updatePlots();
            });

            document.getElementById('slider-b').addEventListener('input', function() {
                b = parseFloat(this.value);
                T = b / a;
                document.getElementById('slider-b-value').textContent = b.toFixed(1);
                document.getElementById('slider-t-value').textContent = T.toFixed(1);
                document.getElementById('slider-t').value = T;
                updatePlots();
            });

            document.getElementById('slider-t').addEventListener('input', function() {
                T = parseFloat(this.value);
                b = a * T;
                document.getElementById('slider-t-value').textContent = T.toFixed(1);
                document.getElementById('slider-b-value').textContent = b.toFixed(1);
                document.getElementById('slider-b').value = b;
                updatePlots();
            });

            // Checkboxes
            ['x', 'y', 'z', 'w'].forEach((sig, idx) => {
                document.getElementById(`check-${sig}`).addEventListener('change', function() {
                    Plotly.restyle('plot-bottom', {visible: this.checked}, [idx]);
                });
            });

            // Initial plot
            setTimeout(updatePlots, 100); // Wait for MathJax
        });

        // Generate signal data
        function generateSignalData(signalDef, transform = null) {
            const tMin = -10;
            const tMax = 20;
            const dt = 0.01;
            const xData = [];
            const yData = [];

            for (let t = tMin; t <= tMax; t += dt) {
                let tTransformed = transform ? transform(t) : t;
                let value = evaluateSignal(signalDef, tTransformed);
                xData.push(t);
                yData.push(value);
            }

            return {x: xData, y: yData};
        }

        // Evaluate signal at given time
        function evaluateSignal(signalDef, t) {
            if (Array.isArray(signalDef)) {
                // Single part signal
                return interpolateSignal(signalDef, t);
            } else {
                // Multi-part signal (UH)
                let val1 = interpolateSignal(signalDef.part1, t);
                let val2 = interpolateSignal(signalDef.part2, t);
                return Math.max(val1, val2);
            }
        }

        // Linear interpolation for piecewise signals
        function interpolateSignal(points, t) {
            if (t < points[0][0] || t > points[points.length - 1][0]) {
                return 0;
            }

            for (let i = 0; i < points.length - 1; i++) {
                if (t >= points[i][0] && t <= points[i + 1][0]) {
                    const t1 = points[i][0];
                    const t2 = points[i + 1][0];
                    const y1 = points[i][1];
                    const y2 = points[i + 1][1];
                    return y1 + (y2 - y1) * (t - t1) / (t2 - t1);
                }
            }

            return 0;
        }

        // Update both plots
        function updatePlots() {
            const signalDef = signals[currentSignal];

            // Generate data for all signals
            const xData = generateSignalData(signalDef);
            const yData = generateSignalData(signalDef, t => a * t - b);
            const zData = generateSignalData(signalDef, t => t - b);
            const wData = generateSignalData(signalDef, t => a * t);

            // Common layout settings
            const annotations = [
                {
                    x: T,
                    y: 0,
                    xref: 'x',
                    yref: 'y',
                    text: 'T',
                    showarrow: false,
                    font: {color: 'red', size: 14},
                    yshift: 15
                },
                {
                    x: b,
                    y: 0,
                    xref: 'x',
                    yref: 'y',
                    text: 'b',
                    showarrow: false,
                    font: {color: 'green', size: 14},
                    yshift: 15
                }
            ];

            const shapes = [
                {
                    type: 'circle',
                    x0: T - 0.1,
                    y0: -0.1,
                    x1: T + 0.1,
                    y1: 0.1,
                    fillcolor: 'red',
                    line: {color: 'red'}
                },
                {
                    type: 'circle',
                    x0: b - 0.1,
                    y0: -0.1,
                    x1: b + 0.1,
                    y1: 0.1,
                    fillcolor: 'green',
                    line: {color: 'green'}
                }
            ];

            // Top plot
            const topTrace = {
                x: xData.x,
                y: xData.y,
                mode: 'lines',
                name: '\\(x(t)\\)',
                line: {color: 'rgb(70, 130, 180)', width: 2.5}
            };

            const topLayout = {
                title: 'signal',
                xaxis: {title: 'time (s)', range: [-10, 20], zeroline: true},
                yaxis: {range: [-0.5, 4.5], zeroline: true},
                showlegend: true,
                legend: {x: 0.01, y: 0.75},
                annotations: annotations,
                shapes: shapes
            };

            Plotly.newPlot('plot-top', [topTrace], topLayout);

            // Bottom plot
            const bottomTraces = [
                {
                    x: xData.x,
                    y: xData.y,
                    mode: 'lines',
                    name: '\\(x(t)\\)',
                    line: {color: 'rgb(70, 130, 180)', width: 2.5},
                    visible: document.getElementById('check-x').checked
                },
                {
                    x: yData.x,
                    y: yData.y,
                    mode: 'lines',
                    name: '\\(y(t) = x(at-b) = x(a(t-T))\\)',
                    line: {color: 'red', width: 2.5},
                    visible: document.getElementById('check-y').checked
                },
                {
                    x: zData.x,
                    y: zData.y,
                    mode: 'lines',
                    name: '\\(z(t) = x(t-b)\\)',
                    line: {color: 'olivedrab', width: 2.5},
                    visible: document.getElementById('check-z').checked
                },
                {
                    x: wData.x,
                    y: wData.y,
                    mode: 'lines',
                    name: '\\(w(t) = x(at)\\)',
                    line: {color: 'saddlebrown', width: 2.5},
                    visible: document.getElementById('check-w').checked
                }
            ];

            const bottomLayout = {
                xaxis: {title: 'time (s)', range: [-10, 20], zeroline: true},
                yaxis: {range: [-0.5, 4.5], zeroline: true},
                showlegend: true,
                legend: {x: 0.01, y: 0.98},
                annotations: annotations,
                shapes: shapes
            };

            Plotly.newPlot('plot-bottom', bottomTraces, bottomLayout);
        }
    </script>
</body>
</html>
