    <!-- Scripts -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Global variables
        let scene, camera, renderer, controls;
        let mainGroup;

        // Initialize the scene
        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera setup
            camera = new THREE.PerspectiveCamera(
                60,
                window.innerWidth / window.innerHeight,
                0.1,
                1000
            );
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);

            // Renderer setup
            const canvas = document.getElementById('main-canvas');
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);

            // Controls setup
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const hemisphereLight = new THREE.HemisphereLight(0xffffff, 0x444444);
            scene.add(hemisphereLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 10, 5);
            scene.add(directionalLight);

            // Main group for all geometry
            mainGroup = new THREE.Group();
            scene.add(mainGroup);

            // Initial scene update
            updateScene();

            // Start animation loop
            animate();
        }

        // Create curved triangle geometry
        function createCurvedTriangle(trim, colorCenter, colorEdge) {
            const geometry = new THREE.BufferGeometry();
            const vertices = [];
            const colors = [];
            const edgeVertices = [];

            // Calculate triangle vertices on sphere
            const radius = 1;
            const angle = 2 * Math.PI / 3;

            // Triangle vertices
            const v1 = new THREE.Vector3(0, radius, 0);
            const v2 = new THREE.Vector3(radius * Math.sin(angle), radius * Math.cos(angle), 0);
            const v3 = new THREE.Vector3(radius * Math.sin(2 * angle), radius * Math.cos(2 * angle), 0);

            // Rotate to proper position
            const rotMatrix = new THREE.Matrix4().makeRotationX(-Math.PI / 3);
            v1.applyMatrix4(rotMatrix);
            v2.applyMatrix4(rotMatrix);
            v3.applyMatrix4(rotMatrix);

            // Center point
            const center = new THREE.Vector3().addVectors(v1, v2).add(v3).divideScalar(3);
            center.normalize().multiplyScalar(radius * trim);

            // Generate curved edges
            const segments = 20;
            const edge1 = [], edge2 = [], edge3 = [];

            for (let i = 0; i <= segments; i++) {
                const t = i / segments;

                // Edge 1: v1 to v2
                const p1 = new THREE.Vector3().lerpVectors(v1, v2, t);
                p1.normalize().multiplyScalar(radius);
                edge1.push(p1);

                // Edge 2: v2 to v3
                const p2 = new THREE.Vector3().lerpVectors(v2, v3, t);
                p2.normalize().multiplyScalar(radius);
                edge2.push(p2);

                // Edge 3: v3 to v1
                const p3 = new THREE.Vector3().lerpVectors(v3, v1, t);
                p3.normalize().multiplyScalar(radius);
                edge3.push(p3);
            }

            // Build triangulated surface
            const allEdgePoints = [...edge1.slice(0, -1), ...edge2.slice(0, -1), ...edge3.slice(0, -1)];

            // Add center vertex
            vertices.push(center.x, center.y, center.z);
            colors.push(...colorCenter);

            // Add edge vertices and create faces
            for (let i = 0; i < allEdgePoints.length; i++) {
                const p = allEdgePoints[i];
                vertices.push(p.x, p.y, p.z);
                colors.push(...colorEdge);
                edgeVertices.push(p.x, p.y, p.z);
            }

            // Create faces
            const indices = [];
            for (let i = 1; i <= allEdgePoints.length; i++) {
                const next = i === allEdgePoints.length ? 1 : i + 1;
                indices.push(0, i, next);
            }

            geometry.setIndex(indices);
            geometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
            geometry.setAttribute('color', new THREE.Float32BufferAttribute(colors, 3));
            geometry.computeVertexNormals();

            // Create edge geometry
            const edgeGeometry = new THREE.BufferGeometry();
            edgeGeometry.setAttribute('position', new THREE.Float32BufferAttribute(edgeVertices, 3));

            return { geometry, edgeGeometry };
        }

        // Update the scene based on controls
        function updateScene() {
            // Clear existing geometry
            while (mainGroup.children.length > 0) {
                mainGroup.remove(mainGroup.children[0]);
            }

            // Get control values
            const unitsX = parseInt(document.querySelector('input[name="unitsX"]:checked').value);
            const unitsY = parseInt(document.querySelector('input[name="unitsY"]:checked').value);
            const trim = parseFloat(document.getElementById('slider-trim').value);
            const showDouble = document.getElementById('checkbox-double').checked;
            const showSphere = document.getElementById('checkbox-sphere').checked;
            const showTriangles = document.getElementById('checkbox-triangles').checked;

            // Define lattice basis vectors
            const basisX = new THREE.Vector3(2, 0, 0);
            const basisY = new THREE.Vector3(1, 0, Math.sqrt(3));

            // Collections for triangles feature
            const triangleVertices = [];

            // Generate main lattice
            for (let i = 0; i < unitsX; i++) {
                for (let j = 0; j < unitsY; j++) {
                    // Calculate position
                    const position = new THREE.Vector3()
                        .addScaledVector(basisX, i)
                        .addScaledVector(basisY, j);

                    // Create curved triangle
                    const colorCenter = [1, 0.75, 0.8]; // Light pink
                    const colorEdge = [0.56, 0.25, 0.56]; // Dark magenta
                    const { geometry, edgeGeometry } = createCurvedTriangle(trim, colorCenter, colorEdge);

                    // Create mesh
                    const material = new THREE.MeshStandardMaterial({
                        vertexColors: true,
                        side: THREE.DoubleSide
                    });
                    const mesh = new THREE.Mesh(geometry, material);

                    // Apply rotation for alternating pattern
                    if ((i + j) % 2 === 1) {
                        mesh.rotation.z = Math.PI;
                    }

                    mesh.position.copy(position);
                    mainGroup.add(mesh);

                    // Add edge outline
                    const edgeMaterial = new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 2 });
                    const edgeLine = new THREE.LineLoop(edgeGeometry, edgeMaterial);
                    edgeLine.position.copy(position);
                    if ((i + j) % 2 === 1) {
                        edgeLine.rotation.z = Math.PI;
                    }
                    mainGroup.add(edgeLine);

                    // Store triangle vertices for triangles feature
                    if (showTriangles) {
                        const v1 = new THREE.Vector3(0, 0.66, 0).add(position);
                        const v2 = new THREE.Vector3(-0.57, -0.33, 0).add(position);
                        const v3 = new THREE.Vector3(0.57, -0.33, 0).add(position);

                        if ((i + j) % 2 === 1) {
                            v1.sub(position).applyAxisAngle(new THREE.Vector3(0, 0, 1), Math.PI).add(position);
                            v2.sub(position).applyAxisAngle(new THREE.Vector3(0, 0, 1), Math.PI).add(position);
                            v3.sub(position).applyAxisAngle(new THREE.Vector3(0, 0, 1), Math.PI).add(position);
                        }

                        triangleVertices.push(v1, v2, v3);
                    }
                }
            }

            // Generate double lattice if enabled
            if (showDouble) {
                const offset = new THREE.Vector3()
                    .addScaledVector(basisX, 2/3)
                    .addScaledVector(basisY, 1/3);

                for (let i = 0; i < unitsX; i++) {
                    for (let j = 0; j < unitsY; j++) {
                        const position = new THREE.Vector3()
                            .addScaledVector(basisX, i)
                            .addScaledVector(basisY, j)
                            .add(offset);

                        const colorCenter = [0.5, 1, 1]; // Light cyan
                        const colorEdge = [0, 0.5, 0.5]; // Dark teal
                        const { geometry, edgeGeometry } = createCurvedTriangle(trim, colorCenter, colorEdge);

                        const material = new THREE.MeshStandardMaterial({
                            vertexColors: true,
                            side: THREE.DoubleSide
                        });
                        const mesh = new THREE.Mesh(geometry, material);

                        if ((i + j) % 2 === 0) {
                            mesh.rotation.z = Math.PI;
                        }

                        mesh.position.copy(position);
                        mainGroup.add(mesh);

                        const edgeMaterial = new THREE.LineBasicMaterial({ color: 0x000000, linewidth: 2 });
                        const edgeLine = new THREE.LineLoop(edgeGeometry, edgeMaterial);
                        edgeLine.position.copy(position);
                        if ((i + j) % 2 === 0) {
                            edgeLine.rotation.z = Math.PI;
                        }
                        mainGroup.add(edgeLine);
                    }
                }
            }

            // Add reference sphere if enabled
            if (showSphere) {
                const sphereGeometry = new THREE.SphereGeometry(1, 32, 32);
                const sphereMaterial = new THREE.MeshPhongMaterial({
                    color: 0xdddddd,
                    shininess: 80
                });
                const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
                sphere.position.set(0, -0.5, 0);
                mainGroup.add(sphere);
            }

            // Add flat triangles if enabled
            if (showTriangles) {
                // Create triangles mesh
                const trianglesGeometry = new THREE.BufferGeometry();
                const vertices = [];
                const indices = [];

                for (let i = 0; i < triangleVertices.length; i += 3) {
                    const idx = vertices.length / 3;
                    vertices.push(
                        triangleVertices[i].x, triangleVertices[i].y, triangleVertices[i].z,
                        triangleVertices[i + 1].x, triangleVertices[i + 1].y, triangleVertices[i + 1].z,
                        triangleVertices[i + 2].x, triangleVertices[i + 2].y, triangleVertices[i + 2].z
                    );
                    indices.push(idx, idx + 1, idx + 2);
                }

                trianglesGeometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));
                trianglesGeometry.setIndex(indices);

                const trianglesMaterial = new THREE.MeshBasicMaterial({
                    color: 0x800080,
                    transparent: true,
                    opacity: 0.4,
                    side: THREE.DoubleSide
                });
                const trianglesMesh = new THREE.Mesh(trianglesGeometry, trianglesMaterial);
                mainGroup.add(trianglesMesh);

                // Create points at vertices
                const pointsGeometry = new THREE.BufferGeometry();
                pointsGeometry.setAttribute('position', new THREE.Float32BufferAttribute(vertices, 3));

                const pointsMaterial = new THREE.PointsMaterial({
                    color: 0x000000,
                    size: 3
                });
                const points = new THREE.Points(pointsGeometry, pointsMaterial);
                mainGroup.add(points);
            }
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Handle window resize
        function onWindowResize() {
            const canvas = document.getElementById('main-canvas');
            camera.aspect = canvas.clientWidth / canvas.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);
        }

        // Set up event listeners
        window.addEventListener('resize', onWindowResize);

        // Radio button listeners
        document.querySelectorAll('input[name="unitsX"], input[name="unitsY"]').forEach(radio => {
            radio.addEventListener('change', updateScene);
        });

        // Slider listener
        const sliderTrim = document.getElementById('slider-trim');
        const labelTrimValue = document.getElementById('label-trim-value');
        sliderTrim.addEventListener('input', (e) => {
            labelTrimValue.textContent = parseFloat(e.target.value).toFixed(3);
            updateScene();
        });

        // Checkbox listeners
        document.querySelectorAll('#checkbox-double, #checkbox-sphere, #checkbox-triangles').forEach(checkbox => {
            checkbox.addEventListener('change', updateScene);
        });

        // Reset button listener
        document.getElementById('btn-reset-view').addEventListener('click', () => {
            controls.reset();
        });

        // Initialize the application
        init();
    </script>
</body>
</html>
