            cursor: pointer;
            border-radius: 3px;
            font-size: 14px;
        }

        .button-group button.active {
            background-color: #ccc;
            border-style: inset;
        }

        #canvas-container {
            background-color: white;
            border: 2px solid #999;
            display: inline-block;
            box-shadow: 0 2px 5px rgba(0,0,0,0.1);
        }
    </style>
</head>
<body>
    <div id="main-container">
        <button id="info-button">+</button>

        <div id="description-panel" style="display: none;">
            <p>This demo visualizes time-encoding algorithms (ASDM and IAF) applied to various analog signals. 
            Use the controls to adjust the time window and select different functions and encoding methods. 
            The blue curve shows the original analog signal, while the red visualization shows the encoded output.</p>
        </div>

        <div id="control-panel">
            <div>
                <label for="slider-start-time">start time</label>
                <input type="range" id="slider-start-time" min="-5" max="5" step="0.1" value="-1">
                <span id="start-time-value">-1.0</span>
            </div>

            <div>
                <label for="slider-total-time">total time</label>
                <input type="range" id="slider-total-time" min="1" max="5" step="0.1" value="2.2">
                <span id="total-time-value">2.2</span>
            </div>

            <div>
                <label>function</label>
                <div id="function-buttons" class="button-group">
                    <button id="btn-f">f</button>
                    <button id="btn-g" class="active">g</button>
                    <button id="btn-sin">sin</button>
                    <button id="btn-cos">cos</button>
                    <button id="btn-tan">tan</button>
                </div>
            </div>

            <div>
                <label>encoding method</label>
                <div id="encoding-buttons" class="button-group">
                    <button id="btn-asdm" class="active">ASDM</button>
                    <button id="btn-iaf">IAF</button>
                </div>
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script>
        // State variables
        let startTime = -1.0;
        let totalTime = 2.2;
        let selectedFunction = 'g';
        let selectedEncoding = 'ASDM';
        let sketch;

        // Info button toggle
        document.getElementById('info-button').addEventListener('click', function() {
            const panel = document.getElementById('description-panel');
            panel.style.display = panel.style.display === 'none' ? 'block' : 'none';
        });

        // Slider event handlers
        document.getElementById('slider-start-time').addEventListener('input', function(e) {
            startTime = parseFloat(e.target.value);
            document.getElementById('start-time-value').textContent = startTime.toFixed(1);
            if (sketch) sketch.redraw();
        });

        document.getElementById('slider-total-time').addEventListener('input', function(e) {
            totalTime = parseFloat(e.target.value);
            document.getElementById('total-time-value').textContent = totalTime.toFixed(1);
            if (sketch) sketch.redraw();
        });

        // Function button handlers
        document.querySelectorAll('#function-buttons button').forEach(btn => {
            btn.addEventListener('click', function() {
                document.querySelectorAll('#function-buttons button').forEach(b => b.classList.remove('active'));
                this.classList.add('active');
                selectedFunction = this.id.replace('btn-', '');
                if (sketch) sketch.redraw();
            });
        });

        // Encoding button handlers
        document.querySelectorAll('#encoding-buttons button').forEach(btn => {
            btn.addEventListener('click', function() {
                document.querySelectorAll('#encoding-buttons button').forEach(b => b.classList.remove('active'));
                this.classList.add('active');
                selectedEncoding = this.id.replace('btn-', '').toUpperCase();
                if (sketch) sketch.redraw();
            });
        });

        // p5.js sketch
        const sketchCode = function(p) {
            const margin = 50;
            const canvasWidth = 800;
            const canvasHeight = 400;
            const plotWidth = canvasWidth - 2 * margin;
            const plotHeight = canvasHeight - 2 * margin;

            // Analog functions
            const analogFunctions = {
                'f': (t) => 0.5 * t,
                'g': (t) => Math.sin(2 * Math.PI * t) + 0.3 * Math.cos(3 * 2 * Math.PI * t),
                'sin': (t) => Math.sin(2 * Math.PI * t),
                'cos': (t) => Math.cos(2 * Math.PI * t),
                'tan': (t) => Math.tan(Math.PI * t)
            };

            p.setup = function() {
                p.createCanvas(canvasWidth, canvasHeight);
                p.noLoop();
            };

            p.draw = function() {
                p.background(255);

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(margin, canvasHeight - margin, canvasWidth - margin, canvasHeight - margin); // x-axis
                p.line(margin, margin, margin, canvasHeight - margin); // y-axis

                // Draw tick marks and labels
                p.strokeWeight(1);
                p.textAlign(p.CENTER, p.TOP);

                // X-axis ticks
                const xMin = startTime;
                const xMax = startTime + totalTime;
                for (let x = Math.ceil(xMin * 2) / 2; x <= xMax; x += 0.5) {
                    const xPos = p.map(x, xMin, xMax, margin, canvasWidth - margin);
                    p.line(xPos, canvasHeight - margin, xPos, canvasHeight - margin + 5);
                    if (Math.abs(x - Math.round(x)) < 0.01) {
                        p.text(x.toFixed(0), xPos, canvasHeight - margin + 10);
                    }
                }

                // Y-axis ticks
                p.textAlign(p.RIGHT, p.CENTER);
                for (let y = -1; y <= 1; y += 0.5) {
                    const yPos = p.map(y, -1.2, 1.2, canvasHeight - margin, margin);
                    p.line(margin - 5, yPos, margin, yPos);
                    p.text(y.toFixed(1), margin - 10, yPos);
                }

                // Draw analog signal
                p.stroke(0, 0, 255);
                p.strokeWeight(2);
                p.noFill();

                const func = analogFunctions[selectedFunction];
                const dt = 0.01;

                p.beginShape();
                for (let t = xMin; t <= xMax; t += dt) {
                    const y = func(t);
                    if (selectedFunction === 'tan' && Math.abs(y) > 10) {
                        p.endShape();
                        p.beginShape();
                        continue;
                    }
                    const xPos = p.map(t, xMin, xMax, margin, canvasWidth - margin);
                    const yPos = p.map(p.constrain(y, -1.2, 1.2), -1.2, 1.2, canvasHeight - margin, margin);
                    p.vertex(xPos, yPos);
                }
                p.endShape();

                // Draw encoded signal
                p.stroke(255, 0, 0);
                p.strokeWeight(2);

                if (selectedEncoding === 'ASDM') {
                    drawASDM(func, xMin, xMax);
                } else if (selectedEncoding === 'IAF') {
                    drawIAF(func, xMin, xMax);
                }
            };

            function drawASDM(func, xMin, xMax) {
                const B = 0.25;
                const delta = 0.05;
                const dt = 0.001;

                let currentTime = xMin;
                let outputLevel = B;
                let integral = 0;
                let flipTimes = [{time: xMin, level: outputLevel}];

                while (currentTime < xMax) {
                    integral += (func(currentTime) - outputLevel) * dt;

                    if ((outputLevel === B && integral < -delta) || 
                        (outputLevel === -B && integral > delta)) {
                        outputLevel = -outputLevel;
                        integral = 0;
                        flipTimes.push({time: currentTime, level: outputLevel});
                    }

                    currentTime += dt;
                }

                // Draw square wave
                for (let i = 0; i < flipTimes.length - 1; i++) {
                    const x1 = p.map(flipTimes[i].time, xMin, xMax, margin, canvasWidth - margin);
                    const x2 = p.map(flipTimes[i + 1].time, xMin, xMax, margin, canvasWidth - margin);
                    const y = p.map(flipTimes[i].level, -1.2, 1.2, canvasHeight - margin, margin);

                    p.line(x1, y, x2, y);
                    if (i < flipTimes.length - 1) {
                        const y2 = p.map(flipTimes[i + 1].level, -1.2, 1.2, canvasHeight - margin, margin);
                        p.line(x2, y, x2, y2);
                    }
                }

                // Draw last segment
                if (flipTimes.length > 0) {
                    const lastFlip = flipTimes[flipTimes.length - 1];
                    const x1 = p.map(lastFlip.time, xMin, xMax, margin, canvasWidth - margin);
                    const x2 = canvasWidth - margin;
                    const y = p.map(lastFlip.level, -1.2, 1.2, canvasHeight - margin, margin);
                    p.line(x1, y, x2, y);
                }
            }

            function drawIAF(func, xMin, xMax) {
                const theta = 0.05;
                const ySpike = 0.3;
                const dt = 0.001;

                let currentTime = xMin;
                let integral = 0;
                let spikeTimes = [];

                while (currentTime < xMax) {
                    integral += func(currentTime) * dt;

                    if (integral >= theta) {
                        spikeTimes.push(currentTime);
                        integral = 0;
                    }

                    currentTime += dt;
                }

                // Draw spikes
                p.strokeWeight(2);
                for (let spikeTime of spikeTimes) {
                    const x = p.map(spikeTime, xMin, xMax, margin, canvasWidth - margin);
                    const y0 = p.map(0, -1.2, 1.2, canvasHeight - margin, margin);
                    const y1 = p.map(ySpike, -1.2, 1.2, canvasHeight - margin, margin);

                    p.line(x, y0, x, y1);
                    p.noStroke();
                    p.fill(255, 0, 0);
                    p.circle(x, y1, 6);
                    p.stroke(255, 0, 0);
                    p.noFill();
                }
            }
        };

        // Create p5 instance
        sketch = new p5(sketchCode, 'canvas-container');
    </script>
</body>
</html>
