    <script>
        // Global variables
        let scene, renderer, perspectiveCamera, orthographicCamera, activeCamera;
        let planeMesh, wireframeMesh;
        let time = 0;
        let isPlaying = false;
        let plotPoints = 40;

        // Wave parameters
        let pWaveType = 'sine';
        let pWaveAmplitude = 0.1;
        let pWaveWavelength = 2.0;
        let sWaveType = 'sine';
        let sWaveAmplitude = 0.1;
        let sWaveWavelength = 2.5;
        let sWaveDirection = 'xy';

        // Initialize Three.js scene
        function init() {
            const container = document.getElementById('visualization-container');

            // Scene
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xE0E0E0);

            // Renderer
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(container.clientWidth, container.clientHeight);
            container.appendChild(renderer.domElement);

            // Cameras
            const aspect = container.clientWidth / container.clientHeight;
            perspectiveCamera = new THREE.PerspectiveCamera(45, aspect, 0.1, 100);
            perspectiveCamera.position.set(10, 10, 10);
            perspectiveCamera.lookAt(0, 0, 0);

            const frustumSize = 12;
            orthographicCamera = new THREE.OrthographicCamera(
                -frustumSize * aspect / 2, frustumSize * aspect / 2,
                frustumSize / 2, -frustumSize / 2,
                0.1, 100
            );
            orthographicCamera.position.set(0, 20, 0);
            orthographicCamera.lookAt(0, 0, 0);

            activeCamera = orthographicCamera;

            // Lights
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(5, 10, 5);
            scene.add(directionalLight);

            // Create grid
            createGrid();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function createGrid() {
            // Remove existing meshes
            if (planeMesh) {
                scene.remove(planeMesh);
                planeMesh.geometry.dispose();
                planeMesh.material.dispose();
            }
            if (wireframeMesh) {
                scene.remove(wireframeMesh);
                wireframeMesh.geometry.dispose();
                wireframeMesh.material.dispose();
            }

            // Create plane geometry
            const geometry = new THREE.PlaneBufferGeometry(10, 10, plotPoints, plotPoints);

            // Add vertex colors
            const colors = new Float32Array(geometry.attributes.position.count * 3);
            geometry.setAttribute('color', new THREE.BufferAttribute(colors, 3));

            // Create mesh
            const material = new THREE.MeshLambertMaterial({ 
                vertexColors: true,
                side: THREE.DoubleSide
            });
            planeMesh = new THREE.Mesh(geometry, material);
            scene.add(planeMesh);

            // Create wireframe
            const wireframeGeometry = new THREE.WireframeGeometry(geometry);
            const wireframeMaterial = new THREE.LineBasicMaterial({ color: 0x556B2F });
            wireframeMesh = new THREE.LineSegments(wireframeGeometry, wireframeMaterial);
            scene.add(wireframeMesh);

            updateWaves();
        }

        function waveFunction(u, type) {
            if (type === 'sine') {
                return Math.sin(2 * Math.PI * u);
            } else {
                return Math.exp(-u * u * 4);
            }
        }

        function updateWaves() {
            const positions = planeMesh.geometry.attributes.position;
            const colors = planeMesh.geometry.attributes.color;
            const wireframePositions = wireframeMesh.geometry.attributes.position;

            for (let i = 0; i < positions.count; i++) {
                const x = positions.getX(i);
                const y = positions.getY(i);
                const r = Math.sqrt(x * x + y * y);

                let z = 0;

                // P-wave (longitudinal)
                if (r > 0) {
                    const pPhase = (r / pWaveWavelength) - time;
                    const pMag = pWaveAmplitude * waveFunction(pPhase, pWaveType);
                    const radialX = x / r;
                    const radialY = y / r;

                    positions.setX(i, x + pMag * radialX);
                    positions.setY(i, y + pMag * radialY);
                }

                // S-wave (transverse)
                const sPhase = (r / sWaveWavelength) - time;
                const sMag = sWaveAmplitude * waveFunction(sPhase, sWaveType);

                if (sWaveDirection === 'z') {
                    z += sMag;
                } else if (r > 0) {
                    const tangentX = -y / r;
                    const tangentY = x / r;
                    positions.setX(i, positions.getX(i) + sMag * tangentX);
                    positions.setY(i, positions.getY(i) + sMag * tangentY);
                }

                positions.setZ(i, z);

                // Update wireframe
                wireframePositions.setXYZ(i * 2, positions.getX(i), positions.getY(i), z);
                wireframePositions.setXYZ(i * 2 + 1, positions.getX(i), positions.getY(i), z);

                // Color based on z displacement
                const colorValue = Math.max(-1, Math.min(1, z * 5));
                if (colorValue > 0) {
                    colors.setXYZ(i, 1, 1 - colorValue, 1 - colorValue);
                } else {
                    colors.setXYZ(i, 1 + colorValue, 1 + colorValue, 1);
                }
            }

            positions.needsUpdate = true;
            colors.needsUpdate = true;
            wireframePositions.needsUpdate = true;
        }

        function animate() {
            requestAnimationFrame(animate);

            if (isPlaying) {
                time += 0.02;
                document.getElementById('time-slider').value = time;
            }

            updateWaves();
            renderer.render(scene, activeCamera);
        }

        function onWindowResize() {
            const container = document.getElementById('visualization-container');
            const aspect = container.clientWidth / container.clientHeight;

            perspectiveCamera.aspect = aspect;
            perspectiveCamera.updateProjectionMatrix();

            const frustumSize = 12;
            orthographicCamera.left = -frustumSize * aspect / 2;
            orthographicCamera.right = frustumSize * aspect / 2;
            orthographicCamera.updateProjectionMatrix();

            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        // Event listeners
        document.getElementById('play-pause-button').addEventListener('click', function() {
            isPlaying = !isPlaying;
            this.textContent = isPlaying ? '❚❚' : '►';
        });

        document.getElementById('reset-button').addEventListener('click', function() {
            time = 0;
            isPlaying = false;
            document.getElementById('time-slider').value = 0;
            document.getElementById('play-pause-button').textContent = '►';
        });

        document.getElementById('step-button').addEventListener('click', function() {
            time += 0.1;
            document.getElementById('time-slider').value = time;
        });

        document.getElementById('time-slider').addEventListener('input', function() {
            time = parseFloat(this.value);
        });

        document.getElementById('plot-points-slider').addEventListener('input', function() {
            plotPoints = parseInt(this.value);
            createGrid();
        });

        // P-wave controls
        document.getElementById('p-wave-sine-button').addEventListener('click', function() {
            pWaveType = 'sine';
            this.classList.add('active');
            document.getElementById('p-wave-pulse-button').classList.remove('active');
        });

        document.getElementById('p-wave-pulse-button').addEventListener('click', function() {
            pWaveType = 'pulse';
            this.classList.add('active');
            document.getElementById('p-wave-sine-button').classList.remove('active');
        });

        document.getElementById('p-wave-amplitude-slider').addEventListener('input', function() {
            pWaveAmplitude = parseFloat(this.value);
        });

        document.getElementById('p-wave-wavelength-slider').addEventListener('input', function() {
            pWaveWavelength = parseFloat(this.value);
        });

        // S-wave controls
        document.getElementById('s-wave-sine-button').addEventListener('click', function() {
            sWaveType = 'sine';
            this.classList.add('active');
            document.getElementById('s-wave-pulse-button').classList.remove('active');
        });

        document.getElementById('s-wave-pulse-button').addEventListener('click', function() {
            sWaveType = 'pulse';
            this.classList.add('active');
            document.getElementById('s-wave-sine-button').classList.remove('active');
        });

        document.getElementById('s-wave-amplitude-slider').addEventListener('input', function() {
            sWaveAmplitude = parseFloat(this.value);
        });

        document.getElementById('s-wave-wavelength-slider').addEventListener('input', function() {
            sWaveWavelength = parseFloat(this.value);
        });

        document.getElementById('s-wave-dir-xy-button').addEventListener('click', function() {
            sWaveDirection = 'xy';
            this.classList.add('active');
            document.getElementById('s-wave-dir-z-button').classList.remove('active');
        });

        document.getElementById('s-wave-dir-z-button').addEventListener('click', function() {
            sWaveDirection = 'z';
            this.classList.add('active');
            document.getElementById('s-wave-dir-xy-button').classList.remove('active');
        });

        // Viewpoint controls
        document.getElementById('viewpoint-top-button').addEventListener('click', function() {
            activeCamera = orthographicCamera;
            this.classList.add('active');
            document.getElementById('viewpoint-default-button').classList.remove('active');
        });

        document.getElementById('viewpoint-default-button').addEventListener('click', function() {
            activeCamera = perspectiveCamera;
            this.classList.add('active');
            document.getElementById('viewpoint-top-button').classList.remove('active');
        });

        // Initialize and start animation
        init();
        animate();
    </script>
</body>
</html>
