    <script>
        // Global state
        let a = 1, b = -1, c = -1;
        let z = 1.5;
        let plotRange = 4;
        let showLabels = false;
        let showCircle = false;
        let showAxes = false;
        let showGridLines = false;

        // Points
        let O, L1, L2, L3, L1p, L2p;

        // p5.js sketch
        function setup() {
            let canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
            noLoop();
            updateVisualization();
        }

        function draw() {
            background(255);

            // Calculate bounding box
            calculatePoints();
            let minX = Math.min(0, L1.x, L2.x, L3.x, L1p.x, L2p.x);
            let maxX = Math.max(0, L1.x, L2.x, L3.x, L1p.x, L2p.x);
            let minY = Math.min(0, L1.y, L2.y, L3.y, L1p.y, L2p.y);
            let maxY = Math.max(0, L1.y, L2.y, L3.y, L1p.y, L2p.y);

            // Add padding
            let padding = plotRange * 0.2;
            minX -= padding;
            maxX += padding;
            minY -= padding;
            maxY += padding;

            // Center and scale
            push();
            translate(width/2, height/2);
            let scale = Math.min(width / (2 * plotRange), height / (2 * plotRange));
            scale(scale, -scale);

            // Grid and axes
            if (showGridLines) {
                drawGrid();
            }

            if (showAxes) {
                drawAxes();
            }

            // Draw coefficient path
            stroke(0);
            strokeWeight(2/scale);
            noFill();
            beginShape();
            vertex(O.x, O.y);
            vertex(L1.x, L1.y);
            vertex(L2.x, L2.y);
            vertex(L3.x, L3.y);
            endShape();

            // Draw test path
            stroke(0);
            line(O.x, O.y, L1p.x, L1p.y);
            stroke(255, 0, 255);
            line(L1p.x, L1p.y, L2p.x, L2p.y);
            stroke(255, 0, 0);
            line(L2p.x, L2p.y, L3.x, L3.y);

            // Draw points
            fill(0);
            noStroke();
            let pointSize = 6/scale;
            ellipse(O.x, O.y, pointSize, pointSize);
            ellipse(L1.x, L1.y, pointSize, pointSize);
            ellipse(L2.x, L2.y, pointSize, pointSize);
            ellipse(L3.x, L3.y, pointSize, pointSize);
            ellipse(L1p.x, L1p.y, pointSize, pointSize);
            ellipse(L2p.x, L2p.y, pointSize, pointSize);

            // Draw circle
            if (showCircle) {
                stroke(0);
                strokeWeight(1/scale);
                noFill();
                let centerX = (a - c) / 2;
                let centerY = b / 2;
                let diameter = Math.sqrt((a - c) * (a - c) + b * b);
                ellipse(centerX, centerY, diameter, diameter);
            }

            // Draw labels
            if (showLabels) {
                push();
                scale(1, -1);
                textSize(14/scale);
                fill(0);
                text("O", O.x + 0.1, -O.y - 0.1);
                text("L1", L1.x + 0.1, -L1.y - 0.1);
                text("L2", L2.x + 0.1, -L2.y - 0.1);
                text("L3", L3.x + 0.1, -L3.y - 0.1);
                text("L1'", L1p.x + 0.1, -L1p.y - 0.1);
                text("L2'", L2p.x + 0.1, -L2p.y - 0.1);
                pop();
            }

            pop();
        }

        function drawGrid() {
            stroke(200);
            strokeWeight(0.5/scale);
            let step = 1;
            if (plotRange > 5) step = 2;
            if (plotRange > 10) step = 5;

            for (let i = -plotRange; i <= plotRange; i += step) {
                line(i, -plotRange, i, plotRange);
                line(-plotRange, i, plotRange, i);
            }
        }

        function drawAxes() {
            stroke(0);
            strokeWeight(1/scale);
            line(-plotRange, 0, plotRange, 0);
            line(0, -plotRange, 0, plotRange);
        }

        function calculatePoints() {
            O = createVector(0, 0);
            L1 = createVector(a, 0);
            L2 = createVector(a, b);
            L3 = createVector(a - c, b);
            L1p = createVector(a, -z * a);
            L2p = createVector(a + b * z + a * z * z, -z * a + b + 2 * a * z);
        }

        function updateVisualization() {
            calculatePoints();

            // Calculate P(z)
            let pz = a * z * z + b * z + c;

            // Calculate |L3 L2'|
            let l3l2 = Math.sqrt((L3.x - L2p.x) * (L3.x - L2p.x) + (L3.y - L2p.y) * (L3.y - L2p.y));

            // Update display values
            document.getElementById('value-pz').textContent = pz.toFixed(4);
            document.getElementById('value-l3l2').textContent = l3l2.toFixed(4);
            document.getElementById('value-z').textContent = z.toFixed(2);

            // Update formula
            updateFormula();

            redraw();
        }

        function updateFormula() {
            let formula = '';
            if (a === 1) formula += 'z^2';
            else if (a === -1) formula += '-z^2';
            else formula += a + 'z^2';

            if (b > 0) formula += ' + ' + (b === 1 ? '' : b) + 'z';
            else if (b < 0) formula += ' - ' + (b === -1 ? '' : Math.abs(b)) + 'z';

            if (c > 0) formula += ' + ' + c;
            else if (c < 0) formula += ' - ' + Math.abs(c);

            document.getElementById('formula-text').textContent = formula;

            if (window.MathJax && MathJax.typesetPromise) {
                MathJax.typesetPromise([document.getElementById('formula-display')]);
            }
        }

        // Event listeners
        document.getElementById('slider-z').addEventListener('input', function(e) {
            z = parseFloat(e.target.value);
            document.getElementById('label-z').textContent = z.toFixed(2);
            updateVisualization();
        });

        document.getElementById('slider-plot-range').addEventListener('input', function(e) {
            plotRange = parseFloat(e.target.value);
            document.getElementById('label-plot-range').textContent = plotRange;
            updateVisualization();
        });

        document.getElementById('check-show-labels').addEventListener('change', function(e) {
            showLabels = e.target.checked;
            redraw();
        });

        document.getElementById('check-show-circle').addEventListener('change', function(e) {
            showCircle = e.target.checked;
            redraw();
        });

        document.getElementById('check-show-axes').addEventListener('change', function(e) {
            showAxes = e.target.checked;
            redraw();
        });

        document.getElementById('check-show-grid-lines').addEventListener('change', function(e) {
            showGridLines = e.target.checked;
            redraw();
        });

        document.getElementById('btn-new-example').addEventListener('click', function() {
            // Generate random coefficients
            a = Math.random() < 0.5 ? Math.floor(Math.random() * 3) + 1 : -Math.floor(Math.random() * 3) - 1;
            b = Math.floor(Math.random() * 11) - 5;
            c = Math.floor(Math.random() * 11) - 5;
            updateVisualization();
        });

        document.getElementById('btn-golden-ratio').addEventListener('click', function() {
            a = 1;
            b = -1;
            c = -1;
            updateVisualization();
        });
    </script>
</body>
