    <script>
        // Generate sample solution curve data
        const solutionCurveData = [];
        for (let i = 0; i <= 700; i++) {
            const alpha = i * 0.0001;
            const t = i / 100;
            const t2 = 4 + 3 * Math.sin(t) + 1.5 * Math.sin(2*t) + 0.5 * Math.sin(3*t);
            const x1 = 0.5 + 0.3 * Math.cos(t);
            const x2 = 0.5 + 0.3 * Math.sin(t);
            const t1 = 2 + 1.5 * Math.cos(t);
            solutionCurveData.push({alpha, x1, x2, t1, t2});
        }

        // Sample turning points data
        const turningPointsData = [
            {alpha: 0.008, t2: 6.5},
            {alpha: 0.015, t2: 1.8},
            {alpha: 0.025, t2: 5.2},
            {alpha: 0.035, t2: 2.8},
            {alpha: 0.048, t2: 7.2},
            {alpha: 0.062, t2: 3.5}
        ];

        let currentView = 'plot_solutions';
        let currentAlpha = 0.039;
        let p5Instance = null;

        // View switching
        document.getElementById('btn-plot').addEventListener('click', () => {
            setView('plot_solutions');
        });

        document.getElementById('btn-table').addEventListener('click', () => {
            setView('table');
        });

        document.getElementById('btn-turning-points').addEventListener('click', () => {
            setView('plot_turning_points');
        });

        // Alpha slider
        const slider = document.getElementById('slider-alpha');
        const alphaDisplay = document.getElementById('alpha-value-display');

        slider.addEventListener('input', (e) => {
            currentAlpha = parseFloat(e.target.value);
            alphaDisplay.textContent = currentAlpha.toFixed(4);

            if (currentView === 'plot_solutions') {
                if (p5Instance) p5Instance.redraw();
            } else if (currentView === 'table') {
                updateTable();
            }
        });

        function setView(view) {
            currentView = view;

            // Update active button
            document.querySelectorAll('#view-switcher button').forEach(btn => {
                btn.classList.remove('active');
            });

            const plotView = document.getElementById('plot-view');
            const tableView = document.getElementById('table-view');
            const paramControls = document.getElementById('parameter-controls');

            switch(view) {
                case 'plot_solutions':
                    document.getElementById('btn-plot').classList.add('active');
                    plotView.style.display = 'flex';
                    tableView.style.display = 'none';
                    paramControls.style.display = 'flex';
                    if (p5Instance) p5Instance.redraw();
