        function findIntersections(alpha) {
            const intersections = [];
            for (let i = 1; i < solutionCurveData.length; i++) {
                const prev = solutionCurveData[i-1];
                const curr = solutionCurveData[i];

                if ((prev.alpha <= alpha && curr.alpha >= alpha) ||
                    (prev.alpha >= alpha && curr.alpha <= alpha)) {
                    // Linear interpolation
                    const t = (alpha - prev.alpha) / (curr.alpha - prev.alpha);
                    intersections.push({
                        alpha: alpha,
                        x1: prev.x1 + t * (curr.x1 - prev.x1),
                        x2: prev.x2 + t * (curr.x2 - prev.x2),
                        t1: prev.t1 + t * (curr.t1 - prev.t1),
                        t2: prev.t2 + t * (curr.t2 - prev.t2)
                    });
                }
            }
            return intersections;
        }

