            const tbody = document.getElementById('solutions-table-body');
            tbody.innerHTML = '';

            const intersections = findIntersections(currentAlpha);
            intersections.forEach((intersection, index) => {
                const row = tbody.insertRow();
                row.insertCell(0).textContent = index + 1;
                row.insertCell(1).textContent = intersection.x1.toFixed(5);
                row.insertCell(2).textContent = intersection.x2.toFixed(5);
                row.insertCell(3).textContent = intersection.t1.toFixed(5);
                row.insertCell(4).textContent = intersection.t2.toFixed(5);
            });
        }

        // p5.js sketch
        const sketch = (p) => {
            const canvasWidth = 600;
            const canvasHeight = 600;
            const margin = 50;
            const plotWidth = canvasWidth - 2 * margin;
            const plotHeight = canvasHeight - 2 * margin;

            p.setup = () => {
                p.createCanvas(canvasWidth, canvasHeight);
                p.noLoop();
            };

            p.draw = () => {
                p.background(255);

                // Draw grid
                p.stroke(200);
                p.strokeWeight(1);

                // Vertical grid lines
                for (let i = 0; i <= 7; i++) {
                    const x = margin + (i / 7) * plotWidth;
                    p.line(x, margin, x, canvasHeight - margin);
                }

                // Horizontal grid lines
                for (let i = 0; i <= 8; i++) {
                    const y = margin + (i / 8) * plotHeight;
                    p.line(margin, y, canvasWidth - margin, y);
                }

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(margin, canvasHeight - margin, canvasWidth - margin, canvasHeight - margin); // X-axis
                p.line(margin, margin, margin, canvasHeight - margin); // Y-axis

                // Draw labels
                p.fill(0);
                p.noStroke();
                p.textAlign(p.CENTER, p.TOP);
                p.text('α', canvasWidth / 2, canvasHeight - margin + 10);

                p.push();
                p.translate(margin - 30, canvasHeight / 2);
                p.rotate(-p.PI / 2);
                p.textAlign(p.CENTER, p.BOTTOM);
                p.text('T₂(α)', 0, 0);
                p.pop();

                // Draw tick labels
                p.textAlign(p.CENTER, p.TOP);
                for (let i = 0; i <= 7; i++) {
                    const x = margin + (i / 7) * plotWidth;
                    const val = (i * 0.01).toFixed(2);
                    p.text(val, x, canvasHeight - margin + 5);
                }

                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = 0; i <= 8; i++) {
                    const y = canvasHeight - margin - (i / 8) * plotHeight;
                    p.text(i, margin - 5, y);
                }

                // Draw solution curve
                p.stroke(255, 0, 0);
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for (const point of solutionCurveData) {
                    const x = margin + (point.alpha / 0.07) * plotWidth;
                    const y = canvasHeight - margin - (point.t2 / 8) * plotHeight;
                    p.vertex(x, y);
                }
                p.endShape();

                if (currentView === 'plot_solutions') {
                    // Draw vertical line at current alpha
                    const alphaX = margin + (currentAlpha / 0.07) * plotWidth;
                    p.stroke(0, 255, 0);
                    p.strokeWeight(1);
                    p.drawingContext.setLineDash([5, 5]);
                    p.line(alphaX, margin, alphaX, canvasHeight - margin);
                    p.drawingContext.setLineDash([]);

                    // Draw intersection points
                    const intersections = findIntersections(currentAlpha);
                    p.fill(0, 0, 255);
                    p.noStroke();
                    for (const intersection of intersections) {
                        const x = margin + (intersection.alpha / 0.07) * plotWidth;
                        const y = canvasHeight - margin - (intersection.t2 / 8) * plotHeight;
                        p.circle(x, y, 10);
                    }
                } else if (currentView === 'plot_turning_points') {
                    // Draw turning points
                    p.fill(0, 255, 0);
                    p.noStroke();
                    for (const point of turningPointsData) {
                        const x = margin + (point.alpha / 0.07) * plotWidth;
                        const y = canvasHeight - margin - (point.t2 / 8) * plotHeight;
                        p.circle(x, y, 10);
                    }
                }
            };
        };

        // Initialize p5.js
        p5Instance = new p5(sketch, 'plot-view');

        // Initialize view
        setView('plot_solutions');
    </script>
</body>
</html>
