                </div>
            </div>
            <div id="controls-row-2">
                <div id="growth-slider-container">
                    <label for="slider-growth">spiral growth</label>
                    <input type="range" id="slider-growth" min="0.01" max="0.5" step="0.0005" value="0.15">
                    <span id="display-growth">0.15</span>
                    <button id="btn-reset-growth">+</button>
                </div>
            </div>
        </div>
        <div id="canvas-container">
            <canvas id="p5-canvas"></canvas>
        </div>
    </div>

    <script>
        // State variables
        let spiralType = 'double';
        let itemType = 'point';
        let spiralGrowth = 0.15;
        let z0_loc = {x: 0, y: 0};
        let z_plus_loc = {x: -100, y: 0};
        let z_minus_loc = {x: 100, y: 0};

        // Dragging state
        let dragging = null;

        // Fox shape vertices
        const fox_vertices = [
            {x:-15, y:0}, {x:-10, y:-5}, {x:-5, y:-4}, {x:0, y:-5},
            {x:5, y:-5}, {x:8, y:-2}, {x:10, y:-6}, {x:12, y:-6},
            {x:15, y:0}, {x:12, y:6}, {x:10, y:6}, {x:8, y:2},
            {x:5, y:5}, {x:0, y:5}, {x:-5, y:4}, {x:-10, y:5}
        ];

        // Button event listeners
        document.getElementById('btn-type-single').addEventListener('click', function() {
            spiralType = 'single';
            updateButtonStates('type', this);
            redraw();
        });

        document.getElementById('btn-type-double').addEventListener('click', function() {
            spiralType = 'double';
            updateButtonStates('type', this);
            redraw();
        });

        document.getElementById('btn-item-none').addEventListener('click', function() {
            itemType = 'none';
            updateButtonStates('item', this);
            redraw();
        });

        document.getElementById('btn-item-point').addEventListener('click', function() {
            itemType = 'point';
            updateButtonStates('item', this);
            redraw();
        });

        document.getElementById('btn-item-fox').addEventListener('click', function() {
            itemType = 'fox';
            updateButtonStates('item', this);
            redraw();
        });

        document.getElementById('btn-item-filled-fox').addEventListener('click', function() {
            itemType = 'filled-fox';
            updateButtonStates('item', this);
            redraw();
        });

        // Slider event listeners
        document.getElementById('slider-growth').addEventListener('input', function() {
            spiralGrowth = parseFloat(this.value);
            document.getElementById('display-growth').textContent = spiralGrowth.toFixed(4);
            redraw();
        });

        document.getElementById('btn-reset-growth').addEventListener('click', function() {
            spiralGrowth = 0.25;
            document.getElementById('slider-growth').value = spiralGrowth;
            document.getElementById('display-growth').textContent = spiralGrowth.toFixed(4);
            redraw();
        });

        function updateButtonStates(group, activeBtn) {
            const prefix = group === 'type' ? 'btn-type-' : 'btn-item-';
            const buttons = group === 'type' ? 
                ['btn-type-single', 'btn-type-double'] :
                ['btn-item-none', 'btn-item-point', 'btn-item-fox', 'btn-item-filled-fox'];

            buttons.forEach(id => {
                document.getElementById(id).classList.remove('active');
            });
            activeBtn.classList.add('active');
        }

        // p5.js sketch
        function setup() {
            let canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            document.getElementById('display-growth').textContent = spiralGrowth.toFixed(4);
        }

        function draw() {
            background(255);
            translate(width/2, height/2);

            // Draw spiral
            if (spiralType === 'single') {
                drawSingleSpiral();
            } else {
                drawDoubleSpiral();
            }

            // Draw locators
            if (spiralType === 'single') {
                drawLocator(z0_loc, '#ffff00', 'z0Loc');
            } else {
                drawLocator(z_plus_loc, '#ffff00', 'z+Loc');
                drawLocator(z_minus_loc, '#00ff00', 'z-Loc');
                // Draw z0Loc label at center
                fill(0);
                noStroke();
                textAlign(LEFT, CENTER);
                text('z0Loc', (z_plus_loc.x + z_minus_loc.x)/2 + 15, (z_plus_loc.y + z_minus_loc.y)/2);
            }
        }

        function drawSingleSpiral() {
            stroke('#4A6DA8');
            strokeWeight(2);
            noFill();

            let points = [];
            beginShape();
            for (let t = -8*PI; t <= 8*PI; t += 0.1) {
                let r = exp(spiralGrowth * t);
                let x = z0_loc.x + r * cos(t);
                let y = z0_loc.y + r * sin(t);
                curveVertex(x, y);

                if (t % 0.5 < 0.1) {
                    points.push({x: x, y: y, angle: atan2(r * cos(t) + spiralGrowth * r * sin(t), 
                                                          -r * sin(t) + spiralGrowth * r * cos(t))});
                }
            }
            endShape();

            // Draw items
            drawItems(points);
        }

        function drawDoubleSpiral() {
            stroke('#4A6DA8');
            strokeWeight(2);
            noFill();

            let points = [];

            // First branch
            beginShape();
            for (let t = -8*PI; t <= -0.01; t += 0.1) {
                let pt = getMobiusPoint(t);
                if (pt && abs(pt.x) < 2000 && abs(pt.y) < 2000) {
                    curveVertex(pt.x, pt.y);
                    if (t % 0.5 < 0.1) {
                        points.push({x: pt.x, y: pt.y, angle: pt.angle});
                    }
                }
            }
            endShape();

            // Second branch
            beginShape();
            for (let t = 0.01; t <= 8*PI; t += 0.1) {
                let pt = getMobiusPoint(t);
                if (pt && abs(pt.x) < 2000 && abs(pt.y) < 2000) {
                    curveVertex(pt.x, pt.y);
                    if (t % 0.5 < 0.1) {
                        points.push({x: pt.x, y: pt.y, angle: pt.angle});
                    }
                }
            }
            endShape();

            // Draw items
            drawItems(points);
        }

        function getMobiusPoint(t) {
            let w_real = exp(spiralGrowth * t) * cos(t);
            let w_imag = exp(spiralGrowth * t) * sin(t);

            // Mobius transformation: z = (p*w - q) / (w - 1)
            let p_real = z_plus_loc.x;
            let p_imag = z_plus_loc.y;
            let q_real = z_minus_loc.x;
            let q_imag = z_minus_loc.y;

            // Numerator: p*w - q
            let num_real = p_real * w_real - p_imag * w_imag - q_real;
            let num_imag = p_real * w_imag + p_imag * w_real - q_imag;

            // Denominator: w - 1
            let den_real = w_real - 1;
            let den_imag = w_imag;

            let den_mag_sq = den_real * den_real + den_imag * den_imag;

            if (den_mag_sq < 0.0001) return null;

            // Division
            let z_real = (num_real * den_real + num_imag * den_imag) / den_mag_sq;
            let z_imag = (num_imag * den_real - num_real * den_imag) / den_mag_sq;

            // Calculate derivative for angle
            let dw_real = (spiralGrowth * cos(t) - sin(t)) * exp(spiralGrowth * t);
            let dw_imag = (spiralGrowth * sin(t) + cos(t)) * exp(spiralGrowth * t);

            let dz_real = ((p_real - q_real) * dw_real - (p_imag - q_imag) * dw_imag * den_mag_sq - 
                          2 * num_real * den_real * dw_real - 2 * num_real * den_imag * dw_imag) / (den_mag_sq * den_mag_sq);
            let dz_imag = ((p_real - q_real) * dw_imag + (p_imag - q_imag) * dw_real * den_mag_sq - 
                          2 * num_imag * den_real * dw_real - 2 * num_imag * den_imag * dw_imag) / (den_mag_sq * den_mag_sq);

            return {x: z_real, y: z_imag, angle: atan2(dz_imag, dz_real)};
        }

        function drawItems(points) {
            if (itemType === 'none') return;

            points.forEach(pt => {
                push();
                translate(pt.x, pt.y);

                if (itemType === 'point') {
                    noStroke();
                    fill('#CB3335');
                    circle(0, 0, 5);
                } else if (itemType === 'fox' || itemType === 'filled-fox') {
                    rotate(pt.angle);
                    scale(0.8);

                    if (itemType === 'fox') {
                        noFill();
                        stroke('#D6292D');
                        strokeWeight(1);
                    } else {
                        fill('#D6292D');
                        noStroke();
                    }

                    beginShape();
                    fox_vertices.forEach(v => vertex(v.x, v.y));
                    endShape(CLOSE);
                }
                pop();
            });
        }

        function drawLocator(loc, color, label) {
            push();
            translate(loc.x, loc.y);

            // Circle
            stroke(0);
            strokeWeight(1);
            fill(color + 'cc');
            circle(0, 0, 20);

            // Crosshair
            stroke(0);
            line(-10, 0, 10, 0);
            line(0, -10, 0, 10);

            // Label
            fill(0);
            noStroke();
            textAlign(LEFT, CENTER);
            text(label, 15, 0);

            pop();
        }

        function mousePressed() {
            let mx = mouseX - width/2;
            let my = mouseY - height/2;

            if (spiralType === 'single') {
                if (dist(mx, my, z0_loc.x, z0_loc.y) < 10) {
                    dragging = 'z0';
                }
            } else {
                if (dist(mx, my, z_plus_loc.x, z_plus_loc.y) < 10) {
                    dragging = 'z_plus';
                } else if (dist(mx, my, z_minus_loc.x, z_minus_loc.y) < 10) {
                    dragging = 'z_minus';
                }
            }
        }

        function mouseDragged() {
            let mx = mouseX - width/2;
            let my = mouseY - height/2;

            if (dragging === 'z0') {
                z0_loc.x = mx;
                z0_loc.y = my;
            } else if (dragging === 'z_plus') {
                z_plus_loc.x = mx;
                z_plus_loc.y = my;
            } else if (dragging === 'z_minus') {
                z_minus_loc.x = mx;
                z_minus_loc.y = my;
            }
        }

        function mouseReleased() {
            dragging = null;
        }
    </script>
</body>
</html>
