<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Dijkstra's Algorithm Visualization</title>
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.7.0/p5.min.js"></script>
    <style>
        body {
            margin: 0;
            padding: 0;
            font-family: Arial, sans-serif;
            overflow: hidden;
        }

        #main-container {
            display: flex;
            height: 100vh;
        }

        #control-panel {
            width: 250px;
            padding: 20px;
            background-color: #f0f0f0;
            display: flex;
            flex-direction: column;
            gap: 20px;
        }

        #viz-container {
            flex: 1;
            display: flex;
            flex-direction: column;
            align-items: center;
            padding: 20px;
        }

        button {
            padding: 10px;
            cursor: pointer;
            background-color: #e0e0e0;
            border: 1px solid #999;
            border-radius: 4px;
            font-size: 14px;
        }

        button:hover:not(:disabled) {
            background-color: #d0d0d0;
        }

        button:disabled {
            opacity: 0.5;
            cursor: not-allowed;
        }

        button.active {
            background-color: #666;
            color: white;
        }

        .vertex-grid {
            display: grid;
            grid-template-columns: repeat(4, 1fr);
            gap: 5px;
            margin-top: 10px;
        }

        .vertex-grid button {
            padding: 8px;
            font-size: 16px;
            font-weight: bold;
        }

        label {
            font-weight: bold;
            margin-bottom: 5px;
        }

        .section {
            display: flex;
            flex-direction: column;
        }

        #title-display {
            margin: 0 0 20px 0;
            font-size: 24px;
        }
    </style>
</head>
<body>
    <div id="main-container">
        <div id="control-panel">
            <button id="start-over-btn">start over</button>

            <div class="section">
                <label>initial vertex</label>
                <div class="vertex-grid">
                    <button id="btn-m" class="active">m</button>
                    <button id="btn-n">n</button>
                    <button id="btn-o">o</button>
                    <button id="btn-p">p</button>
                    <button id="btn-i">i</button>
                    <button id="btn-j">j</button>
                    <button id="btn-k">k</button>
                    <button id="btn-l">l</button>
                    <button id="btn-e">e</button>
                    <button id="btn-f">f</button>
                    <button id="btn-g">g</button>
                    <button id="btn-h">h</button>
                    <button id="btn-a">a</button>
                    <button id="btn-b">b</button>
                    <button id="btn-c">c</button>
                    <button id="btn-d">d</button>
                </div>
            </div>

            <div class="section">
                <button id="fixed-example-btn">fixed example</button>
                <button id="random-example-btn">random example</button>
            </div>

            <div class="section">
                <label>implement algorithm</label>
                <button id="next-step-btn">next step</button>
            </div>
        </div>

        <div id="viz-container">
            <h1 id="title-display">Dijkstra's algorithm starting at vertex m</h1>
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        // Graph data structure
        const vertices = ['m', 'n', 'o', 'p', 'i', 'j', 'k', 'l', 'e', 'f', 'g', 'h', 'a', 'b', 'c', 'd'];
        const vertexPositions = {
            'm': {x: 150, y: 150}, 'n': {x: 250, y: 150}, 'o': {x: 350, y: 150}, 'p': {x: 450, y: 150},
            'i': {x: 150, y: 250}, 'j': {x: 250, y: 250}, 'k': {x: 350, y: 250}, 'l': {x: 450, y: 250},
            'e': {x: 150, y: 350}, 'f': {x: 250, y: 350}, 'g': {x: 350, y: 350}, 'h': {x: 450, y: 350},
            'a': {x: 150, y: 450}, 'b': {x: 250, y: 450}, 'c': {x: 350, y: 450}, 'd': {x: 450, y: 450}
        };

        const fixedWeights = {
            'm-n': 8, 'n-o': 7, 'o-p': 7,
            'i-j': 9, 'j-k': 7, 'k-l': 1,
            'e-f': 1, 'f-g': 7, 'g-h': 6,
            'a-b': 10, 'b-c': 4, 'c-d': 8,
            'm-i': 6, 'i-e': 9, 'e-a': 9,
            'n-j': 7, 'j-f': 6, 'f-b': 5,
            'o-k': 1, 'k-g': 8, 'g-c': 8,
            'p-l': 2, 'l-h': 1, 'h-d': 7
        };

        let edges = {};
        let startVertex = 'm';
        let distances = {};
        let visited = new Set();
        let predecessors = {};

        // Initialize graph
        function initializeGraph() {
            // Build adjacency list
            for (let key in edges) {
                const [u, v] = key.split('-');
                if (!graph[u]) graph[u] = {};
                if (!graph[v]) graph[v] = {};
                graph[u][v] = edges[key];
                graph[v][u] = edges[key];
            }
        }

        const graph = {};

        // Reset algorithm state
        function resetAlgorithm() {
            distances = {};
            visited = new Set();
            predecessors = {};

            vertices.forEach(v => {
                distances[v] = v === startVertex ? 0 : Infinity;
                predecessors[v] = null;
            });

            document.getElementById('next-step-btn').disabled = false;
            redraw();
        }

        // Load fixed example
        function loadFixedExample() {
            edges = {...fixedWeights};
            initializeGraph();
            resetAlgorithm();
        }

        // Load random example
        function loadRandomExample() {
            edges = {};
            for (let key in fixedWeights) {
                edges[key] = Math.floor(Math.random() * 10) + 1;
            }
            initializeGraph();
            resetAlgorithm();
        }

        // Execute one step of Dijkstra's algorithm
        function nextStep() {
            // Find unvisited vertex with minimum distance
            let minDist = Infinity;
            let minVertex = null;

            vertices.forEach(v => {
                if (!visited.has(v) && distances[v] < minDist) {
                    minDist = distances[v];
                    minVertex = v;
                }
            });

            if (minVertex === null) {
                document.getElementById('next-step-btn').disabled = true;
                return;
            }

            // Visit the vertex
            visited.add(minVertex);

            // Update distances to neighbors
            if (graph[minVertex]) {
                for (let neighbor in graph[minVertex]) {
                    if (!visited.has(neighbor)) {
                        const newDist = distances[minVertex] + graph[minVertex][neighbor];
                        if (newDist < distances[neighbor]) {
                            distances[neighbor] = newDist;
                            predecessors[neighbor] = minVertex;
                        }
                    }
                }
            }

            // Check if all vertices are visited
            if (visited.size === vertices.length) {
                document.getElementById('next-step-btn').disabled = true;
            }

            redraw();
        }

        // Event listeners
        document.getElementById('start-over-btn').addEventListener('click', () => {
            startVertex = 'm';
            document.getElementById('title-display').textContent = "Dijkstra's algorithm starting at vertex m";

            // Update button styles
            document.querySelectorAll('.vertex-grid button').forEach(btn => {
                btn.classList.remove('active');
            });
            document.getElementById('btn-m').classList.add('active');

            loadFixedExample();
        });

        // Vertex button listeners
        vertices.forEach(v => {
            document.getElementById(`btn-${v}`).addEventListener('click', () => {
                startVertex = v;
                document.getElementById('title-display').textContent = `Dijkstra's algorithm starting at vertex ${v}`;

                // Update button styles
                document.querySelectorAll('.vertex-grid button').forEach(btn => {
                    btn.classList.remove('active');
                });
                document.getElementById(`btn-${v}`).classList.add('active');

                resetAlgorithm();
            });
        });

        document.getElementById('fixed-example-btn').addEventListener('click', loadFixedExample);
        document.getElementById('random-example-btn').addEventListener('click', loadRandomExample);
        document.getElementById('next-step-btn').addEventListener('click', nextStep);

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(600, 600);
            canvas.parent('canvas-container');
            noLoop();
            loadFixedExample();
        }

        function draw() {
            background(255);

            // Draw edges
            strokeWeight(1);
            for (let key in edges) {
                const [u, v] = key.split('-');
                const pos1 = vertexPositions[u];
                const pos2 = vertexPositions[v];

                // Check if edge is in shortest path tree
                const isInTree = (visited.has(v) && predecessors[v] === u) || 
                               (visited.has(u) && predecessors[u] === v);

                if (isInTree) {
                    stroke(0, 0, 255);
                    strokeWeight(3);
                } else {
                    stroke(0);
                    strokeWeight(1);
                }

                line(pos1.x, pos1.y, pos2.x, pos2.y);

                // Draw edge weight
                const midX = (pos1.x + pos2.x) / 2;
                const midY = (pos1.y + pos2.y) / 2;

                noStroke();
                fill(255);
                rect(midX - 10, midY - 10, 20, 20);
                fill(0);
                textAlign(CENTER, CENTER);
                text(edges[key], midX, midY);
            }

            // Draw vertices
            vertices.forEach(v => {
                const pos = vertexPositions[v];
                const isVisited = visited.has(v);

                // Draw vertex
                strokeWeight(2);
                stroke(0);
                fill(isVisited ? color(255, 0, 0) : color(0));
                circle(pos.x, pos.y, 10);

                // Draw vertex label
                noStroke();
                fill(0);
                textAlign(CENTER, CENTER);
                text(v, pos.x - 20, pos.y);

                // Draw distance label
                fill(isVisited ? color(255, 0, 0) : color(0));
                const distText = distances[v] === Infinity ? '∞' : distances[v];
                text(`(${distText})`, pos.x, pos.y - 20);
            });
        }
    </script>
</body>
