<body>
    <div id="app-container">
        <div id="controls-panel">
            <div class="control-group">
                <label for="slider-sphere-diameter">diameter of sphere</label>
                <input type="range" id="slider-sphere-diameter" min="0.5" max="3.0" step="0.01" value="1.5">
            </div>

            <div class="control-group">
                <label>polyhedron vertices</label>
                <div id="polyhedron-buttons">
                    <button id="btn-cube">Cube</button>
                    <button id="btn-dodecahedron">Dodecahedron</button>
                    <button id="btn-icosahedron">Icosahedron</button>
                    <button id="btn-octahedron" class="active">Octahedron</button>
                    <button id="btn-tetrahedron">Tetrahedron</button>
                </div>
            </div>

            <div class="control-group">
                <label for="slider-cone-height">height</label>
                <input type="range" id="slider-cone-height" min="0.1" max="4.0" step="0.01" value="1.5">
            </div>

            <div class="control-group">
                <label for="slider-cone-base">base</label>
                <input type="range" id="slider-cone-base" min="0.05" max="1.0" step="0.01" value="0.4">
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script>
        // Scene setup
        const scene = new THREE.Scene();
        const camera = new THREE.PerspectiveCamera(45, window.innerWidth / window.innerHeight, 0.1, 1000);
        camera.position.set(0, 0, 8);

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        renderer.setClearColor(0xf0f0f0);
        const container = document.getElementById('canvas-container');
        renderer.setSize(container.clientWidth, container.clientHeight);
        container.appendChild(renderer.domElement);

        // Controls
        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0x404040);
        scene.add(ambientLight);

        const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
        directionalLight1.position.set(1, 1, 1);
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.4);
        directionalLight2.position.set(-1, -1, -1);
        scene.add(directionalLight2);

        // Sphere
        const sphereGeometry = new THREE.SphereGeometry(1.5 / 2, 32, 32);
        const sphereMaterial = new THREE.MeshPhongMaterial({ 
            color: 0xdddddd, 
            shininess: 50 
        });
        const sphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
        scene.add(sphere);

        // Cone material
        const coneMaterial = new THREE.MeshPhongMaterial({ 
            color: 0xffa500, 
            shininess: 30 
        });

        // Store cones
        let cones = [];
        let currentPolyhedron = 'octahedron';

        // Get vertices for polyhedrons
        function getPolyhedronVertices(type) {
            let geometry;
            const radius = 1;

            switch(type) {
                case 'tetrahedron':
                    geometry = new THREE.TetrahedronGeometry(radius, 0);
                    break;
                case 'cube':
                    geometry = new THREE.BoxGeometry(radius * 1.5, radius * 1.5, radius * 1.5);
                    break;
                case 'octahedron':
                    geometry = new THREE.OctahedronGeometry(radius, 0);
                    break;
                case 'dodecahedron':
                    geometry = new THREE.DodecahedronGeometry(radius, 0);
                    break;
                case 'icosahedron':
                    geometry = new THREE.IcosahedronGeometry(radius, 0);
                    break;
            }

            const vertices = [];
            const positions = geometry.attributes.position.array;
            const vertexSet = new Set();

            for (let i = 0; i < positions.length; i += 3) {
                const vertex = new THREE.Vector3(positions[i], positions[i+1], positions[i+2]);
                const key = `${vertex.x.toFixed(6)},${vertex.y.toFixed(6)},${vertex.z.toFixed(6)}`;
                if (!vertexSet.has(key)) {
                    vertexSet.add(key);
                    vertices.push(vertex);
                }
            }

            return vertices;
        }

        // Create cones
        function createCones() {
            // Remove existing cones
            cones.forEach(cone => scene.remove(cone));
            cones = [];

            const sphereRadius = parseFloat(document.getElementById('slider-sphere-diameter').value) / 2;
            const coneHeight = parseFloat(document.getElementById('slider-cone-height').value);
            const coneBase = parseFloat(document.getElementById('slider-cone-base').value);

            const vertices = getPolyhedronVertices(currentPolyhedron);

            vertices.forEach(vertex => {
                const direction = vertex.clone().normalize();
                const position = direction.clone().multiplyScalar(sphereRadius + coneHeight / 2);

                const coneGeometry = new THREE.ConeGeometry(coneBase, coneHeight, 32);
                const cone = new THREE.Mesh(coneGeometry, coneMaterial);

                cone.position.copy(position);
                cone.quaternion.setFromUnitVectors(new THREE.Vector3(0, 1, 0), direction);

                scene.add(cone);
                cones.push(cone);
            });
        }

        // Update sphere
        function updateSphere() {
            const diameter = parseFloat(document.getElementById('slider-sphere-diameter').value);
            const scale = diameter / 1.5;
            sphere.scale.set(scale, scale, scale);
            createCones();
        }

        // Update cones
        function updateCones() {
            createCones();
        }

        // Event listeners
        document.getElementById('slider-sphere-diameter').addEventListener('input', updateSphere);
        document.getElementById('slider-cone-height').addEventListener('input', updateCones);
        document.getElementById('slider-cone-base').addEventListener('input', updateCones);

        // Polyhedron buttons
        const polyhedronButtons = {
            'btn-tetrahedron': 'tetrahedron',
            'btn-cube': 'cube',
            'btn-octahedron': 'octahedron',
            'btn-dodecahedron': 'dodecahedron',
            'btn-icosahedron': 'icosahedron'
        };

        Object.keys(polyhedronButtons).forEach(buttonId => {
            document.getElementById(buttonId).addEventListener('click', function() {
                // Update active button
                document.querySelectorAll('#polyhedron-buttons button').forEach(btn => {
                    btn.classList.remove('active');
                });
                this.classList.add('active');

                // Update polyhedron
                currentPolyhedron = polyhedronButtons[buttonId];
                createCones();
            });
        });

        // Window resize
        window.addEventListener('resize', () => {
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize
        createCones();
        animate();
    </script>
</body>
</html>
