<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="select-planet">planet</label>
            <select id="select-planet">
                <option value="manual" selected>-- choose orbit manually --</option>
                <option value="Earth">Earth</option>
                <option value="Mars">Mars</option>
            </select>

            <div id="manual-controls">
                <div>
                    <input type="checkbox" id="checkbox-manual" checked>
                    <label for="checkbox-manual" class="checkbox-label">choose orbit manually</label>
                </div>

                <div>
                    <label for="slider-tilt">axis angle relative to ecliptic</label>
                    <input type="range" id="slider-tilt" min="0" max="90" step="0.1" value="23.5">
                    <span id="tilt-value">23.5°</span>
                </div>

                <div>
                    <label for="slider-equinox">spring equinox point</label>
                    <input type="range" id="slider-equinox" min="-180" max="180" step="1" value="0">
                    <span id="equinox-value">0°</span>
                </div>

                <div>
                    <label for="slider-eccentricity">orbit eccentricity</label>
                    <input type="range" id="slider-eccentricity" min="0" max="0.5" step="0.001" value="0.1">
                    <span id="eccentricity-value">0.100</span>
                </div>
            </div>

            <div id="display-controls">
                <div>
                    <input type="checkbox" id="checkbox-snapshots" checked>
                    <label for="checkbox-snapshots" class="checkbox-label">show sun snapshots</label>
                </div>
                <div>
                    <input type="checkbox" id="checkbox-scales" checked>
                    <label for="checkbox-scales" class="checkbox-label">show scales</label>
                </div>
            </div>
        </div>

        <div id="visualization-area">
            <div id="canvas-container"></div>
        </div>
    </div>

    <script>
        const planetData = {
            "Earth": { tilt: 23.44, eccentricity: 0.0167, equinox: 11.26 },
            "Mars": { tilt: 25.19, eccentricity: 0.0934, equinox: -86.9 }
        };

        let analemmaPoints = [];
        let canvasWidth = 800;
        let canvasHeight = 600;

        // Control elements
        const selectPlanet = document.getElementById('select-planet');
        const checkboxManual = document.getElementById('checkbox-manual');
        const sliderTilt = document.getElementById('slider-tilt');
        const sliderEquinox = document.getElementById('slider-equinox');
        const sliderEccentricity = document.getElementById('slider-eccentricity');
        const checkboxSnapshots = document.getElementById('checkbox-snapshots');
        const checkboxScales = document.getElementById('checkbox-scales');

        // Value displays
        const tiltValue = document.getElementById('tilt-value');
        const equinoxValue = document.getElementById('equinox-value');
        const eccentricityValue = document.getElementById('eccentricity-value');

        // Event listeners
        selectPlanet.addEventListener('change', handlePlanetSelection);
        checkboxManual.addEventListener('change', handleManualToggle);
        sliderTilt.addEventListener('input', updateSliderDisplay);
        sliderEquinox.addEventListener('input', updateSliderDisplay);
        sliderEccentricity.addEventListener('input', updateSliderDisplay);
        checkboxSnapshots.addEventListener('change', () => redraw());
        checkboxScales.addEventListener('change', () => redraw());

        function handlePlanetSelection() {
            const selected = selectPlanet.value;
            if (selected === 'manual') {
                checkboxManual.checked = true;
                enableSliders(true);
            } else {
                const data = planetData[selected];
                sliderTilt.value = data.tilt;
                sliderEccentricity.value = data.eccentricity;
                sliderEquinox.value = data.equinox;
                checkboxManual.checked = false;
                enableSliders(false);
                updateSliderDisplay();
            }
            calculateAnalemma();
            redraw();
        }

        function handleManualToggle() {
            if (checkboxManual.checked) {
                selectPlanet.value = 'manual';
                enableSliders(true);
            } else {
                enableSliders(false);
            }
            redraw();
        }

        function enableSliders(enable) {
            sliderTilt.disabled = !enable;
            sliderEquinox.disabled = !enable;
            sliderEccentricity.disabled = !enable;
        }

        function updateSliderDisplay() {
            tiltValue.textContent = sliderTilt.value + '°';
            equinoxValue.textContent = sliderEquinox.value + '°';
            eccentricityValue.textContent = parseFloat(sliderEccentricity.value).toFixed(3);
            calculateAnalemma();
            redraw();
        }

        function solveKeplerEquation(M, ecc) {
            let E = M;
            for (let i = 0; i < 10; i++) {
                E = M + ecc * Math.sin(E);
            }
            return E;
        }

        function calculateAnalemma() {
            analemmaPoints = [];
            const tilt = parseFloat(sliderTilt.value) * Math.PI / 180;
            const ecc = parseFloat(sliderEccentricity.value);
            const equinoxAngle = parseFloat(sliderEquinox.value) * Math.PI / 180;

            for (let d = 0; d <= 365; d++) {
                const M = (2 * Math.PI * d) / 365.25;
                const E = solveKeplerEquation(M, ecc);
                const v = 2 * Math.atan2(
                    Math.sqrt(1 + ecc) * Math.sin(E/2),
                    Math.sqrt(1 - ecc) * Math.cos(E/2)
                );
                const lambda = v + equinoxAngle;

                const y = Math.asin(Math.sin(tilt) * Math.sin(lambda));
                const alpha = Math.atan2(Math.cos(tilt) * Math.sin(lambda), Math.cos(lambda));
                let EoT = M - alpha;

                // Handle wraparound
                if (EoT > Math.PI) EoT -= 2 * Math.PI;
                if (EoT < -Math.PI) EoT += 2 * Math.PI;

                analemmaPoints.push({
                    x: EoT * 180 / Math.PI * 4,  // Scale for display
                    y: y * 180 / Math.PI * 10,   // Scale for display
                    day: d
                });
            }
        }

        function setup() {
            const canvas = createCanvas(canvasWidth, canvasHeight);
            canvas.parent('canvas-container');
            calculateAnalemma();
            noLoop();
        }

        function draw() {
            background(255);

            push();
            translate(width/2, height/2);

            // Draw orbit ellipse at top
            push();
            translate(0, -height/2 + 100);
            noFill();
            stroke(139, 69, 19);
            strokeWeight(2);
            const ecc = parseFloat(sliderEccentricity.value);
            const orbitWidth = 120;
            const orbitHeight = orbitWidth * Math.sqrt(1 - ecc * ecc);
            ellipse(0, 0, orbitWidth, orbitHeight);
            pop();

            // Draw scales if enabled
            if (checkboxScales.checked) {
                stroke(0);
                strokeWeight(1);

                // X-axis
                line(-300, 0, 300, 0);
                // Y-axis
                line(0, -200, 0, 200);

                // Tick marks and labels
                textAlign(CENTER, TOP);
                for (let x = -60; x <= 60; x += 20) {
                    line(x * 4, -5, x * 4, 5);
                    if (x !== 0) {
                        text(x, x * 4, 10);
                    }
                }

                textAlign(RIGHT, CENTER);
                for (let y = -20; y <= 20; y += 10) {
                    if (y !== 0) {
                        line(-5, -y * 10, 5, -y * 10);
                        text(y, -10, -y * 10);
                    }
                }
            }

            // Draw analemma curve
            noFill();
            stroke(255, 165, 0);
            strokeWeight(2);
            beginShape();
            for (let point of analemmaPoints) {
                vertex(point.x, -point.y);
            }
            endShape();

            // Draw sun snapshots if enabled
            if (checkboxSnapshots.checked) {
                fill(255, 165, 0);
                noStroke();
                for (let i = 0; i < analemmaPoints.length; i += 20) {
                    circle(analemmaPoints[i].x, -analemmaPoints[i].y, 8);
                }
            }

            pop();
        }

        // Initialize
        updateSliderDisplay();
    </script>
</body>
</html>
