                C: {A: 16, B: 12, D: 43, E: 32, F: 36, G: 44},
                D: {A: 44, B: 12, C: 43, E: 41, F: 30, G: 35},
                E: {A: 30, B: 10, C: 32, D: 41, F: 31, G: 20},
                F: {A: 44, B: 30, C: 36, D: 30, E: 31, G: 18},
                G: {A: 23, B: 25, C: 44, D: 35, E: 20, F: 18}
            },
            optimal: {
                path: ['A', 'C', 'B', 'D', 'F', 'G', 'E', 'A'],
                weight: 138
            }
        };

        // Generate random graph
        function generateRandomGraph() {
            const vertices = {};
            const edges = {};
            const vertexNames = ['A', 'B', 'C', 'D', 'E', 'F', 'G'];

            // Position vertices in a heptagon
            const centerX = 200;
            const centerY = 200;
            const radius = 140;

            vertexNames.forEach((name, i) => {
                const angle = (i * 2 * Math.PI) / 7 - Math.PI / 2;
                vertices[name] = {
                    x: centerX + radius * Math.cos(angle),
                    y: centerY + radius * Math.sin(angle)
                };
                edges[name] = {};
            });

            // Generate random weights
            for (let i = 0; i < vertexNames.length; i++) {
                for (let j = i + 1; j < vertexNames.length; j++) {
                    const weight = Math.floor(Math.random() * 41) + 10; // 10-50
                    edges[vertexNames[i]][vertexNames[j]] = weight;
                    edges[vertexNames[j]][vertexNames[i]] = weight;
                }
            }

            // Find optimal solution (brute force for 7 vertices)
            const optimal = findOptimalHamiltonianCircuit(vertexNames, edges);

            return { vertices, edges, optimal };
        }

        // Find optimal Hamiltonian circuit
        function findOptimalHamiltonianCircuit(vertexNames, edges) {
            let minWeight = Infinity;
            let optimalPath = [];

            function permute(arr, start) {
                if (start === arr.length - 1) {
                    const path = [...arr, arr[0]];
                    let weight = 0;
                    for (let i = 0; i < path.length - 1; i++) {
                        weight += edges[path[i]][path[i + 1]];
                    }
                    if (weight < minWeight) {
                        minWeight = weight;
                        optimalPath = path;
                    }
                    return;
                }

                for (let i = start; i < arr.length; i++) {
                    [arr[start], arr[i]] = [arr[i], arr[start]];
                    permute(arr, start + 1);
                    [arr[start], arr[i]] = [arr[i], arr[start]];
                }
            }

            permute(vertexNames.slice(), 1); // Fix first vertex to avoid duplicate cycles

            return { path: optimalPath, weight: minWeight };
        }

        // Initialize algorithm state
        function initializeAlgorithmState() {
            algorithmState = {
                path: [],
                visited: new Set(),
                currentVertex: null,
                totalWeight: 0,
                step: 0,
                complete: false
            };

            if (currentAlgorithm === 'cheapest-link') {
                algorithmState.sortedEdges = getSortedEdges();
                algorithmState.vertexDegree = {};
                Object.keys(graphData.vertices).forEach(v => {
                    algorithmState.vertexDegree[v] = 0;
                });
            }
        }

        // Get sorted edges for cheapest link algorithm
        function getSortedEdges() {
            const edges = [];
            const vertices = Object.keys(graphData.vertices);

            for (let i = 0; i < vertices.length; i++) {
                for (let j = i + 1; j < vertices.length; j++) {
                    edges.push({
                        from: vertices[i],
                        to: vertices[j],
                        weight: graphData.edges[vertices[i]][vertices[j]]
                    });
                }
            }

            return edges.sort((a, b) => a.weight - b.weight);
        }

        // Nearest neighbor step
        function nearestNeighborStep() {
            if (algorithmState.complete) return;

            if (algorithmState.currentVertex === null) {
                algorithmState.currentVertex = startingVertex;
                algorithmState.visited.add(startingVertex);
            } else {
                let nearestVertex = null;
                let minWeight = Infinity;

                Object.keys(graphData.edges[algorithmState.currentVertex]).forEach(vertex => {
                    if (!algorithmState.visited.has(vertex)) {
                        const weight = graphData.edges[algorithmState.currentVertex][vertex];
                        if (weight < minWeight) {
                            minWeight = weight;
                            nearestVertex = vertex;
                        }
                    }
                });

                if (nearestVertex) {
                    algorithmState.path.push({
                        from: algorithmState.currentVertex,
                        to: nearestVertex,
                        weight: minWeight
                    });
                    algorithmState.totalWeight += minWeight;
                    algorithmState.currentVertex = nearestVertex;
                    algorithmState.visited.add(nearestVertex);
                } else {
                    // Return to start
                    const weight = graphData.edges[algorithmState.currentVertex][startingVertex];
                    algorithmState.path.push({
                        from: algorithmState.currentVertex,
                        to: startingVertex,
                        weight: weight
                    });
                    algorithmState.totalWeight += weight;
                    algorithmState.complete = true;
                }
            }

            algorithmState.step++;
            updateDisplay();
        }

        // Cheapest link step
        function cheapestLinkStep() {
            if (algorithmState.complete) return;

            for (let i = algorithmState.step; i < algorithmState.sortedEdges.length; i++) {
                const edge = algorithmState.sortedEdges[i];

                // Check if adding this edge would give a vertex degree > 2
                if (algorithmState.vertexDegree[edge.from] >= 2 || 
                    algorithmState.vertexDegree[edge.to] >= 2) {
                    continue;
                }

                // Check if it would create a premature cycle
                if (algorithmState.path.length < Object.keys(graphData.vertices).length - 1) {
                    if (wouldCreateCycle(edge)) {
                        continue;
                    }
                }

                // Add the edge
                algorithmState.path.push(edge);
                algorithmState.totalWeight += edge.weight;
                algorithmState.vertexDegree[edge.from]++;
                algorithmState.vertexDegree[edge.to]++;

                algorithmState.step = i + 1;

                if (algorithmState.path.length === Object.keys(graphData.vertices).length) {
                    algorithmState.complete = true;
                }

                break;
            }

            updateDisplay();
        }

        // Check if edge would create cycle (for cheapest link)
        function wouldCreateCycle(newEdge) {
            const adjacencyList = {};
            Object.keys(graphData.vertices).forEach(v => {
                adjacencyList[v] = [];
            });

            algorithmState.path.forEach(edge => {
                adjacencyList[edge.from].push(edge.to);
                adjacencyList[edge.to].push(edge.from);
            });

            // Check if from and to are already connected
            const visited = new Set();
            const queue = [newEdge.from];
            visited.add(newEdge.from);

            while (queue.length > 0) {
                const current = queue.shift();
                if (current === newEdge.to) return true;

                adjacencyList[current].forEach(neighbor => {
                    if (!visited.has(neighbor)) {
                        visited.add(neighbor);
                        queue.push(neighbor);
                    }
                });
            }

            return false;
        }

        // Update display
        function updateDisplay() {
            // Update table
            const tableTitle = document.getElementById('table-title');
            const table = document.getElementById('table-edge-list');

            if (algorithmState.path.length > 0) {
                tableTitle.textContent = 'Edges selected by algorithm';

                let tableHTML = '<tr><th>Edge</th><th>Weight</th></tr>';
                algorithmState.path.forEach(edge => {
                    tableHTML += `<tr><td>${edge.from}-${edge.to}</td><td>${edge.weight}</td></tr>`;
                });
                table.innerHTML = tableHTML;
            }

            // Update total weight
            const totalWeightText = document.getElementById('text-total-weight');
            if (algorithmState.complete) {
                totalWeightText.textContent = `Total weight of Hamilton circuit: ${algorithmState.totalWeight}`;
            } else {
                totalWeightText.textContent = '';
            }

            // Disable next step if complete
            if (algorithmState.complete) {
                document.getElementById('btn-next-step').disabled = true;
            }

            // Redraw canvas
            if (algoSketch) {
                algoSketch.redraw();
            }
        }

        // P5.js sketch for algorithm visualization
        function createAlgoSketch(p) {
            p.setup = function() {
                p.createCanvas(400, 400);
                p.noLoop();
            };

            p.draw = function() {
                p.background(255);

                if (!graphData) return;

                // Draw all edges
                p.stroke(200);
                p.strokeWeight(1);
                Object.keys(graphData.vertices).forEach(v1 => {
                    Object.keys(graphData.edges[v1]).forEach(v2 => {
                        if (v1 < v2) { // Draw each edge only once
                            const pos1 = graphData.vertices[v1];
                            const pos2 = graphData.vertices[v2];
                            p.line(pos1.x, pos1.y, pos2.x, pos2.y);

                            // Draw weight
                            const midX = (pos1.x + pos2.x) / 2;
                            const midY = (pos1.y + pos2.y) / 2;
                            p.push();
                            p.fill(255);
                            p.noStroke();
                            p.rect(midX - 10, midY - 8, 20, 16);
                            p.fill(0);
                            p.textAlign(p.CENTER, p.CENTER);
                            p.text(graphData.edges[v1][v2], midX, midY);
                            p.pop();
                        }
                    });
                });

                // Draw algorithm path
                if (algorithmState && algorithmState.path.length > 0) {
                    p.stroke(255, 140, 0);
                    p.strokeWeight(3);
                    algorithmState.path.forEach(edge => {
                        const pos1 = graphData.vertices[edge.from];
                        const pos2 = graphData.vertices[edge.to];
                        p.line(pos1.x, pos1.y, pos2.x, pos2.y);
                    });
                }

                // Draw vertices
                p.strokeWeight(2);
                p.stroke(0);
                Object.keys(graphData.vertices).forEach(vertex => {
                    const pos = graphData.vertices[vertex];
                    p.fill(255);
                    p.ellipse(pos.x, pos.y, 30, 30);
                    p.fill(0);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.text(vertex, pos.x, pos.y);
                });
            };
        }

        // P5.js sketch for optimal solution
        function createOptimalSketch(p) {
            p.setup = function() {
                p.createCanvas(400, 400);
                p.noLoop();
            };

            p.draw = function() {
                p.background(255);

                if (!graphData || !graphData.optimal) return;

                // Draw all edges
                p.stroke(200);
                p.strokeWeight(1);
                Object.keys(graphData.vertices).forEach(v1 => {
                    Object.keys(graphData.edges[v1]).forEach(v2 => {
                        if (v1 < v2) {
                            const pos1 = graphData.vertices[v1];
                            const pos2 = graphData.vertices[v2];
                            p.line(pos1.x, pos1.y, pos2.x, pos2.y);

                            // Draw weight
                            const midX = (pos1.x + pos2.x) / 2;
                            const midY = (pos1.y + pos2.y) / 2;
                            p.push();
                            p.fill(255);
                            p.noStroke();
                            p.rect(midX - 10, midY - 8, 20, 16);
                            p.fill(0);
                            p.textAlign(p.CENTER, p.CENTER);
                            p.text(graphData.edges[v1][v2], midX, midY);
                            p.pop();
                        }
                    });
                });

                // Draw optimal path
                p.stroke(0, 100, 255);
                p.strokeWeight(3);
                for (let i = 0; i < graphData.optimal.path.length - 1; i++) {
                    const v1 = graphData.optimal.path[i];
                    const v2 = graphData.optimal.path[i + 1];
                    const pos1 = graphData.vertices[v1];
                    const pos2 = graphData.vertices[v2];
                    p.line(pos1.x, pos1.y, pos2.x, pos2.y);
                }

                // Draw vertices
                p.strokeWeight(2);
                p.stroke(0);
                Object.keys(graphData.vertices).forEach(vertex => {
                    const pos = graphData.vertices[vertex];
                    p.fill(255);
                    p.ellipse(pos.x, pos.y, 30, 30);
                    p.fill(0);
                    p.textAlign(p.CENTER, p.CENTER);
                    p.text(vertex, pos.x, pos.y);
                });
            };
        }

        // Event handlers
        function handleAlgorithmChange() {
            currentAlgorithm = document.querySelector('input[name="algorithm-choice"]:checked').value;
            const vertexSelector = document.getElementById('starting-vertex-selector');

            if (currentAlgorithm === 'nearest-neighbor') {
                vertexSelector.style.display = 'block';
            } else {
                vertexSelector.style.display = 'none';
            }

            handleStartOver();
        }

        function handleStartOver() {
            startingVertex = null;
            initializeAlgorithmState();

            // Clear display
            document.getElementById('table-title').textContent = '';
            document.getElementById('table-edge-list').innerHTML = '';
            document.getElementById('text-total-weight').textContent = '';

            // Reset controls
            document.getElementById('btn-next-step').disabled = true;
            document.querySelectorAll('.vertex-btn').forEach(btn => {
                btn.disabled = false;
            });

            // Update title
            if (currentAlgorithm === 'nearest-neighbor') {
                document.getElementById('title-algo').textContent = 'Select a starting vertex';
            } else {
                document.getElementById('title-algo').textContent = 'The cheapest link algorithm';
                document.getElementById('btn-next-step').disabled = false;
            }

            if (algoSketch) {
                algoSketch.redraw();
            }
        }

        function handleExampleChange(type) {
            currentExample = type;

            if (type === 'fixed') {
                graphData = JSON.parse(JSON.stringify(fixedGraph));
            } else {
                graphData = generateRandomGraph();
            }

            document.getElementById('text-example-type-algo').textContent = `${type} example`;
            document.getElementById('text-example-type-optimal').textContent = `${type} example`;

            handleStartOver();
        }

        function handleVertexSelection(vertex) {
            startingVertex = vertex;

            // Disable all vertex buttons
            document.querySelectorAll('.vertex-btn').forEach(btn => {
                btn.disabled = true;
            });

            // Enable next step
            document.getElementById('btn-next-step').disabled = false;

            // Update title
            document.getElementById('title-algo').textContent = 
                `The nearest neighbor algorithm starting at vertex ${vertex}`;
        }

        function handleNextStep() {
            if (currentAlgorithm === 'nearest-neighbor') {
                nearestNeighborStep();
            } else {
                cheapestLinkStep();
            }
        }

        function handleOptimalToggle() {
            const container = document.getElementById('optimal-result-container');
            const checkbox = document.getElementById('checkbox-optimal');

            if (checkbox.checked) {
                container.style.display = 'block';
                document.getElementById('text-optimal-weight').textContent = 
                    `Total weight of optimal circuit: ${graphData.optimal.weight}`;
                if (optimalSketch) {
                    optimalSketch.redraw();
                }
            } else {
                container.style.display = 'none';
            }
        }

        // Initialize on load
        window.addEventListener('load', () => {
            // Create p5 instances
            algoSketch = new p5(createAlgoSketch, 'canvas-algo-wrapper');
            optimalSketch = new p5(createOptimalSketch, 'canvas-optimal-wrapper');

            // Load fixed example
            graphData = JSON.parse(JSON.stringify(fixedGraph));
            initializeAlgorithmState();

            // Set up event listeners
            document.getElementById('algo-cheapest-link').addEventListener('change', handleAlgorithmChange);
            document.getElementById('algo-nearest-neighbor').addEventListener('change', handleAlgorithmChange);
            document.getElementById('btn-start-over').addEventListener('click', handleStartOver);
            document.getElementById('btn-fixed-example').addEventListener('click', () => handleExampleChange('fixed'));
            document.getElementById('btn-random-example').addEventListener('click', () => handleExampleChange('random'));
            document.getElementById('btn-next-step').addEventListener('click', handleNextStep);
            document.getElementById('checkbox-optimal').addEventListener('change', handleOptimalToggle);

            document.querySelectorAll('.vertex-btn').forEach(btn => {
                btn.addEventListener('click', () => handleVertexSelection(btn.dataset.vertex));
            });
        });
    </script>
</body>
