    <script>
        // Global variables to store calculated vectors and scales
        let A, B, u, v, w;
        let xScale = 80, yScale = 80;
        let calculationError = false;

        function setup() {
            let canvas = createCanvas(windowWidth - 250, windowHeight);
            canvas.parent('canvas-container');
            noLoop();

            // Attach event listeners to sliders
            const sliders = ['au', 'bu', 'av', 'bv', 'aw', 'bw', 'x-scale', 'y-scale'];
            sliders.forEach(id => {
                const slider = document.getElementById(`slider-${id}`);
                const valueSpan = document.getElementById(`value-${id}`);

                slider.addEventListener('input', function() {
                    valueSpan.textContent = this.value;

                    if (id === 'x-scale') {
                        xScale = parseFloat(this.value);
                    } else if (id === 'y-scale') {
                        yScale = parseFloat(this.value);
                    } else {
                        calculateVectors();
                    }
                    redraw();
                });
            });

            // Initial calculation and draw
            calculateVectors();
            redraw();
        }

        function calculateVectors() {
            try {
                // Read slider values
                const au = parseFloat(document.getElementById('slider-au').value);
                const bu = parseFloat(document.getElementById('slider-bu').value);
                const av = parseFloat(document.getElementById('slider-av').value);
                const bv = parseFloat(document.getElementById('slider-bv').value);
                const aw = parseFloat(document.getElementById('slider-aw').value);
                const bw = parseFloat(document.getElementById('slider-bw').value);

                // Construct measurement matrix M
                const M = math.matrix([[au, bu], [av, bv], [aw, bw]]);

                // Perform SVD
                const svd = math.svd(M);
                const U = svd.U;
                const S_hat = U;

                // Set up and solve system for L
                const K = math.matrix([
                    [U.get([0,0])*U.get([0,0]), 2*U.get([0,0])*U.get([0,1]), U.get([0,1])*U.get([0,1])],
                    [U.get([1,0])*U.get([1,0]), 2*U.get([1,0])*U.get([1,1]), U.get([1,1])*U.get([1,1])],
                    [U.get([2,0])*U.get([2,0]), 2*U.get([2,0])*U.get([2,1]), U.get([2,1])*U.get([2,1])]
                ]);

                const ones = math.matrix([1, 1, 1]);
                const l_vec = math.lusolve(K, ones);

                // Construct L matrix
                const L = math.matrix([
                    [l_vec.get([0,0]), l_vec.get([1,0])],
                    [l_vec.get([1,0]), l_vec.get([2,0])]
                ]);

                // Check if L is positive definite
                const eigenvalues = math.eigs(L).values;
                if (eigenvalues.toArray().some(e => e <= 0)) {
                    throw new Error("L is not positive definite");
                }

                // Cholesky decomposition
                const Q = choleskyDecomposition(L);

                // Calculate final matrices
                const S = math.multiply(U, Q);
                const D_matrix = math.diag(svd.s);
                const Q_inv = math.inv(Q);
                const V_t = math.transpose(svd.V);
                const P = math.multiply(math.multiply(Q_inv, D_matrix), V_t);

                // Extract vectors
                u = createVector(S.get([0,0]), S.get([0,1]));
                v = createVector(S.get([1,0]), S.get([1,1]));
                w = createVector(S.get([2,0]), S.get([2,1]));
                A = createVector(P.get([0,0]), P.get([1,0]));
                B = createVector(P.get([0,1]), P.get([1,1]));

                calculationError = false;
            } catch (error) {
                console.error("Calculation error:", error);
                calculationError = true;
            }
        }

        function choleskyDecomposition(L) {
            const a = L.get([0,0]);
            const b = L.get([0,1]);
            const c = L.get([1,1]);

            const l11 = Math.sqrt(a);
            const l21 = b / l11;
            const l22 = Math.sqrt(c - l21 * l21);

            return math.matrix([[l11, 0], [l21, l22]]);
        }

