            circle(B.x, B.y, 15/xScale);

            // Draw labels
            push();
            scale(1/xScale, -1/yScale);
            textAlign(CENTER, CENTER);
            textSize(14);

            fill(0, 0, 205);
            text('u', u.x * xScale * 5, -u.y * yScale * 5);

            fill(100, 149, 237);
            text('v', v.x * xScale * 5, -v.y * yScale * 5);

            fill(0, 0, 205);
            text('w', w.x * xScale * 5, -w.y * yScale * 5);

            fill(255, 0, 0);
            text('O', 15, -15);
            text('A', A.x * xScale + 15, -A.y * yScale - 15);

            fill(255, 165, 0);
            text('B', B.x * xScale + 15, -B.y * yScale - 15);

            pop();
            pop();
        }

        function drawAxis(axis, length) {
            line(-axis.x * length, -axis.y * length, axis.x * length, axis.y * length);
        }

        function drawProjection(point, axis, dotProduct) {
            const projX = dotProduct * axis.x;
            const projY = dotProduct * axis.y;

            // Draw line from point to projection
            line(point.x, point.y, projX, projY);

            // Draw projection point
            fill(0, 255, 0);
            noStroke();
            circle(projX, projY, 8/xScale);
            stroke(128);
        }

        function windowResized() {
            resizeCanvas(windowWidth - 250, windowHeight);
            redraw();
        }
    </script>
</body>
</html>
