    <script>
        // Global state
        let currentType = '2A2';
        let currentDimension = '2D';
        let t1 = 0, t2 = 0;

        // Three.js objects
        let scene, camera, renderer, controls;
        let currentMesh, wireframeMesh;

        // Initialize Three.js
        function initThree() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf8f8f8);

            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth - 250, window.innerHeight);
            document.getElementById('visualization-container').appendChild(renderer.domElement);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.5);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            setupCamera();
        }

        function setupCamera() {
            if (camera) {
                scene.remove(camera);
            }

            const aspect = (window.innerWidth - 250) / window.innerHeight;

            if (currentDimension === '2D') {
                const frustumSize = 10;
                camera = new THREE.OrthographicCamera(
                    frustumSize * aspect / -2,
                    frustumSize * aspect / 2,
                    frustumSize / 2,
                    frustumSize / -2,
                    0.1,
                    1000
                );
                camera.position.set(0, 0, 10);
                camera.lookAt(0, 0, 0);

                if (controls) {
                    controls.dispose();
                    controls = null;
                }
            } else {
                camera = new THREE.PerspectiveCamera(50, aspect, 0.1, 1000);
                camera.position.set(10, 10, 10);
                camera.lookAt(0, 0, 0);

                controls = new THREE.OrbitControls(camera, renderer.domElement);
                controls.enableDamping = true;
                controls.dampingFactor = 0.05;
            }
        }

        // Parametric functions
        function parametricFunctions(type) {
            const T1 = t1 * 1.5;
            const T2 = t2 * 1.5;

            const functions = {
                '2A1': (u, v) => {
                    const U = u * 4 - 2;
                    return new THREE.Vector3(U + T1, U * U + T2, 0);
                },
                '2A2': (u, v) => {
                    const U = u * 3 - 1.5;
                    return new THREE.Vector3(2 * U * U * U + T1, 3 * U * U + T2, 0);
                },
                '2B2': (u, v) => {
                    const U = u * 4 - 2;
                    return new THREE.Vector3(U * U + T1, U * U * U * U + T2, 0);
                },
                '3A1p': (u, v) => {
                    const U = u * 4 - 2;
                    const V = v * 4 - 2;
                    return new THREE.Vector3(U, V, 0.3 * (U * U + V * V) + T1 * U + T2 * V);
                },
                '3A1m': (u, v) => {
                    const U = u * 4 - 2;
                    const V = v * 4 - 2;
                    return new THREE.Vector3(U, V, -0.3 * (U * U + V * V) + T1 * U + T2 * V);
                },
                '3A2': (u, v) => {
                    const U = u * 3 - 1.5;
                    const V = v * 4 - 2;
                    return new THREE.Vector3(2 * U * U * U + T1, 3 * U * U + T2, V);
                },
                '3A3': (u, v) => {
                    const U = u * 3 - 1.5;
                    const V = v * 8 - 4;
                    return new THREE.Vector3(
                        3 * U * U * U * U + U * U * V,
                        -4 * U * U * U - 2 * U * V + T1 * 2,
                        V + T2 * 2
                    );
                },
                '3B2p': (u, v) => {
                    const U = u * 4 - 2;
                    const V = v * 4 - 2;
                    const term = U * U + T1;
                    return new THREE.Vector3(U, V, term * term + V * V + T2);
                },
                '3B2m': (u, v) => {
                    const U = u * 4 - 2;
                    const V = v * 4 - 2;
                    return new THREE.Vector3(U, V, -(U * U * U * U + T1 * 2 * U * U + V * V - T2));
                },
                '3B3': (u, v) => {
                    const U = u * 4 - 2;
                    const V = v * 4 - 2;
                    return new THREE.Vector3(U, V, U * U * U + T1 * U + V * V + T2);
                },
                '3C3p': (u, v) => {
                    const U = u * 4 - 2;
                    const V = v * 4 - 2;
                    return new THREE.Vector3(
                        U,
                        V,
                        U * U * U - 3 * U * V * V + T1 * 2 * (U * U + V * V) + T2 * 4 * U
                    );
                },
                '3C3m': (u, v) => {
                    const U = u * 4 - 2;
                    const V = v * 4 - 2;
                    return new THREE.Vector3(U, V, U * U * U + V * V * V + T1 * 4 * U + T2 * 4 * V);
                }
            };

            return functions[type];
        }

        // Update visualization
        function updateVisualization() {
            // Clear previous meshes
            if (currentMesh) {
                scene.remove(currentMesh);
                currentMesh.geometry.dispose();
                currentMesh.material.dispose();
            }
            if (wireframeMesh) {
                scene.remove(wireframeMesh);
                wireframeMesh.geometry.dispose();
                wireframeMesh.material.dispose();
            }

            const paramFunc = parametricFunctions(currentType);

            if (currentDimension === '2D') {
                // Create line geometry for 2D
                const points = [];
                const steps = 200;
                for (let i = 0; i <= steps; i++) {
                    const u = i / steps;
                    const point = paramFunc(u, 0);
                    points.push(point);
                }

                const geometry = new THREE.BufferGeometry().setFromPoints(points);
                const material = new THREE.LineBasicMaterial({ color: 0x4a69bd, linewidth: 2 });
                currentMesh = new THREE.Line(geometry, material);
                scene.add(currentMesh);
            } else {
                // Create parametric geometry for 3D
                const geometry = new THREE.ParametricGeometry(paramFunc, 50, 50);
                const material = new THREE.MeshPhongMaterial({
                    color: 0xffc34d,
                    side: THREE.DoubleSide,
                    shininess: 100
                });
                currentMesh = new THREE.Mesh(geometry, material);
                scene.add(currentMesh);

                // Add wireframe for A3 type
                if (currentType === '3A3') {
                    const wireframeGeometry = new THREE.WireframeGeometry(geometry);
                    const wireframeMaterial = new THREE.LineBasicMaterial({ color: 0x000000 });
                    wireframeMesh = new THREE.LineSegments(wireframeGeometry, wireframeMaterial);
                    scene.add(wireframeMesh);
                }
            }
        }

        // Button event handlers
        function setupButtons() {
            const allButtons = document.querySelectorAll('button');

            allButtons.forEach(button => {
                button.addEventListener('click', () => {
                    // Remove active class from all buttons
                    allButtons.forEach(b => b.classList.remove('active'));
                    button.classList.add('active');

                    // Extract type from button ID
                    const id = button.id;
                    currentType = id.replace('btn-', '');
                    currentDimension = currentType.startsWith('2') ? '2D' : '3D';

                    // Update camera if dimension changed
                    setupCamera();

                    // Update visualization
                    updateVisualization();
                });
            });
        }

        // Time pad interaction
        function setupTimePad() {
            const timePad = document.getElementById('time-pad');
            const timeHandle = document.getElementById('time-handle');
            let isDragging = false;

            function updateHandlePosition(e) {
                const rect = timePad.getBoundingClientRect();
                let x = e.clientX - rect.left;
                let y = e.clientY - rect.top;

                // Constrain to bounds
                x = Math.max(0, Math.min(rect.width, x));
                y = Math.max(0, Math.min(rect.height, y));

                // Update handle position
                timeHandle.style.left = x + 'px';
                timeHandle.style.top = y + 'px';

                // Calculate t1 and t2
                t1 = (x / rect.width) * 2 - 1;
                t2 = 1 - (y / rect.height) * 2;

                // Update visualization
                updateVisualization();
            }

            timeHandle.addEventListener('mousedown', () => {
                isDragging = true;
            });

            document.addEventListener('mousemove', (e) => {
                if (isDragging) {
                    updateHandlePosition(e);
                }
            });

            document.addEventListener('mouseup', () => {
                isDragging = false;
            });

            timePad.addEventListener('click', (e) => {
                if (e.target === timePad) {
                    updateHandlePosition(e);
                }
            });
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);

            if (controls) {
                controls.update();
            }

            renderer.render(scene, camera);
        }

        // Window resize handler
        function onWindowResize() {
            const width = window.innerWidth - 250;
            const height = window.innerHeight;

            if (camera.isOrthographicCamera) {
                const aspect = width / height;
                const frustumSize = 10;
                camera.left = frustumSize * aspect / -2;
                camera.right = frustumSize * aspect / 2;
                camera.top = frustumSize / 2;
                camera.bottom = frustumSize / -2;
            } else {
                camera.aspect = width / height;
            }

            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        }

        // Initialize everything
        window.addEventListener('DOMContentLoaded', () => {
            initThree();
            setupButtons();
            setupTimePad();
            updateVisualization();
            animate();

            window.addEventListener('resize', onWindowResize);
        });
    </script>
</body>
</html>
