<body>
    <div id="main-container">
        <div id="control-panel">
            <div class="left-controls">
                <select id="select-plot">
                    <option value="semibatch">Semibatch Reactor</option>
                    <option value="batch">Batch Reactor</option>
                    <option value="selectivity">Selectivity Ratio</option>
                </select>
                <div class="feed-cycler">
                    <span id="cycler-feed-label">feed A to B</span>
                    <button id="btn-feed-down">▼</button>
                    <button id="btn-feed-up">▲</button>
                </div>
            </div>
            <div class="slider-group">
                <div class="slider-control">
                    <label for="slider-feed-rate">feed rate</label>
                    <input type="range" id="slider-feed-rate" min="10" max="200" value="10" step="1">
                    <input type="number" id="input-feed-rate" min="10" max="200" value="10" step="1">
                </div>
                <div class="slider-control">
                    <label for="slider-ua">UA</label>
                    <input type="range" id="slider-ua" min="5000" max="30000" value="15000" step="100">
                    <input type="number" id="input-ua" min="5000" max="30000" value="15000" step="100">
                </div>
                <div class="slider-control">
                    <label for="slider-dh1">\(\Delta H_1\)</label>
                    <input type="range" id="slider-dh1" min="500" max="5000" value="1000" step="50">
                    <input type="number" id="input-dh1" min="500" max="5000" value="1000" step="50">
                </div>
                <div class="slider-control">
                    <label for="slider-dh2">\(\Delta H_2\)</label>
                    <input type="range" id="slider-dh2" min="500" max="5000" value="3000" step="50">
                    <input type="number" id="input-dh2" min="500" max="5000" value="3000" step="50">
                </div>
            </div>
        </div>
        <div id="plot-area">
            <canvas id="plot-canvas"></canvas>
        </div>
    </div>

    <script>
        // Wait for MathJax to load
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']]
            }
        };

        // Global variables
        let feedConfig = 'feed A to B';
        let simulationData = null;

        // Constants
        const V = 100; // L
        const T_initial = 330; // K
        const T_coolant = 330; // K
        const R = 8.314; // J/mol/K
        const Cp_total = 150000; // J/K
        const k1_0 = 3e4; // L²/(mol²·h)
        const E1 = 30000; // J/mol
        const k2_0 = 1e5; // L/(mol·h)
        const E2 = 40000; // J/mol

        // Setup controls
        function setupControls() {
            // Sync sliders and inputs
            const sliderIds = ['feed-rate', 'ua', 'dh1', 'dh2'];
            sliderIds.forEach(id => {
                const slider = document.getElementById(`slider-${id}`);
                const input = document.getElementById(`input-${id}`);

                slider.addEventListener('input', () => {
                    input.value = slider.value;
                    runSimulation();
                });

                input.addEventListener('input', () => {
                    slider.value = input.value;
                    runSimulation();
                });
            });

            // Feed cycler
            document.getElementById('btn-feed-up').addEventListener('click', () => {
                feedConfig = feedConfig === 'feed A to B' ? 'feed B to A' : 'feed A to B';
                document.getElementById('cycler-feed-label').textContent = feedConfig;
                runSimulation();
            });

            document.getElementById('btn-feed-down').addEventListener('click', () => {
                feedConfig = feedConfig === 'feed A to B' ? 'feed B to A' : 'feed A to B';
                document.getElementById('cycler-feed-label').textContent = feedConfig;
                runSimulation();
            });

            // Plot type selector
            document.getElementById('select-plot').addEventListener('change', runSimulation);
        }

        // RK4 ODE solver
        function rk4(y, t, dt, derivatives) {
            const k1 = derivatives(y, t);
            const k2 = derivatives(y.map((v, i) => v + 0.5 * dt * k1[i]), t + 0.5 * dt);
            const k3 = derivatives(y.map((v, i) => v + 0.5 * dt * k2[i]), t + 0.5 * dt);
            const k4 = derivatives(y.map((v, i) => v + dt * k3[i]), t + dt);

            return y.map((v, i) => v + (dt / 6) * (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]));
        }

        // Get control values
        function getControlValues() {
            return {
                plotType: document.getElementById('select-plot').value,
                feedRate: parseFloat(document.getElementById('input-feed-rate').value),
                UA: parseFloat(document.getElementById('input-ua').value),
                dH1: parseFloat(document.getElementById('input-dh1').value),
                dH2: parseFloat(document.getElementById('input-dh2').value)
            };
        }

        // Run simulation
        function runSimulation() {
            const controls = getControlValues();
            const dt = 0.01;
            const tEnd = 2.0;
            const nSteps = Math.floor(tEnd / dt);
            const t_f = 100 / controls.feedRate;

            // Batch reactor simulation
            function simulateBatch() {
                const y0 = [100, 100, 0, 0, T_initial];
                const result = { t: [], Na: [], Nb: [], Nd: [], Nu: [], T: [] };

                let y = [...y0];
                let t = 0;

                const derivatives = (state, time) => {
                    const [Na, Nb, Nd, Nu, T] = state;
                    const k1 = k1_0 * Math.exp(-E1 / (R * T));
                    const k2 = k2_0 * Math.exp(-E2 / (R * T));
                    const r1 = k1 * (Na / V) * Math.pow(Nb / V, 2);
                    const r2 = k2 * (Na / V) * (Nb / V);

                    const dNa_dt = -(r1 + r2) * V;
                    const dNb_dt = -(r1 + r2) * V;
                    const dNd_dt = r1 * V;
                    const dNu_dt = r2 * V;
                    const dT_dt = (controls.UA * (T_coolant - T) + (-controls.dH1 * r1 - controls.dH2 * r2) * V) / Cp_total;

                    return [dNa_dt, dNb_dt, dNd_dt, dNu_dt, dT_dt];
                };

                for (let i = 0; i <= nSteps; i++) {
                    result.t.push(t);
                    result.Na.push(y[0]);
                    result.Nb.push(y[1]);
                    result.Nd.push(y[2]);
                    result.Nu.push(y[3]);
                    result.T.push(y[4]);

                    if (i < nSteps) {
                        y = rk4(y, t, dt, derivatives);
                        t += dt;
                    }
                }

                return result;
            }

            // Semibatch reactor simulation
            function simulateSemibatch() {
                const isFeedAtoB = feedConfig === 'feed A to B';
                const y0 = isFeedAtoB ? [0, 100, 0, 0, T_initial] : [100, 0, 0, 0, T_initial];
                const result = { t: [], Na: [], Nb: [], Nd: [], Nu: [], T: [] };

                let y = [...y0];
                let t = 0;

                const derivatives = (state, time) => {
                    const [Na, Nb, Nd, Nu, T] = state;
                    const k1 = k1_0 * Math.exp(-E1 / (R * T));
                    const k2 = k2_0 * Math.exp(-E2 / (R * T));
                    const r1 = k1 * (Na / V) * Math.pow(Nb / V, 2);
                    const r2 = k2 * (Na / V) * (Nb / V);

                    const F_A0 = isFeedAtoB && time < t_f ? controls.feedRate : 0;
                    const F_B0 = !isFeedAtoB && time < t_f ? controls.feedRate : 0;

                    const dNa_dt = F_A0 - (r1 + r2) * V;
                    const dNb_dt = F_B0 - (r1 + r2) * V;
                    const dNd_dt = r1 * V;
                    const dNu_dt = r2 * V;
                    const dT_dt = (controls.UA * (T_coolant - T) + (-controls.dH1 * r1 - controls.dH2 * r2) * V) / Cp_total;

                    return [dNa_dt, dNb_dt, dNd_dt, dNu_dt, dT_dt];
                };

                for (let i = 0; i <= nSteps; i++) {
                    result.t.push(t);
                    result.Na.push(y[0]);
                    result.Nb.push(y[1]);
                    result.Nd.push(y[2]);
                    result.Nu.push(y[3]);
                    result.T.push(y[4]);

                    if (i < nSteps) {
                        y = rk4(y, t, dt, derivatives);
                        t += dt;
                    }
                }

                return result;
            }

            // Run appropriate simulation
            if (controls.plotType === 'batch') {
                simulationData = simulateBatch();
                simulationData.type = 'batch';
            } else if (controls.plotType === 'semibatch') {
                simulationData = simulateSemibatch();
                simulationData.type = 'semibatch';
            } else if (controls.plotType === 'selectivity') {
                const batchData = simulateBatch();
                const semiData = simulateSemibatch();

                const selectivityData = { t: [], ratio: [] };
                for (let i = 1; i < batchData.t.length; i++) {
                    if (batchData.Nu[i] > 0 && semiData.Nu[i] > 0) {
                        const S_batch = batchData.Nd[i] / batchData.Nu[i];
                        const S_semi = semiData.Nd[i] / semiData.Nu[i];
                        selectivityData.t.push(batchData.t[i]);
                        selectivityData.ratio.push(S_semi / S_batch);
                    }
                }
                simulationData = selectivityData;
                simulationData.type = 'selectivity';
            }
        }

        // p5.js sketch
        let sketch = function(p) {
            p.setup = function() {
                const canvas = p.createCanvas(800, 500);
                canvas.parent('plot-area');
                p.noLoop();
            };

            p.draw = function() {
                if (!simulationData) return;

                p.background(240);

                // Plot margins
                const margin = { left: 70, right: 50, top: 80, bottom: 60 };
                const plotWidth = p.width - margin.left - margin.right;
                const plotHeight = p.height - margin.top - margin.bottom;

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(margin.left, margin.top + plotHeight, margin.left + plotWidth, margin.top + plotHeight); // x-axis
                p.line(margin.left, margin.top, margin.left, margin.top + plotHeight); // y-axis

                // X-axis labels and ticks
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(14);
                p.fill(0);
                p.text('time (h)', margin.left + plotWidth / 2, p.height - 20);

                for (let i = 0; i <= 4; i++) {
                    const x = margin.left + (i / 4) * plotWidth;
                    const xValue = i * 0.5;
                    p.strokeWeight(1);
                    p.stroke(0);
                    p.line(x, margin.top + plotHeight, x, margin.top + plotHeight + 5);
                    p.noStroke();
                    p.text(xValue.toFixed(1), x, margin.top + plotHeight + 8);

                    // Grid lines
                    if (i > 0) {
                        p.stroke(200);
                        p.strokeWeight(0.5);
                        p.line(x, margin.top, x, margin.top + plotHeight);
                    }
                }

                // Y-axis setup
                let yMin, yMax, yLabel, yTicks;
                if (simulationData.type === 'selectivity') {
                    yMin = Math.min(...simulationData.ratio);
                    yMax = Math.max(...simulationData.ratio);
                    const yPadding = (yMax - yMin) * 0.1;
                    yMin -= yPadding;
                    yMax += yPadding;
                    yLabel = 'selectivity ratio';
                    yTicks = 5;
                } else {
                    yMin = 0;
                    yMax = 100;
                    yLabel = 'moles (mol)';
                    yTicks = 5;
                }

                // Y-axis labels and ticks
                p.push();
                p.translate(20, margin.top + plotHeight / 2);
                p.rotate(-p.PI / 2);
                p.textAlign(p.CENTER, p.CENTER);
                p.text(yLabel, 0, 0);
                p.pop();

                p.textAlign(p.RIGHT, p.CENTER);
                for (let i = 0; i <= yTicks; i++) {
                    const y = margin.top + plotHeight - (i / yTicks) * plotHeight;
                    const yValue = yMin + (i / yTicks) * (yMax - yMin);
                    p.strokeWeight(1);
                    p.stroke(0);
                    p.line(margin.left - 5, y, margin.left, y);
                    p.noStroke();
                    p.text(yValue.toFixed(0), margin.left - 10, y);

                    // Grid lines
                    if (i > 0 && i < yTicks) {
                        p.stroke(200);
                        p.strokeWeight(0.5);
                        p.line(margin.left, y, margin.left + plotWidth, y);
                    }
                }

                // Draw data curves
                p.noFill();
                p.strokeWeight(2);

                const mapX = (t) => margin.left + (t / 2.0) * plotWidth;
                const mapY = (val) => margin.top + plotHeight - ((val - yMin) / (yMax - yMin)) * plotHeight;

                // Helper function for dashed lines
                function drawDashedLine(points, color) {
                    p.stroke(color);
                    const dashLength = 5;
                    const gapLength = 3;
                    let distance = 0;

                    for (let i = 1; i < points.length; i++) {
                        const x1 = points[i-1].x;
                        const y1 = points[i-1].y;
                        const x2 = points[i].x;
                        const y2 = points[i].y;
                        const segmentLength = p.dist(x1, y1, x2, y2);

                        let currentLength = 0;
                        while (currentLength < segmentLength) {
                            const startRatio = currentLength / segmentLength;
                            const endRatio = Math.min((currentLength + dashLength) / segmentLength, 1);

                            if (distance % (dashLength + gapLength) < dashLength) {
                                p.line(
                                    p.lerp(x1, x2, startRatio),
                                    p.lerp(y1, y2, startRatio),
                                    p.lerp(x1, x2, endRatio),
                                    p.lerp(y1, y2, endRatio)
                                );
                            }

                            currentLength += dashLength;
                            distance += dashLength;
                            if (currentLength < segmentLength) {
                                currentLength += gapLength;
                                distance += gapLength;
                            }
                        }
                    }
                }

                if (simulationData.type === 'batch' || simulationData.type === 'semibatch') {
                    // Na - green dashed
                    const naPoints = simulationData.t.map((t, i) => ({ x: mapX(t), y: mapY(simulationData.Na[i]) }));
                    drawDashedLine(naPoints, p.color(44, 160, 44));

                    // Nb - black dashed
                    const nbPoints = simulationData.t.map((t, i) => ({ x: mapX(t), y: mapY(simulationData.Nb[i]) }));
                    drawDashedLine(nbPoints, p.color(0, 0, 0));

                    // Nd - blue solid
                    p.stroke(31, 119, 180);
                    p.beginShape();
                    for (let i = 0; i < simulationData.t.length; i++) {
                        p.vertex(mapX(simulationData.t[i]), mapY(simulationData.Nd[i]));
                    }
                    p.endShape();

                    // Nu - red solid
                    p.stroke(214, 39, 40);
                    p.beginShape();
                    for (let i = 0; i < simulationData.t.length; i++) {
                        p.vertex(mapX(simulationData.t[i]), mapY(simulationData.Nu[i]));
                    }
                    p.endShape();

                    // Legend
                    const legendX = margin.left + plotWidth / 2 - 100;
                    const legendY = 30;
                    const legendSpacing = 50;

                    // Na
                    drawDashedLine([{x: legendX, y: legendY}, {x: legendX + 20, y: legendY}], p.color(44, 160, 44));
                    p.noStroke();
                    p.fill(0);
                    p.textAlign(p.LEFT, p.CENTER);
                    p.text('N', legendX + 25, legendY);
                    p.textSize(10);
                    p.text('a', legendX + 35, legendY + 3);
                    p.textSize(14);

                    // Nb
                    drawDashedLine([{x: legendX + legendSpacing, y: legendY}, {x: legendX + legendSpacing + 20, y: legendY}], p.color(0, 0, 0));
                    p.noStroke();
                    p.text('N', legendX + legendSpacing + 25, legendY);
                    p.textSize(10);
                    p.text('b', legendX + legendSpacing + 35, legendY + 3);
                    p.textSize(14);

                    // Nd
                    p.stroke(31, 119, 180);
                    p.strokeWeight(2);
                    p.line(legendX + 2*legendSpacing, legendY, legendX + 2*legendSpacing + 20, legendY);
                    p.noStroke();
                    p.text('N', legendX + 2*legendSpacing + 25, legendY);
                    p.textSize(10);
                    p.text('d', legendX + 2*legendSpacing + 35, legendY + 3);
                    p.textSize(14);

                    // Nu
                    p.stroke(214, 39, 40);
                    p.strokeWeight(2);
                    p.line(legendX + 3*legendSpacing, legendY, legendX + 3*legendSpacing + 20, legendY);
                    p.noStroke();
                    p.text('N', legendX + 3*legendSpacing + 25, legendY);
                    p.textSize(10);
                    p.text('u', legendX + 3*legendSpacing + 35, legendY + 3);
                    p.textSize(14);

                } else if (simulationData.type === 'selectivity') {
                    // S_ratio - purple solid
                    p.stroke(128, 0, 128);
                    p.beginShape();
                    for (let i = 0; i < simulationData.t.length; i++) {
                        p.vertex(mapX(simulationData.t[i]), mapY(simulationData.ratio[i]));
                    }
                    p.endShape();

                    // Legend
                    const legendX = margin.left + plotWidth / 2 - 100;
                    const legendY = 30;
                    p.stroke(128, 0, 128);
                    p.strokeWeight(2);
                    p.line(legendX, legendY, legendX + 20, legendY);
                    p.noStroke();
                    p.fill(0);
                    p.textAlign(p.LEFT, p.CENTER);
                    p.text('semibatch S    / batch S', legendX + 25, legendY);
                    p.textSize(10);
                    p.text('d/u', legendX + 97, legendY + 3);
                    p.text('d/u', legendX + 160, legendY + 3);
                    p.textSize(14);
                }
            };
        };

        // Initialize
        document.addEventListener('DOMContentLoaded', () => {
            setupControls();
            runSimulation();
            new p5(sketch);

            // Redraw when simulation updates
            setInterval(() => {
                if (window.myp5) {
                    window.myp5.redraw();
                }
            }, 100);
        });

        // Store p5 instance globally for redraw
        window.myp5 = new p5(sketch);
    </script>
</body>
</html>
