                <button id="btn-add-200">200</button>
                <button id="btn-add-500">500</button>
            </div>

            <h4>exploration bias</h4>
            <input type="range" id="slider-bias" min="0" max="1" step="0.05" value="0">
            <span id="bias-value-label">0</span>

            <h4>goal radius</h4>
            <input type="range" id="slider-radius" min="1" max="10" step="0.1" value="1">
            <span id="radius-value-label">1</span>
        </div>

        <div id="canvas-container">
            <p id="status-text">1 node, goal not yet reached</p>
            <button id="btn-reset">+</button>
        </div>
    </div>

    <script>
        let sketch = function(p) {
            // State variables
            let treeType = 'RT';
            let obstacleType = 'narrow-passage';
            let explorationBias = 0;
            let goalRadius = 1;
            let nodes = [];
            let edges = [];
            let start = {x: 50, y: 250};
            let goal = {x: 450, y: 450};
            let goalReached = false;
            let path = [];
            let pathLength = 0;
            let draggingGoal = false;
            let obstacles = [];

            p.setup = function() {
                let canvas = p.createCanvas(500, 500);
                canvas.parent('canvas-container');
                resetSimulation();
            };

            p.draw = function() {
                p.background(255);

                // Draw obstacles
                p.stroke('blue');
                p.strokeWeight(8);
                p.noFill();
                for (let obs of obstacles) {
                    p.rect(obs.x, obs.y, obs.w, obs.h);
                }

                // Draw edges for RRT*
                if (treeType === 'RRT*') {
                    p.stroke(0);
                    p.strokeWeight(0.5);
                    for (let edge of edges) {
                        p.line(edge.from.x, edge.from.y, edge.to.x, edge.to.y);
                    }
                }

                // Draw nodes
                p.fill('red');
                p.noStroke();
                for (let node of nodes) {
                    p.circle(node.x, node.y, 5);
                }

                // Draw start node
                p.fill(nodes.length > 0 ? 'green' : 'red');
                p.circle(start.x, start.y, 8);

                // Draw goal
                p.fill(255, 255, 0, 100);
                p.noStroke();
                p.circle(goal.x, goal.y, goalRadius * 20);

                // Draw goal crosshair
                p.stroke(0);
                p.strokeWeight(1);
                p.line(goal.x - 10, goal.y, goal.x + 10, goal.y);
                p.line(goal.x, goal.y - 10, goal.x, goal.y + 10);

                // Draw path if found
                if (goalReached && path.length > 0) {
                    p.stroke('green');
                    p.strokeWeight(3);
                    for (let i = 0; i < path.length - 1; i++) {
                        p.line(path[i].x, path[i].y, path[i+1].x, path[i+1].y);
                    }
                }
            };

            p.mousePressed = function() {
                let d = p.dist(p.mouseX, p.mouseY, goal.x, goal.y);
                if (d < goalRadius * 10) {
                    draggingGoal = true;
                }
            };

            p.mouseDragged = function() {
                if (draggingGoal) {
                    goal.x = p.constrain(p.mouseX, 0, 500);
                    goal.y = p.constrain(p.mouseY, 0, 500);
                    resetSimulation();
                }
            };

            p.mouseReleased = function() {
                draggingGoal = false;
            };

            function resetSimulation() {
                nodes = [{x: start.x, y: start.y, parent: null, cost: 0}];
                edges = [];
                goalReached = false;
                path = [];
                pathLength = 0;
                updateObstacles();
                updateStatus();
            }

            function updateObstacles() {
                obstacles = [];
                if (obstacleType === 'narrow-passage') {
                    // Border
                    obstacles.push({x: 4, y: 4, w: 492, h: 0});
                    obstacles.push({x: 4, y: 4, w: 0, h: 492});
                    obstacles.push({x: 496, y: 4, w: 0, h: 492});
                    obstacles.push({x: 4, y: 496, w: 492, h: 0});
                    // Vertical walls with openings
                    obstacles.push({x: 150, y: 4, w: 0, h: 180});
                    obstacles.push({x: 150, y: 270, w: 0, h: 226});
                    obstacles.push({x: 350, y: 4, w: 0, h: 150});
                    obstacles.push({x: 350, y: 240, w: 0, h: 256});
                } else if (obstacleType === 'random-rects') {
                    // Generate random rectangles
                    for (let i = 0; i < 5; i++) {
                        obstacles.push({
                            x: p.random(50, 400),
                            y: p.random(50, 400),
                            w: p.random(50, 100),
                            h: p.random(50, 100)
                        });
                    }
                }
            }

            function updateStatus() {
                let status = nodes.length + ' node' + (nodes.length !== 1 ? 's' : '');
                if (goalReached) {
                    status += ', path length ' + pathLength.toFixed(2);
                } else {
                    status += ', goal not yet reached';
                }
                document.getElementById('status-text').textContent = status;
            }

            function distance(a, b) {
                return p.sqrt((a.x - b.x) ** 2 + (a.y - b.y) ** 2);
            }

            function isCollisionFree(from, to) {
                // Simple collision check - sample points along the line
                let steps = 20;
                for (let i = 0; i <= steps; i++) {
                    let t = i / steps;
                    let x = from.x + t * (to.x - from.x);
                    let y = from.y + t * (to.y - from.y);

                    for (let obs of obstacles) {
                        if (obs.w === 0) { // Vertical line
                            if (Math.abs(x - obs.x) < 4 && y >= obs.y && y <= obs.y + obs.h) {
                                return false;
                            }
                        } else if (obs.h === 0) { // Horizontal line
                            if (Math.abs(y - obs.y) < 4 && x >= obs.x && x <= obs.x + obs.w) {
                                return false;
                            }
                        } else { // Rectangle
                            if (x >= obs.x && x <= obs.x + obs.w && 
                                y >= obs.y && y <= obs.y + obs.h) {
                                return false;
                            }
                        }
                    }
                }
                return true;
            }

            function findNearestNode(point) {
                let nearest = nodes[0];
                let minDist = distance(point, nearest);

                for (let node of nodes) {
                    let d = distance(point, node);
                    if (d < minDist) {
                        minDist = d;
                        nearest = node;
                    }
                }
                return nearest;
            }

            function findNodesWithinRadius(point, radius) {
                return nodes.filter(node => distance(point, node) <= radius);
            }

            function addNode(count) {
                for (let i = 0; i < count; i++) {
                    let sample;
                    if (p.random() < explorationBias) {
                        sample = {x: goal.x, y: goal.y};
                    } else {
                        sample = {x: p.random(0, 500), y: p.random(0, 500)};
                    }

                    if (treeType === 'RT') {
                        // Random Tree - connect to random existing node
                        let parent = p.random(nodes);
                        if (isCollisionFree(parent, sample)) {
                            let newNode = {
                                x: sample.x, 
                                y: sample.y, 
                                parent: parent,
                                cost: parent.cost + distance(parent, sample)
                            };
                            nodes.push(newNode);
                            checkGoalReached(newNode);
                        }
                    } else if (treeType === 'RRT' || treeType === 'RRT*') {
                        // RRT - connect to nearest node
                        let nearest = findNearestNode(sample);
                        let stepSize = 20;
                        let d = distance(nearest, sample);

                        let newPoint;
                        if (d <= stepSize) {
                            newPoint = sample;
                        } else {
                            let t = stepSize / d;
                            newPoint = {
                                x: nearest.x + t * (sample.x - nearest.x),
                                y: nearest.y + t * (sample.y - nearest.y)
                            };
                        }

                        if (isCollisionFree(nearest, newPoint)) {
                            let newNode = {
                                x: newPoint.x,
                                y: newPoint.y,
                                parent: nearest,
                                cost: nearest.cost + distance(nearest, newPoint)
                            };

                            if (treeType === 'RRT*') {
                                // RRT* - rewire tree
                                let radius = 50;
                                let nearNodes = findNodesWithinRadius(newPoint, radius);

                                // Find best parent
                                for (let node of nearNodes) {
                                    let newCost = node.cost + distance(node, newPoint);
                                    if (newCost < newNode.cost && isCollisionFree(node, newPoint)) {
                                        newNode.parent = node;
                                        newNode.cost = newCost;
                                    }
                                }

                                nodes.push(newNode);
                                edges.push({from: newNode.parent, to: newNode});

                                // Rewire nearby nodes
                                for (let node of nearNodes) {
                                    if (node === newNode.parent) continue;
                                    let newCost = newNode.cost + distance(newNode, node);
                                    if (newCost < node.cost && isCollisionFree(newNode, node)) {
                                        // Remove old edge
                                        edges = edges.filter(e => !(e.from === node.parent && e.to === node));

                                        node.parent = newNode;
                                        node.cost = newCost;
                                        edges.push({from: newNode, to: node});

                                        // Update costs of descendants
                                        updateDescendantCosts(node);
                                    }
                                }
                            } else {
                                nodes.push(newNode);
                            }

                            checkGoalReached(newNode);
                        }
                    }
                }
                updateStatus();
            }

            function updateDescendantCosts(node) {
                for (let n of nodes) {
                    if (n.parent === node) {
                        n.cost = node.cost + distance(node, n);
                        updateDescendantCosts(n);
                    }
                }
            }

            function checkGoalReached(newNode) {
                if (distance(newNode, goal) <= goalRadius * 10) {
                    if (!goalReached || (treeType === 'RRT*' && newNode.cost < pathLength)) {
                        goalReached = true;
                        // Trace path
                        path = [];
                        let current = newNode;
                        while (current) {
                            path.unshift(current);
                            current = current.parent;
                        }
                        // Calculate path length
                        pathLength = 0;
                        for (let i = 0; i < path.length - 1; i++) {
                            pathLength += distance(path[i], path[i+1]);
                        }
                    }
                }
            }

            // Expose functions to global scope
            window.p5sketch = {
                setTreeType: function(type) { treeType = type; },
                setObstacleType: function(type) { obstacleType = type; },
                setExplorationBias: function(bias) { explorationBias = bias; },
                setGoalRadius: function(radius) { goalRadius = radius; },
                resetSimulation: resetSimulation,
                addNode: addNode,
                resetToDefaults: function() {
                    start = {x: 50, y: 250};
                    goal = {x: 450, y: 450};
                    resetSimulation();
                }
            };
        };

        // Create p5 instance
        new p5(sketch);

        // Control panel event listeners
        document.getElementById('btn-rt').addEventListener('click', function() {
            document.querySelectorAll('#tree-type-buttons button').forEach(b => b.classList.remove('active'));
            this.classList.add('active');
            window.p5sketch.setTreeType('RT');
            window.p5sketch.resetSimulation();
            document.getElementById('slider-bias').disabled = true;
        });

        document.getElementById('btn-rrt').addEventListener('click', function() {
            document.querySelectorAll('#tree-type-buttons button').forEach(b => b.classList.remove('active'));
            this.classList.add('active');
            window.p5sketch.setTreeType('RRT');
            window.p5sketch.resetSimulation();
            document.getElementById('slider-bias').disabled = false;
        });

        document.getElementById('btn-rrt-star').addEventListener('click', function() {
            document.querySelectorAll('#tree-type-buttons button').forEach(b => b.classList.remove('active'));
            this.classList.add('active');
            window.p5sketch.setTreeType('RRT*');
            window.p5sketch.resetSimulation();
            document.getElementById('slider-bias').disabled = false;
        });

        document.getElementById('select-obstacle').addEventListener('change', function() {
            window.p5sketch.setObstacleType(this.value);
            window.p5sketch.resetSimulation();
        });

        document.getElementById('btn-add-1').addEventListener('click', () => window.p5sketch.addNode(1));
        document.getElementById('btn-add-10').addEventListener('click', () => window.p5sketch.addNode(10));
        document.getElementById('btn-add-100').addEventListener('click', () => window.p5sketch.addNode(100));
        document.getElementById('btn-add-200').addEventListener('click', () => window.p5sketch.addNode(200));
        document.getElementById('btn-add-500').addEventListener('click', () => window.p5sketch.addNode(500));

        document.getElementById('slider-bias').addEventListener('input', function() {
