    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.128.0/examples/js/controls/OrbitControls.js"></script>
    <script id="MathJax-script" async src="https://cdn.jsdelivr.net/npm/mathjax@3/es5/tex-mml-chtml.js"></script>

    <script>
        let scene, camera, renderer, controls;
        let ionPath;

        // Initialize Three.js scene
        function initScene() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera setup
            camera = new THREE.PerspectiveCamera(60, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(4, 4, 4);
            camera.lookAt(0, 1, 0);

            // Renderer setup
            const canvas = document.getElementById('three-canvas');
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setPixelRatio(window.devicePixelRatio);
            renderer.setSize(canvas.clientWidth, canvas.clientHeight);

            // Controls
            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.target.set(0, 1, 0);
            controls.update();

            // Lighting
            const ambientLight = new THREE.AmbientLight(0x666666);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 1.0);
            directionalLight.position.set(5, 5, 5);
            scene.add(directionalLight);

            // Axes helper
            const axesHelper = new THREE.AxesHelper(4);
            scene.add(axesHelper);

            // Add static geometry
            addPotentialSurface();
            addCentralCylinder();
        }

        // Create parametric surface for potential
        function addPotentialSurface() {
            const surfaceFunction = (u, v, target) => {
                const rho = 0.4 + u * 2.1;
                const theta = v * 2 * Math.PI;
                const x = rho * Math.cos(theta);
                const y_surf = rho * rho + 1 / (rho * rho);
                const z_surf = -rho * Math.sin(theta);
                target.set(x, y_surf, z_surf);
            };

            const geometry = new THREE.ParametricGeometry(surfaceFunction, 50, 50);
            const material = new THREE.MeshStandardMaterial({
                color: 0xE69943,
                metalness: 0.4,
                roughness: 0.5,
                side: THREE.DoubleSide
            });

            const surface = new THREE.Mesh(geometry, material);
            scene.add(surface);
        }

        // Add central cylinder
        function addCentralCylinder() {
            const geometry = new THREE.CylinderGeometry(0.4, 0.4, 2.5, 32);
            const material = new THREE.MeshStandardMaterial({
                color: 0xcccccc,
                metalness: 0.1,
                roughness: 0.9
            });

            const cylinder = new THREE.Mesh(geometry, material);
            cylinder.position.set(0, 1.25, 0);
            scene.add(cylinder);
        }

        // RK4 solver for ODE system
        function rk4Step(Y, params, ds) {
            const derivatives = (state) => {
                const rho = state[0];
                const drho_ds = state[1];
                const phi = state[2];

                const d2rho_ds2 = -(2 * rho - 2 / Math.pow(rho, 3)) - 
                                  4 * params.lambda * params.lambda * rho + 
                                  4 * params.e0 / Math.pow(rho, 3);
                const dphi_ds = 2 * (params.e0 / (rho * rho) - params.lambda);

                return [drho_ds, d2rho_ds2, dphi_ds];
            };

            const k1 = derivatives(Y).map(d => d * ds);
            const Y1 = Y.map((y, i) => y + k1[i] / 2);

            const k2 = derivatives(Y1).map(d => d * ds);
            const Y2 = Y.map((y, i) => y + k2[i] / 2);

            const k3 = derivatives(Y2).map(d => d * ds);
            const Y3 = Y.map((y, i) => y + k3[i]);

            const k4 = derivatives(Y3).map(d => d * ds);

            return Y.map((y, i) => y + (k1[i] + 2 * k2[i] + 2 * k3[i] + k4[i]) / 6);
        }

        // Calculate ion path
        function calculateIonPath(params) {
            const points = [];
            const ds = 0.05;
            const steps = Math.floor(params.t / ds);

            // Initial conditions
            let Y = [params.rho0, 0, 0]; // [rho, drho/ds, phi]

            for (let i = 0; i <= steps; i++) {
                const s = i * ds;
                const rho = Y[0];
                const phi = Y[2];

                const u_rho = rho * rho + 1 / (rho * rho);
                const sqrt_arg = params.e0 - u_rho;
                const z = Math.sqrt(Math.max(0, sqrt_arg)) * 
                         Math.sin(Math.sqrt(2) * params.nu * s + params.zeta0);

                const x = rho * Math.cos(phi);
                const y = rho * Math.sin(phi);

                // Map coordinates to match visualization orientation
                points.push(new THREE.Vector3(x, z, -y));

                // Update state for next step
                if (i < steps) {
                    Y = rk4Step(Y, params, ds);
                }
            }

            return points;
        }

        // Update visualization
        function updateVisualization() {
            // Read parameters
            const params = {
                t: parseFloat(document.getElementById('input-t').value),
                rho0: parseFloat(document.getElementById('input-rho0').value),
                zeta0: parseFloat(document.getElementById('input-zeta0').value),
                e0: parseFloat(document.getElementById('input-e0').value),
                nu: parseFloat(document.getElementById('input-v').value),
                lambda: parseFloat(document.getElementById('input-lambda').value)
            };

            // Remove existing path
            if (ionPath) {
                scene.remove(ionPath);
                ionPath.geometry.dispose();
                ionPath.material.dispose();
            }

            // Calculate new path
            const points = calculateIonPath(params);

            // Create curve and tube geometry
            const curve = new THREE.CatmullRomCurve3(points);
            const geometry = new THREE.TubeGeometry(curve, 512, 0.04, 8, false);
            const material = new THREE.MeshBasicMaterial({ color: 0x61AEEF });

            ionPath = new THREE.Mesh(geometry, material);
            ionPath.name = 'ionPath';
            scene.add(ionPath);
        }

        // Link sliders and number inputs
        function linkControls(sliderId, inputId) {
            const slider = document.getElementById(sliderId);
            const input = document.getElementById(inputId);

            slider.addEventListener('input', () => {
                input.value = slider.value;
                updateVisualization();
            });

            input.addEventListener('input', () => {
                slider.value = input.value;
                updateVisualization();
            });
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Handle window resize
        function handleResize() {
            const container = document.getElementById('visualization-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
        }

        // Initialize everything
        window.addEventListener('load', () => {
            initScene();

            // Link all controls
            linkControls('slider-t', 'input-t');
            linkControls('slider-rho0', 'input-rho0');
            linkControls('slider-zeta0', 'input-zeta0');
            linkControls('slider-e0', 'input-e0');
            linkControls('slider-v', 'input-v');
            linkControls('slider-lambda', 'input-lambda');

            // Initial visualization
            updateVisualization();

            // Start animation
            animate();

            // Handle resize
            window.addEventListener('resize', handleResize);
        });
    </script>
</body>
</html>
