        }
    </style>
</head>
<body>
    <div id="app-container">
        <div id="control-panel">
            <h4>time</h4>
            <input type="range" id="slider-time" min="0" max="672" step="1" value="0">
            <div>
                <button id="btn-play-pause">▶</button>
                <button id="btn-step-back">«</button>
                <button id="btn-reset">⥀</button>
                <button id="btn-step-forward">»</button>
            </div>
            <p id="display-time">0 d 0 h</p>
            <hr>
            <h4>Moon's rotational period related to the current value</h4>
            <div id="rotation-selector">
                <button id="btn-rot-half">half</button>
                <button id="btn-rot-normal" class="active">normal</button>
                <button id="btn-rot-double">double</button>
                <button id="btn-rot-none">no rotation</button>
            </div>
            <hr>
            <div>
                <input type="checkbox" id="check-day-night">
                <label for="check-day-night">day/night</label>
            </div>
            <div>
                <input type="checkbox" id="check-near-side" checked>
                <label for="check-near-side">near side of the Moon</label>
            </div>
            <div>
                <input type="checkbox" id="check-earth-moon-line" checked>
                <label for="check-earth-moon-line">Earth-Moon line</label>
            </div>
            <div>
                <input type="checkbox" id="check-moon-only">
                <label for="check-moon-only">the Moon only</label>
            </div>
        </div>
        <div id="visualization-area">
            <canvas id="main-canvas"></canvas>
        </div>
    </div>

    <script>
        let time = 0;
        let isPlaying = false;
        let rotationMode = 'normal';
        let animationSpeed = 2; // hours per frame

        // Constants
        const EARTH_RADIUS = 80;
        const MOON_RADIUS = 25;
        const ORBITAL_RADIUS = 250;
        const MOON_ORBITAL_PERIOD = 672; // hours (28 days)
        const EARTH_ROTATIONAL_PERIOD = 24; // hours

        // UI elements
        const sliderTime = document.getElementById('slider-time');
        const btnPlayPause = document.getElementById('btn-play-pause');
        const btnStepBack = document.getElementById('btn-step-back');
        const btnReset = document.getElementById('btn-reset');
        const btnStepForward = document.getElementById('btn-step-forward');
        const displayTime = document.getElementById('display-time');

        const btnRotHalf = document.getElementById('btn-rot-half');
        const btnRotNormal = document.getElementById('btn-rot-normal');
        const btnRotDouble = document.getElementById('btn-rot-double');
        const btnRotNone = document.getElementById('btn-rot-none');

        const checkDayNight = document.getElementById('check-day-night');
        const checkNearSide = document.getElementById('check-near-side');
        const checkEarthMoonLine = document.getElementById('check-earth-moon-line');
        const checkMoonOnly = document.getElementById('check-moon-only');

        // Event listeners
        sliderTime.addEventListener('input', (e) => {
            time = parseFloat(e.target.value);
            updateTimeDisplay();
        });

        btnPlayPause.addEventListener('click', () => {
            isPlaying = !isPlaying;
            btnPlayPause.textContent = isPlaying ? '❚❚' : '▶';
        });

        btnStepBack.addEventListener('click', () => {
            time = Math.max(0, time - 1);
            sliderTime.value = time;
            updateTimeDisplay();
        });

        btnStepForward.addEventListener('click', () => {
            time = Math.min(672, time + 1);
            sliderTime.value = time;
            updateTimeDisplay();
        });

        btnReset.addEventListener('click', () => {
            time = 0;
            sliderTime.value = 0;
            updateTimeDisplay();
        });

        // Rotation mode buttons
        const rotationButtons = [btnRotHalf, btnRotNormal, btnRotDouble, btnRotNone];
        rotationButtons.forEach(btn => {
            btn.addEventListener('click', (e) => {
                rotationButtons.forEach(b => b.classList.remove('active'));
                e.target.classList.add('active');

                if (e.target.id === 'btn-rot-half') rotationMode = 'half';
                else if (e.target.id === 'btn-rot-normal') rotationMode = 'normal';
                else if (e.target.id === 'btn-rot-double') rotationMode = 'double';
                else if (e.target.id === 'btn-rot-none') rotationMode = 'none';
            });
        });

        function updateTimeDisplay() {
            const days = Math.floor(time / 24);
            const hours = Math.floor(time % 24);
            displayTime.textContent = `${days} d ${hours} h`;
        }

        // p5.js sketch
        function setup() {
            const vizArea = document.getElementById('visualization-area');
            const canvas = createCanvas(vizArea.offsetWidth, vizArea.offsetHeight);
            canvas.parent('visualization-area');
            canvas.id('main-canvas');
        }

        function draw() {
            background(20);

            // Update time if playing
            if (isPlaying) {
                time += animationSpeed;
                if (time > 672) time = 0;
                sliderTime.value = time;
                updateTimeDisplay();
            }

            // Move origin to center
            translate(width / 2, height / 2);

            // Calculate angles
            const earthRotation = (time / EARTH_ROTATIONAL_PERIOD) * TWO_PI;
            const moonOrbit = (time / MOON_ORBITAL_PERIOD) * TWO_PI;
            let moonRotation;

            switch(rotationMode) {
                case 'half':
                    moonRotation = (time / (MOON_ORBITAL_PERIOD / 2)) * TWO_PI;
                    break;
                case 'normal':
                    moonRotation = moonOrbit;
                    break;
                case 'double':
                    moonRotation = (time / (MOON_ORBITAL_PERIOD * 2)) * TWO_PI;
                    break;
                case 'none':
                    moonRotation = 0;
                    break;
            }

            // Draw Earth if not moon-only mode
            if (!checkMoonOnly.checked) {
                push();

                // Earth body
                fill(59, 130, 246); // #3B82F6
                noStroke();
                circle(0, 0, EARTH_RADIUS * 2);

                // North pole
                fill(255);
                circle(0, -EARTH_RADIUS + 10, 20);

                // Land masses
                fill(34, 197, 94); // #22C55E
                push();
                rotate(earthRotation);
                beginShape();
                vertex(-30, -50);
                vertex(30, -60);
                vertex(40, -20);
                vertex(20, 20);
                vertex(-20, 30);
                vertex(-40, -10);
                endShape(CLOSE);

                beginShape();
                vertex(-20, 40);
                vertex(10, 45);
                vertex(15, 60);
                vertex(-15, 65);
                vertex(-25, 50);
                endShape(CLOSE);

                // Red stick figure on Earth
                stroke(255, 0, 0);
                strokeWeight(3);
                line(0, -EARTH_RADIUS, 0, -EARTH_RADIUS - 20);
                line(0, -EARTH_RADIUS - 10, -10, -EARTH_RADIUS - 5);
                line(0, -EARTH_RADIUS - 10, 10, -EARTH_RADIUS - 5);
                line(0, -EARTH_RADIUS - 20, -10, -EARTH_RADIUS - 30);
                line(0, -EARTH_RADIUS - 20, 10, -EARTH_RADIUS - 30);
                noStroke();
                fill(255, 0, 0);
                circle(0, -EARTH_RADIUS - 20, 8);
                pop();

                // Day/night terminator on Earth
                if (checkDayNight.checked) {
                    fill(0, 0, 0, 128);
                    arc(0, 0, EARTH_RADIUS * 2, EARTH_RADIUS * 2, HALF_PI, -HALF_PI);
                }

                pop();
            }

            // Calculate Moon position
            let mx = ORBITAL_RADIUS * cos(moonOrbit - HALF_PI);
            let my = ORBITAL_RADIUS * sin(moonOrbit - HALF_PI);

            if (checkMoonOnly.checked) {
                mx = 0;
                my = 0;
            }

            // Draw Earth-Moon line
            if (checkEarthMoonLine.checked && !checkMoonOnly.checked) {
                stroke(255);
                strokeWeight(1);
                drawingContext.setLineDash([5, 5]);
                line(0, 0, mx, my);
                drawingContext.setLineDash([]);
            }

            // Draw Moon
            push();
            translate(mx, my);

            // Moon body
            fill(128); // #808080
            noStroke();
            circle(0, 0, MOON_RADIUS * 2);

            // Near side marker and stick figure
            if (checkNearSide.checked) {
                push();
                rotate(moonRotation);

                // Green arc for near side
                fill(34, 197, 94, 180); // #22C55E with transparency
                arc(0, 0, MOON_RADIUS * 2, MOON_RADIUS * 2, -HALF_PI, HALF_PI);

                // Magenta stick figure
                stroke(217, 70, 239); // #D946EF
                strokeWeight(2);
                line(0, -MOON_RADIUS, 0, -MOON_RADIUS + 12);
                line(0, -MOON_RADIUS + 6, -6, -MOON_RADIUS + 9);
                line(0, -MOON_RADIUS + 6, 6, -MOON_RADIUS + 9);
                line(0, -MOON_RADIUS + 12, -6, -MOON_RADIUS + 18);
                line(0, -MOON_RADIUS + 12, 6, -MOON_RADIUS + 18);
                noStroke();
                fill(217, 70, 239);
                circle(0, -MOON_RADIUS + 12, 5);

                pop();
            }

            // Day/night terminator on Moon
            if (checkDayNight.checked) {
                fill(0, 0, 0, 128);
                arc(0, 0, MOON_RADIUS * 2, MOON_RADIUS * 2, HALF_PI, -HALF_PI);
            }

            pop();
        }

        function windowResized() {
            const vizArea = document.getElementById('visualization-area');
            resizeCanvas(vizArea.offsetWidth, vizArea.offsetHeight);
        }
    </script>
</body>
</html>
