    <!-- Visualization Area -->
    <div id="plot-div"></div>

    <!-- Zoom Control Panel -->
    <div id="zoom-controls">
        <div>
            <label for="slider-x-lower">x axis lower boundary</label>
            <input type="range" id="slider-x-lower" min="900" max="1100" value="902" step="1">
            <span id="span-x-lower-value">902.</span>
        </div>
        <div>
            <label for="slider-x-upper">x axis upper boundary</label>
            <input type="range" id="slider-x-upper" min="900" max="1100" value="1091" step="1">
            <span id="span-x-upper-value">1091.</span>
        </div>
    </div>

    <script>
        // Constants for spectrum calculation
        const nu0 = 1000;  // Band origin in cm^-1
        const B_upper = 0.98;
        const A_upper = 1.95;
        const B_lower = 1.0;
        const A_lower = 2.0;
        const zeta = 0.2;
        const T = 300;  // Temperature in K
        const k_B = 0.695;  // Boltzmann constant in cm^-1/K
        const J_max = 60;

        // Calculate energy and line positions
        function calculateEnergy(J, K, B, A) {
            return B * J * (J + 1) + (A - B) * K * K;
        }

        function calculateIntensity(J, K, E_lower) {
            const population = (2 * J + 1) * Math.exp(-E_lower / (k_B * T));
            return population;
        }

        function calculateSpectrum(K_values, includePositive, includeNegative, branches) {
            const lines = [];

            for (const K of K_values) {
                // P branch (ΔJ = -1)
                if (branches.includes('P') || branches.includes('all')) {
                    for (let J = K + 1; J <= J_max; J++) {
                        const E_lower = calculateEnergy(J, K, B_lower, A_lower);

                        if (includePositive && K >= 0) {
                            const E_upper_pos = calculateEnergy(J - 1, K + 1, B_upper, A_upper);
                            const nu_pos = nu0 + E_upper_pos - E_lower + zeta * (K + 1);
                            const intensity = calculateIntensity(J, K, E_lower);
                            lines.push({
                                wavenumber: nu_pos,
                                intensity: intensity,
                                type: 'positive',
                                branch: 'P',
                                K: K
                            });
                        }

                        if (includeNegative && K > 0) {
                            const E_upper_neg = calculateEnergy(J - 1, K - 1, B_upper, A_upper);
                            const nu_neg = nu0 + E_upper_neg - E_lower - zeta * (K - 1);
                            const intensity = calculateIntensity(J, K, E_lower);
                            lines.push({
                                wavenumber: nu_neg,
                                intensity: intensity,
                                type: 'negative',
                                branch: 'P',
                                K: K
                            });
                        }
                    }
                }

                // Q branch (ΔJ = 0)
                if (branches.includes('Q') || branches.includes('all')) {
                    for (let J = K; J <= J_max; J++) {
                        const E_lower = calculateEnergy(J, K, B_lower, A_lower);

                        if (includePositive && K >= 0) {
                            const E_upper_pos = calculateEnergy(J, K + 1, B_upper, A_upper);
                            const nu_pos = nu0 + E_upper_pos - E_lower + zeta * (K + 1);
                            const intensity = calculateIntensity(J, K, E_lower);
                            lines.push({
                                wavenumber: nu_pos,
                                intensity: intensity,
                                type: 'positive',
                                branch: 'Q',
                                K: K
                            });
                        }

                        if (includeNegative && K > 0) {
                            const E_upper_neg = calculateEnergy(J, K - 1, B_upper, A_upper);
                            const nu_neg = nu0 + E_upper_neg - E_lower - zeta * (K - 1);
                            const intensity = calculateIntensity(J, K, E_lower);
                            lines.push({
                                wavenumber: nu_neg,
                                intensity: intensity,
                                type: 'negative',
                                branch: 'Q',
                                K: K
                            });
                        }
                    }
                }

                // R branch (ΔJ = +1)
                if (branches.includes('R') || branches.includes('all')) {
                    for (let J = K; J <= J_max - 1; J++) {
                        const E_lower = calculateEnergy(J, K, B_lower, A_lower);

                        if (includePositive && K >= 0) {
                            const E_upper_pos = calculateEnergy(J + 1, K + 1, B_upper, A_upper);
                            const nu_pos = nu0 + E_upper_pos - E_lower + zeta * (K + 1);
                            const intensity = calculateIntensity(J, K, E_lower);
                            lines.push({
                                wavenumber: nu_pos,
                                intensity: intensity,
                                type: 'positive',
                                branch: 'R',
                                K: K
                            });
                        }

                        if (includeNegative && K > 0) {
                            const E_upper_neg = calculateEnergy(J + 1, K - 1, B_upper, A_upper);
                            const nu_neg = nu0 + E_upper_neg - E_lower - zeta * (K - 1);
                            const intensity = calculateIntensity(J, K, E_lower);
                            lines.push({
                                wavenumber: nu_neg,
                                intensity: intensity,
                                type: 'negative',
                                branch: 'R',
                                K: K
                            });
                        }
                    }
                }
            }

            // Normalize intensities
            const maxIntensity = Math.max(...lines.map(l => l.intensity));
            lines.forEach(line => line.intensity = line.intensity / maxIntensity);

            return lines;
        }

        // Plot update function
        function updatePlot() {
            const viewMode = document.querySelector('input[name="view-selector"]:checked').value;
            const selectedK = parseInt(document.querySelector('input[name="k-selector"]:checked').value);
            const subbandType = document.querySelector('input[name="subband-type"]:checked').value;
            const branchType = document.querySelector('input[name="branch-selector"]:checked').value;

            let lines = [];
            let plotTitle = '';

            if (viewMode === 'full') {
                // Full spectrum - all K values
                const branches = ['P', 'Q', 'R'];
                lines = calculateSpectrum([0, 1, 2, 3, 4, 5], true, true, branches);
                plotTitle = 'Rovibronic Spectrum';
            } else {
                // Sub-band view
                const branches = branchType === 'all' ? ['P', 'Q', 'R'] : [branchType];
                const includePositive = subbandType === 'full' || subbandType === 'positive';
                const includeNegative = subbandType === 'full' || subbandType === 'negative';

                lines = calculateSpectrum([selectedK], includePositive, includeNegative, branches);

                plotTitle = `K = ${selectedK} sub-band`;
                if (subbandType === 'positive') plotTitle += '\n"positive" sub-band';
                if (subbandType === 'negative') plotTitle += '\n"negative" sub-band';
            }

            // Create traces
            const traces = [];

            if (viewMode === 'full') {
                // For full spectrum, use single black trace
                traces.push({
                    x: lines.map(l => l.wavenumber),
                    y: lines.map(l => l.intensity),
                    type: 'bar',
                    width: 0.1,
                    marker: { color: 'black' },
                    showlegend: false
                });
            } else {
                // For sub-band view, separate by type
                const positiveLines = lines.filter(l => l.type === 'positive');
                const negativeLines = lines.filter(l => l.type === 'negative');

                if (positiveLines.length > 0) {
                    traces.push({
                        x: positiveLines.map(l => l.wavenumber),
                        y: positiveLines.map(l => l.intensity),
                        type: 'bar',
                        width: 0.1,
                        marker: { color: 'red' },
                        showlegend: false
                    });
                }

                if (negativeLines.length > 0) {
                    traces.push({
                        x: negativeLines.map(l => l.wavenumber),
                        y: negativeLines.map(l => l.intensity),
                        type: 'bar',
                        width: 0.1,
                        marker: { color: 'blue' },
                        showlegend: false
                    });
                }
            }

            const layout = {
                title: plotTitle,
                xaxis: {
                    title: 'wavenumber (cm⁻¹)',
                    range: [
                        parseFloat(document.getElementById('slider-x-lower').value),
                        parseFloat(document.getElementById('slider-x-upper').value)
                    ]
                },
                yaxis: {
                    title: 'absorbance',
                    range: [0, 1.2]
                },
                showlegend: false,
                bargap: 0
            };

            Plotly.newPlot('plot-div', traces, layout);
        }

        // Event listeners
        document.querySelectorAll('input[name="view-selector"]').forEach(radio => {
            radio.addEventListener('change', (e) => {
                const isSubband = e.target.value === 'sub';

                document.getElementById('k-selector-group').classList.toggle('disabled', !isSubband);
                document.getElementById('subband-type-group').classList.toggle('disabled', !isSubband);

                if (!isSubband) {
                    document.getElementById('branch-selector-group').style.display = 'none';
                } else {
                    updateBranchVisibility();
                }

                updatePlot();
            });
        });

        document.querySelectorAll('input[name="k-selector"]').forEach(radio => {
            radio.addEventListener('change', () => {
                const selectedK = parseInt(document.querySelector('input[name="k-selector"]:checked').value);
                document.getElementById('radio-subband-negative').disabled = selectedK === 0;
                if (selectedK === 0 && document.getElementById('radio-subband-negative').checked) {
                    document.getElementById('radio-subband-full').checked = true;
                }
                updatePlot();
            });
        });

        document.querySelectorAll('input[name="subband-type"]').forEach(radio => {
            radio.addEventListener('change', () => {
                updateBranchVisibility();
                updatePlot();
            });
        });

        document.querySelectorAll('input[name="branch-selector"]').forEach(radio => {
            radio.addEventListener('change', updatePlot);
        });

        function updateBranchVisibility() {
            const subbandType = document.querySelector('input[name="subband-type"]:checked').value;
            const showBranches = subbandType === 'positive' || subbandType === 'negative';
            document.getElementById('branch-selector-group').style.display = showBranches ? 'flex' : 'none';
        }

        // Zoom controls
        const lowerSlider = document.getElementById('slider-x-lower');
        const upperSlider = document.getElementById('slider-x-upper');
        const lowerSpan = document.getElementById('span-x-lower-value');
        const upperSpan = document.getElementById('span-x-upper-value');

        lowerSlider.addEventListener('input', (e) => {
            let value = parseFloat(e.target.value);
            if (value >= parseFloat(upperSlider.value)) {
                upperSlider.value = value + 1;
                upperSpan.textContent = upperSlider.value + '.';
            }
            lowerSpan.textContent = value + '.';
            updatePlot();
        });

        upperSlider.addEventListener('input', (e) => {
            let value = parseFloat(e.target.value);
            if (value <= parseFloat(lowerSlider.value)) {
                lowerSlider.value = value - 1;
                lowerSpan.textContent = lowerSlider.value + '.';
            }
            upperSpan.textContent = value + '.';
            updatePlot();
        });

        // Initial plot
        updatePlot();
    </script>
</body>
</html>
