    </script>
    <script type="module">
        import * as THREE from 'three';
        import { OrbitControls } from 'three/addons/controls/OrbitControls.js';

        // State variables
        let currentWaveType = 'P';
        let scene, camera, renderer, controls;
        let radiationMesh, radiationParent, boxHelper;
        let compassGroup, arrowHelper, uLabel;

        // Initialize Three.js scene
        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            // Camera setup
            camera = new THREE.PerspectiveCamera(
                75,
                window.innerWidth / window.innerHeight,
                0.1,
                1000
            );
            camera.position.set(5, 5, 5);
            camera.lookAt(0, 0, 0);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ antialias: true });
            renderer.setSize(window.innerWidth, window.innerHeight - document.getElementById('control-panel').offsetHeight);
            document.getElementById('canvas-container').appendChild(renderer.domElement);

            // Controls setup
            controls = new OrbitControls(camera, renderer.domElement);
            controls.enableDamping = true;
            controls.dampingFactor = 0.05;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.8);
            directionalLight1.position.set(5, 5, 5);
            scene.add(directionalLight1);

            const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight2.position.set(-5, -5, -5);
            scene.add(directionalLight2);

            // Create radiation pattern parent
            radiationParent = new THREE.Object3D();
            scene.add(radiationParent);

            // Create initial radiation pattern
            updateRadiationPattern();

            // Create compass
            createCompass();

            // Create "U" arrow
            const arrowDir = new THREE.Vector3(0, 0, 1);
            const arrowOrigin = new THREE.Vector3(0, 0, 0);
            arrowHelper = new THREE.ArrowHelper(arrowDir, arrowOrigin, 2, 0x000000, 0.7, 0.3);
            scene.add(arrowHelper);

            // Create "U" label
            const uCanvas = document.createElement('canvas');
            uCanvas.width = 64;
            uCanvas.height = 64;
            const uCtx = uCanvas.getContext('2d');
            uCtx.fillStyle = 'black';
            uCtx.font = 'bold 48px Arial';
            uCtx.textAlign = 'center';
            uCtx.textBaseline = 'middle';
            uCtx.fillText('U', 32, 32);

            const uTexture = new THREE.CanvasTexture(uCanvas);
            const uMaterial = new THREE.SpriteMaterial({ map: uTexture });
            uLabel = new THREE.Sprite(uMaterial);
            uLabel.scale.set(0.5, 0.5, 1);
            uLabel.position.set(0, 0, 2.3);
            scene.add(uLabel);

            // Start animation loop
            animate();
        }

        // Create compass overlay
        function createCompass() {
            compassGroup = new THREE.Group();

            // Create circle
            const circleGeometry = new THREE.CircleGeometry(3, 64);
            const circleEdges = new THREE.EdgesGeometry(circleGeometry);
            const circleMaterial = new THREE.LineDashedMaterial({
                color: 0x666666,
                dashSize: 0.1,
                gapSize: 0.1
            });
            const circle = new THREE.LineSegments(circleEdges, circleMaterial);
            circle.computeLineDistances();
            circle.rotation.x = -Math.PI / 2;
            compassGroup.add(circle);

            // Create cardinal direction labels
            const directions = [
                { text: 'N', angle: 0 },
                { text: 'E', angle: 90 },
                { text: 'S', angle: 180 },
                { text: 'W', angle: 270 }
            ];

            directions.forEach(dir => {
                const canvas = document.createElement('canvas');
                canvas.width = 64;
                canvas.height = 64;
                const ctx = canvas.getContext('2d');
                ctx.fillStyle = 'black';
                ctx.font = 'bold 40px Arial';
                ctx.textAlign = 'center';
                ctx.textBaseline = 'middle';
                ctx.fillText(dir.text, 32, 32);

                const texture = new THREE.CanvasTexture(canvas);
                const material = new THREE.SpriteMaterial({ map: texture });
                const sprite = new THREE.Sprite(material);

                const radian = (dir.angle * Math.PI) / 180;
                sprite.position.x = Math.sin(radian) * 3.5;
                sprite.position.z = Math.cos(radian) * 3.5;
                sprite.position.y = 0;
                sprite.scale.set(0.5, 0.5, 1);

                compassGroup.add(sprite);
            });

            // Create degree markings
            const degreeMarkings = [30, 60, 120, 150, 210, 240, 300, 330];
            degreeMarkings.forEach(deg => {
                const canvas = document.createElement('canvas');
                canvas.width = 64;
                canvas.height = 64;
                const ctx = canvas.getContext('2d');
                ctx.fillStyle = '#666666';
                ctx.font = '24px Arial';
                ctx.textAlign = 'center';
                ctx.textBaseline = 'middle';
                ctx.fillText(deg + '°', 32, 32);

                const texture = new THREE.CanvasTexture(canvas);
                const material = new THREE.SpriteMaterial({ map: texture });
                const sprite = new THREE.Sprite(material);

                const radian = (deg * Math.PI) / 180;
                sprite.position.x = Math.sin(radian) * 3.2;
                sprite.position.z = Math.cos(radian) * 3.2;
                sprite.position.y = 0;
                sprite.scale.set(0.4, 0.4, 1);

                compassGroup.add(sprite);
            });

            scene.add(compassGroup);
        }

        // Create radiation pattern mesh
        function createRadiationPattern(waveType) {
            const segmentsU = 64;
            const segmentsV = 64;

            function parametricFunction(u, v, target) {
                const phi = u * Math.PI * 2;
                const theta = v * Math.PI;

                let r;
                if (waveType === 'P') {
                    r = Math.abs(Math.sin(2 * theta) * Math.sin(phi));
                } else if (waveType === 'SV') {
                    r = Math.abs(Math.cos(2 * theta) * Math.sin(phi));
                } else if (waveType === 'SH') {
                    r = Math.abs(Math.cos(theta) * Math.cos(phi));
                }

                const x = r * Math.sin(theta) * Math.cos(phi);
                const y = r * Math.sin(theta) * Math.sin(phi);
                const z = r * Math.cos(theta);

                target.set(x, y, z);
            }

            const geometry = new THREE.ParametricGeometry(parametricFunction, segmentsU, segmentsV);
            const material = new THREE.MeshStandardMaterial({
                color: 0xD2B48C,
                side: THREE.DoubleSide
            });

            return new THREE.Mesh(geometry, material);
        }

        // Update radiation pattern
        function updateRadiationPattern() {
            // Remove old mesh
            if (radiationMesh) {
                radiationParent.remove(radiationMesh);
                radiationMesh.geometry.dispose();
                radiationMesh.material.dispose();
            }

            // Create new mesh
            radiationMesh = createRadiationPattern(currentWaveType);
            radiationParent.add(radiationMesh);

            // Update box helper
            if (boxHelper) {
                scene.remove(boxHelper);
            }
            boxHelper = new THREE.BoxHelper(radiationParent, 0xff0000);
            boxHelper.visible = document.getElementById('checkbox-box').checked;
            scene.add(boxHelper);

            // Apply rotations
            updateRotations();
        }

        // Update rotations based on slider values
        function updateRotations() {
            const strike = parseFloat(document.getElementById('slider-strike').value) * Math.PI / 180;
            const dip = parseFloat(document.getElementById('slider-dip').value) * Math.PI / 180;
            const rake = parseFloat(document.getElementById('slider-rake').value) * Math.PI / 180;

            radiationParent.rotation.set(0, 0, 0);
            radiationParent.rotateZ(strike);
            radiationParent.rotateX(dip);
            radiationParent.rotateY(rake);

            if (boxHelper) {
                boxHelper.update();
            }
        }

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Handle window resize
        function handleResize() {
            const width = window.innerWidth;
            const height = window.innerHeight - document.getElementById('control-panel').offsetHeight;

            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        }

        // Set up event listeners
        function setupEventListeners() {
            // Slider events
            document.getElementById('slider-strike').addEventListener('input', function(e) {
                document.getElementById('strike-value').textContent = e.target.value;
                updateRotations();
            });

            document.getElementById('slider-dip').addEventListener('input', function(e) {
                document.getElementById('dip-value').textContent = e.target.value;
                updateRotations();
            });

            document.getElementById('slider-rake').addEventListener('input', function(e) {
                document.getElementById('rake-value').textContent = e.target.value;
                updateRotations();
            });

            // Wave type button events
            document.getElementById('btn-p').addEventListener('click', function() {
                document.querySelector('.button-container button.active').classList.remove('active');
                this.classList.add('active');
                currentWaveType = 'P';
                updateRadiationPattern();
            });

            document.getElementById('btn-sv').addEventListener('click', function() {
                document.querySelector('.button-container button.active').classList.remove('active');
                this.classList.add('active');
                currentWaveType = 'SV';
                updateRadiationPattern();
            });

            document.getElementById('btn-sh').addEventListener('click', function() {
                document.querySelector('.button-container button.active').classList.remove('active');
                this.classList.add('active');
                currentWaveType = 'SH';
                updateRadiationPattern();
            });

            // Checkbox event
            document.getElementById('checkbox-box').addEventListener('change', function(e) {
                if (boxHelper) {
                    boxHelper.visible = e.target.checked;
                }
            });

            // Window resize event
            window.addEventListener('resize', handleResize);
        }

        // Initialize everything when DOM is loaded
        window.addEventListener('DOMContentLoaded', function() {
            // Render math notation
            MathJax.typeset();

            // Initialize Three.js scene
            init();

            // Set up event listeners
            setupEventListeners();
        });
    </script>
</body>
</html>
