    <script>
        // Joystick interaction variables
        let isDragging = false;
        let currentHandle = null;
        let dragOffset = { x: 0, y: 0 };

        // Share positions (normalized 0-1)
        let share1Pos = { x: 0.75, y: 0.25 };
        let share2Pos = { x: 0.25, y: 0.75 };

        // p5.js variables
        let share1_graphic, share2_graphic;
        let secretSize = 64;
        let shareSize = secretSize * 2;
        let canvasSize = 400;

        // Initialize joystick positions
        function initializeJoysticks() {
            const handle1 = document.getElementById('joystick-handle1');
            const handle2 = document.getElementById('joystick-handle2');

            handle1.style.left = '75%';
            handle1.style.top = '25%';

            handle2.style.left = '25%';
            handle2.style.top = '75%';
        }

        // Joystick event handlers
        function setupJoystickEvents() {
            const handle1 = document.getElementById('joystick-handle1');
            const handle2 = document.getElementById('joystick-handle2');
            const container1 = document.getElementById('joystick-share1');
            const container2 = document.getElementById('joystick-share2');

            [handle1, handle2].forEach((handle, index) => {
                handle.addEventListener('mousedown', (e) => {
                    isDragging = true;
                    currentHandle = handle;
                    const rect = handle.getBoundingClientRect();
                    dragOffset.x = e.clientX - rect.left - rect.width / 2;
                    dragOffset.y = e.clientY - rect.top - rect.height / 2;
                    e.preventDefault();
                });
            });

            document.addEventListener('mousemove', (e) => {
                if (!isDragging || !currentHandle) return;

                const container = currentHandle.parentElement;
                const rect = container.getBoundingClientRect();

                let x = e.clientX - rect.left - dragOffset.x;
                let y = e.clientY - rect.top - dragOffset.y;

                // Clamp to container bounds
                const handleRadius = 15;
                x = Math.max(handleRadius, Math.min(rect.width - handleRadius, x));
                y = Math.max(handleRadius, Math.min(rect.height - handleRadius, y));

                // Update handle position
                currentHandle.style.left = (x / rect.width * 100) + '%';
                currentHandle.style.top = (y / rect.height * 100) + '%';

                // Update share position
                const normalizedX = x / rect.width;
                const normalizedY = y / rect.height;

                if (currentHandle.id === 'joystick-handle1') {
                    share1Pos.x = normalizedX;
                    share1Pos.y = normalizedY;
                } else {
                    share2Pos.x = normalizedX;
                    share2Pos.y = normalizedY;
                }
            });

            document.addEventListener('mouseup', () => {
                isDragging = false;
                currentHandle = null;
            });
        }

        // Reset button handler
        document.getElementById('btn-reset').addEventListener('click', () => {
            share1Pos = { x: 0.75, y: 0.25 };
            share2Pos = { x: 0.25, y: 0.75 };
            initializeJoysticks();
        });

        // p5.js sketch
        function setup() {
            let canvas = createCanvas(canvasSize, canvasSize);
            canvas.parent('visualization-area');

            // Create the secret image (smiley face)
            let secret = createSecretImage();

            // Generate shares
            share1_graphic = createGraphics(shareSize, shareSize);
            share2_graphic = createGraphics(shareSize, shareSize);

            generateShares(secret, share1_graphic, share2_graphic);
        }

        function draw() {
            background(255);

            // Calculate actual positions based on normalized coordinates
            const moveRange = 150; // Maximum movement range
            const centerX = width / 2;
            const centerY = height / 2;

            const x1 = centerX + (share1Pos.x - 0.5) * moveRange - shareSize / 2;
            const y1 = centerY + (share1Pos.y - 0.5) * moveRange - shareSize / 2;

            const x2 = centerX + (share2Pos.x - 0.5) * moveRange - shareSize / 2;
            const y2 = centerY + (share2Pos.y - 0.5) * moveRange - shareSize / 2;

            // Draw shares
            image(share1_graphic, x1, y1);
            image(share2_graphic, x2, y2);
        }

        function createSecretImage() {
            // Create a simple smiley face pattern
            let secret = [];
            for (let i = 0; i < secretSize; i++) {
                secret[i] = [];
                for (let j = 0; j < secretSize; j++) {
                    secret[i][j] = 0; // Default to white
                }
            }

            // Draw smiley face
            let cx = secretSize / 2;
            let cy = secretSize / 2;
            let radius = secretSize * 0.4;

            // Face outline
            for (let i = 0; i < secretSize; i++) {
                for (let j = 0; j < secretSize; j++) {
                    let d = dist(i, j, cx, cy);
                    if (d >= radius - 2 && d <= radius + 2) {
                        secret[i][j] = 1;
                    }
                }
            }

            // Eyes
            let eyeY = cy - radius * 0.3;
            let eyeOffset = radius * 0.3;
            for (let i = -3; i <= 3; i++) {
                for (let j = -3; j <= 3; j++) {
                    if (i*i + j*j <= 9) {
                        secret[int(cx - eyeOffset + i)][int(eyeY + j)] = 1;
                        secret[int(cx + eyeOffset + i)][int(eyeY + j)] = 1;
                    }
                }
            }

            // Smile
            for (let angle = 0.2 * PI; angle <= 0.8 * PI; angle += 0.01) {
                let x = cx + cos(angle) * radius * 0.6;
                let y = cy + sin(angle) * radius * 0.6;
                for (let i = -1; i <= 1; i++) {
                    for (let j = -1; j <= 1; j++) {
                        let px = int(x + i);
                        let py = int(y + j);
                        if (px >= 0 && px < secretSize && py >= 0 && py < secretSize) {
                            secret[px][py] = 1;
                        }
                    }
                }
            }

            return secret;
        }

        function generateShares(secret, share1, share2) {
            share1.clear();
            share2.clear();

            for (let i = 0; i < secretSize; i++) {
                for (let j = 0; j < secretSize; j++) {
                    let pattern = random() > 0.5 ? 0 : 1;

                    if (secret[i][j] === 0) {
                        // White pixel - same pattern on both shares
                        drawPattern(share1, i * 2, j * 2, pattern);
                        drawPattern(share2, i * 2, j * 2, pattern);
                    } else {
                        // Black pixel - complementary patterns
                        drawPattern(share1, i * 2, j * 2, pattern);
                        drawPattern(share2, i * 2, j * 2, 1 - pattern);
                    }
                }
            }
        }

        function drawPattern(graphic, x, y, pattern) {
            graphic.fill(0);
            graphic.noStroke();

            if (pattern === 0) {
                // Pattern: [[Black, Transparent], [Transparent, Black]]
                graphic.rect(x, y, 1, 1);
                graphic.rect(x + 1, y + 1, 1, 1);
            } else {
                // Pattern: [[Transparent, Black], [Black, Transparent]]
                graphic.rect(x + 1, y, 1, 1);
                graphic.rect(x, y + 1, 1, 1);
            }
        }

        // Initialize everything
        initializeJoysticks();
        setupJoystickEvents();
    </script>
</body>
</html>
