    <script>
        let locators;
        let draggedLocator = -1;

        function setup() {
            let canvas = createCanvas(550, 550);
            canvas.parent('canvas-container');
            angleMode(RADIANS);

            // Initialize locators
            locators = [
                createVector(-50, 0),
                createVector(50, 0)
            ];

            // Add event listeners
            document.querySelectorAll('input[name="rings"]').forEach(radio => {
                radio.addEventListener('change', () => redraw());
            });

            document.querySelectorAll('input[name="cpr"]').forEach(radio => {
                radio.addEventListener('change', () => redraw());
            });

            document.getElementById('checkbox-inverse').addEventListener('change', () => redraw());

            noLoop();
            redraw();
        }

        function draw() {
            background('#C1D4E6');
            translate(width / 2, height / 2);

            const isInverse = document.getElementById('checkbox-inverse').checked;

            if (isInverse) {
                drawInverseMode();
            } else {
                drawNormalMode();
            }
        }

        function drawNormalMode() {
            const rings = parseInt(document.querySelector('input[name="rings"]:checked').value);
            const circlesPerRing = parseInt(document.querySelector('input[name="cpr"]:checked').value);

            const R_max = width / 2 * 0.9;
            const sinValue = sin(PI / circlesPerRing);
            const k = (1 - sinValue) / (1 + sinValue);
            const R_c = R_max / (1 + sinValue);
            const r = R_c * sinValue;

            // Draw guide lines
            stroke('#A0A0A0');
            strokeWeight(1);
            for (let j = 0; j < circlesPerRing; j++) {
                const angle = j * 2 * PI / circlesPerRing;
                line(0, 0, R_c * cos(angle), R_c * sin(angle));
            }

            // Draw circles
            fill('#8C929D');
            stroke(0);
            strokeWeight(1);

            for (let i = 0; i < rings; i++) {
                const R_c_i = R_c * pow(k, i);
                const r_i = r * pow(k, i);

                for (let j = 0; j < circlesPerRing; j++) {
                    const angle = j * 2 * PI / circlesPerRing;
                    const x = R_c_i * cos(angle);
                    const y = R_c_i * sin(angle);
                    circle(x, y, 2 * r_i);
                }
            }
        }

        function drawInverseMode() {
            // First draw the original circles to get their positions
            const rings = parseInt(document.querySelector('input[name="rings"]:checked').value);
            const circlesPerRing = parseInt(document.querySelector('input[name="cpr"]:checked').value);

            const R_max = width / 2 * 0.9;
            const sinValue = sin(PI / circlesPerRing);
            const k = (1 - sinValue) / (1 + sinValue);
            const R_c = R_max / (1 + sinValue);
            const r = R_c * sinValue;

            // Draw locators
            fill('#FF0000');
            noStroke();
            circle(locators[0].x, locators[0].y, 8);
            circle(locators[1].x, locators[1].y, 8);

            // Draw connecting line
            stroke('#FF0000');
            strokeWeight(1);
            line(locators[0].x, locators[0].y, locators[1].x, locators[1].y);

            // Calculate inversion circle
            const C_inv = p5.Vector.add(locators[0], locators[1]).mult(0.5);
            const R_inv = p5.Vector.dist(locators[0], locators[1]) / 2;

            // Draw inversion circle
            fill(255, 0, 0, 0.3 * 255);
            stroke(0);
            circle(C_inv.x, C_inv.y, 2 * R_inv);

            // Draw inverted circles
            fill(220, 100, 220, 0.5 * 255);
            stroke(0);
            strokeWeight(1);

            for (let i = 0; i < rings; i++) {
                const R_c_i = R_c * pow(k, i);
                const r_i = r * pow(k, i);

                for (let j = 0; j < circlesPerRing; j++) {
                    const angle = j * 2 * PI / circlesPerRing;
                    const x = R_c_i * cos(angle);
                    const y = R_c_i * sin(angle);

                    const C_orig = createVector(x, y);
                    const r_orig = r_i;

                    invertCircle(C_orig, r_orig, C_inv, R_inv);
                }
            }
        }

        function invertCircle(C_orig, r_orig, C_inv, R_inv) {
            const d = p5.Vector.dist(C_orig, C_inv);

            if (abs(d - r_orig) < 0.001) {
                // Inverse is a line
                const dir = p5.Vector.sub(C_orig, C_inv).normalize();
                const perpDir = createVector(-dir.y, dir.x);
                const dist = R_inv * R_inv / (2 * d);

                const lineCenter = p5.Vector.add(C_inv, p5.Vector.mult(dir, dist));
                const p1 = p5.Vector.add(lineCenter, p5.Vector.mult(perpDir, width));
                const p2 = p5.Vector.sub(lineCenter, p5.Vector.mult(perpDir, width));

                line(p1.x, p1.y, p2.x, p2.y);
            } else {
                // Inverse is a circle
                const factor = R_inv * R_inv / (d * d - r_orig * r_orig);
                const C_new = p5.Vector.add(C_inv, p5.Vector.mult(p5.Vector.sub(C_orig, C_inv), factor));
                const r_new = abs(factor) * r_orig;

                circle(C_new.x, C_new.y, 2 * r_new);
            }
        }

        function mousePressed() {
            if (!document.getElementById('checkbox-inverse').checked) return;

            const mx = mouseX - width / 2;
            const my = mouseY - height / 2;

            for (let i = 0; i < 2; i++) {
                const d = dist(mx, my, locators[i].x, locators[i].y);
                if (d < 10) {
                    draggedLocator = i;
                    loop();
                    break;
                }
            }
        }

        function mouseDragged() {
            if (draggedLocator >= 0) {
                locators[draggedLocator].x = mouseX - width / 2;
                locators[draggedLocator].y = mouseY - height / 2;
            }
        }

        function mouseReleased() {
            draggedLocator = -1;
            noLoop();
        }
    </script>
</body>
</html>
