            canvas.parent('visualization-container');
            noLoop();

            // Add event listeners
            sliderLogCa.addEventListener('input', function() {
                logCa = parseFloat(this.value);
                redraw();
            });

            sliderPka.addEventListener('input', function() {
                pKa = parseFloat(this.value);
                redraw();
            });

            // Initial draw
            redraw();
        }

        function draw() {
            background(255);

            // Set up coordinate system
            const margin = 60;
            const plotWidth = width - 2 * margin;
            const plotHeight = height - 2 * margin;

            // Data ranges
            const pKaMin = -4;
            const pKaMax = 7;
            const pHMin = 0;
            const pHMax = 7;

            // Draw axes
            stroke(100);
            strokeWeight(2);
            // x-axis
            line(margin, height - margin, width - margin, height - margin);
            // y-axis
            line(margin, margin, margin, height - margin);

            // Draw axis labels and ticks
            textAlign(CENTER);
            fill(0);
            noStroke();

            // x-axis ticks and labels
            for (let pKaVal = pKaMin; pKaVal <= pKaMax; pKaVal++) {
                const x = map(pKaVal, pKaMin, pKaMax, margin, width - margin);
                stroke(100);
                line(x, height - margin, x, height - margin + 5);
                noStroke();
                text(pKaVal, x, height - margin + 20);
            }
            text('pKa', width / 2, height - 20);

            // y-axis ticks and labels
            textAlign(RIGHT);
            for (let pHVal = pHMin; pHVal <= pHMax; pHVal++) {
                const y = map(pHVal, pHMin, pHMax, height - margin, margin);
                stroke(100);
                line(margin - 5, y, margin, y);
                noStroke();
                text(pHVal, margin - 10, y + 5);
            }

            push();
            translate(20, height / 2);
            rotate(-PI / 2);
            textAlign(CENTER);
            text('pH', 0, 0);
            pop();

            // Calculate Ca from logCa
            const Ca = Math.pow(10, logCa);

            // Draw strong acid approximation (red horizontal line)
            const strongAcidPH = -logCa;
            if (strongAcidPH >= pHMin && strongAcidPH <= pHMax) {
                stroke(128, 0, 0); // maroon
                strokeWeight(2);
                const y = map(strongAcidPH, pHMin, pHMax, height - margin, margin);
                line(margin, y, width - margin, y);
            }

            // Draw weak acid approximation (green slanted line)
            stroke(0, 128, 0); // green
            strokeWeight(2);
            beginShape();
            noFill();
            for (let pKaVal = pKaMin; pKaVal <= pKaMax; pKaVal += 0.1) {
                const weakAcidPH = 0.5 * (pKaVal - logCa);
                if (weakAcidPH >= pHMin && weakAcidPH <= pHMax) {
                    const x = map(pKaVal, pKaMin, pKaMax, margin, width - margin);
                    const y = map(weakAcidPH, pHMin, pHMax, height - margin, margin);
                    vertex(x, y);
                }
            }
            endShape();

            // Draw exact pH curve (blue curve)
            stroke(0, 0, 255); // blue
            strokeWeight(3);
            beginShape();
            noFill();
            for (let pKaVal = pKaMin; pKaVal <= pKaMax; pKaVal += 0.05) {
                const Ka = Math.pow(10, -pKaVal);
                // Solve quadratic equation: [H+]^2 + Ka[H+] - Ka*Ca = 0
                const a = 1;
                const b = Ka;
                const c = -Ka * Ca;
                const discriminant = b * b - 4 * a * c;
                const H_plus = (-b + Math.sqrt(discriminant)) / (2 * a);
                const pH = -Math.log10(H_plus);

                if (pH >= pHMin && pH <= pHMax) {
                    const x = map(pKaVal, pKaMin, pKaMax, margin, width - margin);
                    const y = map(pH, pHMin, pHMax, height - margin, margin);
                    vertex(x, y);
                }
            }
            endShape();

            // Draw indicator point (orange)
            const Ka = Math.pow(10, -pKa);
            const a = 1;
            const b = Ka;
            const c = -Ka * Ca;
            const discriminant = b * b - 4 * a * c;
            const H_plus = (-b + Math.sqrt(discriminant)) / (2 * a);
            const pH = -Math.log10(H_plus);

            if (pH >= pHMin && pH <= pHMax && pKa >= pKaMin && pKa <= pKaMax) {
                fill(255, 165, 0); // orange
                stroke(0);
                strokeWeight(2);
                const x = map(pKa, pKaMin, pKaMax, margin, width - margin);
                const y = map(pH, pHMin, pHMax, height - margin, margin);
                ellipse(x, y, 12, 12);
            }
        }

        function windowResized() {
            const container = document.getElementById('visualization-container');
            resizeCanvas(container.offsetWidth, container.offsetHeight);
            redraw();
        }
    </script>
</body>
</html>
