                </div>
            </div>

            <!-- End Point Control -->
            <div class="control-group">
                <label>end point</label>
                <div id="joystick-end" class="joystick-area">
                    <div id="handle-end" class="joystick-handle"></div>
                </div>
            </div>

            <!-- Obstacles Dropdown -->
            <div class="control-group">
                <label for="select-obstacles">obstacles</label>
                <select id="select-obstacles">
                    <option value="1-square">1 square</option>
                    <option value="2-squares">2 squares</option>
                    <option value="l-shaped-wall">L-shaped wall</option>
                </select>
            </div>
        </div>

        <!-- Right column: Visualization Canvas -->
        <div id="canvas-container">
            <!-- p5.js canvas will be inserted here by the script -->
        </div>
    </div>

    <!-- CDN for p5.js -->
    <script src="https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.4.0/p5.js"></script>

    <script>
        // Grid configuration
        const GRID_SIZE = 50;
        const CELL_SIZE = 12;

        // State variables
        let startPoint = { x: 0.15, y: 0.85 };
        let endPoint = { x: 0.85, y: 0.15 };
        let history = [];
        let currentStep = 1;

        // Priority Queue implementation for pathfinding
        class PriorityQueue {
            constructor() {
                this.elements = [];
            }

            enqueue(element, priority) {
                this.elements.push({ element, priority });
                this.elements.sort((a, b) => a.priority - b.priority);
            }

            dequeue() {
                return this.elements.shift().element;
            }

            isEmpty() {
                return this.elements.length === 0;
            }
        }

        // Obstacle patterns
        const obstaclePatterns = {
            '1-square': [
                { x: 25, y: 25 }, { x: 26, y: 25 }, { x: 25, y: 26 }, { x: 26, y: 26 }
            ],
            '2-squares': [
                { x: 20, y: 20 }, { x: 21, y: 20 }, { x: 20, y: 21 }, { x: 21, y: 21 },
                { x: 30, y: 30 }, { x: 31, y: 30 }, { x: 30, y: 31 }, { x: 31, y: 31 }
            ],
            'l-shaped-wall': [
                { x: 25, y: 15 }, { x: 25, y: 16 }, { x: 25, y: 17 }, { x: 25, y: 18 },
                { x: 25, y: 19 }, { x: 25, y: 20 }, { x: 25, y: 21 }, { x: 25, y: 22 },
                { x: 25, y: 23 }, { x: 25, y: 24 }, { x: 25, y: 25 },
                { x: 26, y: 25 }, { x: 27, y: 25 }, { x: 28, y: 25 }, { x: 29, y: 25 },
                { x: 30, y: 25 }, { x: 31, y: 25 }, { x: 32, y: 25 }, { x: 33, y: 25 },
                { x: 34, y: 25 }, { x: 35, y: 25 }
            ]
        };

        // Joystick control setup
        function setupJoystick(joystickId, handleId, defaultX, defaultY, onChange) {
            const joystick = document.getElementById(joystickId);
            const handle = document.getElementById(handleId);
            let isDragging = false;

            // Set initial position
            handle.style.left = (defaultX * 100) + '%';
            handle.style.top = ((1 - defaultY) * 100) + '%';

            function updatePosition(e) {
                const rect = joystick.getBoundingClientRect();
                let x = (e.clientX - rect.left) / rect.width;
                let y = 1 - (e.clientY - rect.top) / rect.height;

                x = Math.max(0, Math.min(1, x));
                y = Math.max(0, Math.min(1, y));

                handle.style.left = (x * 100) + '%';
                handle.style.top = ((1 - y) * 100) + '%';

                return { x, y };
            }

            handle.addEventListener('mousedown', (e) => {
                isDragging = true;
                handle.classList.add('dragging');
                e.preventDefault();
            });

            document.addEventListener('mousemove', (e) => {
                if (isDragging) {
                    updatePosition(e);
                }
            });

            document.addEventListener('mouseup', (e) => {
                if (isDragging) {
                    isDragging = false;
                    handle.classList.remove('dragging');
                    const pos = updatePosition(e);
                    onChange(pos);
                }
            });

            joystick.addEventListener('click', (e) => {
                if (e.target === joystick) {
                    const pos = updatePosition(e);
                    onChange(pos);
                }
            });
        }

        // Setup controls
        setupJoystick('joystick-start', 'handle-start', startPoint.x, startPoint.y, (pos) => {
            startPoint = pos;
            runPathfinding();
        });

        setupJoystick('joystick-end', 'handle-end', endPoint.x, endPoint.y, (pos) => {
            endPoint = pos;
            runPathfinding();
        });

        // Algorithm selection
        document.getElementById('radio-dijkstra').addEventListener('change', runPathfinding);
        document.getElementById('radio-a-star').addEventListener('change', runPathfinding);

        // Obstacles selection
        document.getElementById('select-obstacles').addEventListener('change', runPathfinding);

        // Step slider
        document.getElementById('slider-step').addEventListener('input', (e) => {
            currentStep = parseInt(e.target.value);
            document.getElementById('label-step').textContent = `+ ${currentStep}`;
        });

        // Pathfinding algorithms
        function getNeighbors(x, y, grid) {
            const neighbors = [];
            const directions = [
                [-1, -1, Math.sqrt(2)], [0, -1, 1], [1, -1, Math.sqrt(2)],
                [-1, 0, 1], [1, 0, 1],
                [-1, 1, Math.sqrt(2)], [0, 1, 1], [1, 1, Math.sqrt(2)]
            ];

            for (const [dx, dy, cost] of directions) {
                const nx = x + dx;
                const ny = y + dy;
                if (nx >= 0 && nx < GRID_SIZE && ny >= 0 && ny < GRID_SIZE && !grid[ny][nx].obstacle) {
                    neighbors.push({ x: nx, y: ny, cost });
                }
            }
            return neighbors;
        }

        function manhattanDistance(x1, y1, x2, y2) {
            return Math.abs(x1 - x2) + Math.abs(y1 - y2);
        }

        function dijkstra(grid, start, end) {
            const frames = [];
            const distances = {};
            const previous = {};
            const pq = new PriorityQueue();
            const visited = new Set();
            const frontier = new Set();

            const startKey = `${start.x},${start.y}`;
            distances[startKey] = 0;
            pq.enqueue(start, 0);
            frontier.add(startKey);

            while (!pq.isEmpty()) {
                const current = pq.dequeue();
                const currentKey = `${current.x},${current.y}`;

                frontier.delete(currentKey);
                visited.add(currentKey);

                // Record frame
                frames.push({
                    visited: new Set(visited),
                    frontier: new Set(frontier),
                    current: current,
                    path: null
                });

                if (current.x === end.x && current.y === end.y) {
                    break;
                }

                const neighbors = getNeighbors(current.x, current.y, grid);

                for (const neighbor of neighbors) {
                    const neighborKey = `${neighbor.x},${neighbor.y}`;
                    const alt = distances[currentKey] + neighbor.cost;

                    if (distances[neighborKey] === undefined || alt < distances[neighborKey]) {
                        distances[neighborKey] = alt;
                        previous[neighborKey] = current;

                        if (!visited.has(neighborKey)) {
                            pq.enqueue(neighbor, alt);
                            frontier.add(neighborKey);
                        }
                    }
                }
            }

            // Reconstruct path
            const path = [];
            let current = end;
            const endKey = `${end.x},${end.y}`;

            if (previous[endKey]) {
                while (current) {
                    path.unshift(current);
                    const key = `${current.x},${current.y}`;
                    current = previous[key];
                }
            }

            // Add final frame with path
            if (frames.length > 0) {
                frames[frames.length - 1].path = path;
            }

            return frames;
        }

        function aStar(grid, start, end) {
            const frames = [];
            const gScore = {};
            const fScore = {};
            const previous = {};
            const pq = new PriorityQueue();
            const visited = new Set();
            const frontier = new Set();

            const startKey = `${start.x},${start.y}`;
            gScore[startKey] = 0;
            fScore[startKey] = manhattanDistance(start.x, start.y, end.x, end.y);
            pq.enqueue(start, fScore[startKey]);
            frontier.add(startKey);

            while (!pq.isEmpty()) {
                const current = pq.dequeue();
                const currentKey = `${current.x},${current.y}`;

                frontier.delete(currentKey);
                visited.add(currentKey);

                // Record frame
                frames.push({
                    visited: new Set(visited),
                    frontier: new Set(frontier),
                    current: current,
                    path: null
                });

                if (current.x === end.x && current.y === end.y) {
                    break;
                }

                const neighbors = getNeighbors(current.x, current.y, grid);

                for (const neighbor of neighbors) {
                    const neighborKey = `${neighbor.x},${neighbor.y}`;
                    const tentativeGScore = gScore[currentKey] + neighbor.cost;

                    if (gScore[neighborKey] === undefined || tentativeGScore < gScore[neighborKey]) {
                        gScore[neighborKey] = tentativeGScore;
                        fScore[neighborKey] = tentativeGScore + manhattanDistance(neighbor.x, neighbor.y, end.x, end.y);
                        previous[neighborKey] = current;

                        if (!visited.has(neighborKey)) {
                            pq.enqueue(neighbor, fScore[neighborKey]);
                            frontier.add(neighborKey);
                        }
                    }
                }
            }

            // Reconstruct path
            const path = [];
            let current = end;
            const endKey = `${end.x},${end.y}`;

            if (previous[endKey]) {
                while (current) {
                    path.unshift(current);
                    const key = `${current.x},${current.y}`;
                    current = previous[key];
                }
            }

            // Add final frame with path
            if (frames.length > 0) {
                frames[frames.length - 1].path = path;
            }

            return frames;
        }

        function runPathfinding() {
            // Create grid
            const grid = Array(GRID_SIZE).fill().map(() => 
                Array(GRID_SIZE).fill().map(() => ({ obstacle: false }))
            );

            // Add obstacles
            const obstacleType = document.getElementById('select-obstacles').value;
            const obstacles = obstaclePatterns[obstacleType];
            obstacles.forEach(obs => {
                if (obs.x >= 0 && obs.x < GRID_SIZE && obs.y >= 0 && obs.y < GRID_SIZE) {
                    grid[obs.y][obs.x].obstacle = true;
                }
            });

            // Convert normalized coordinates to grid coordinates
            const start = {
                x: Math.floor(startPoint.x * GRID_SIZE),
                y: Math.floor((1 - startPoint.y) * GRID_SIZE)
            };
            const end = {
                x: Math.floor(endPoint.x * GRID_SIZE),
                y: Math.floor((1 - endPoint.y) * GRID_SIZE)
            };

            // Run selected algorithm
            const algorithm = document.querySelector('input[name="algorithm"]:checked').value;
            if (algorithm === 'dijkstra') {
                history = dijkstra(grid, start, end);
            } else {
                history = aStar(grid, start, end);
            }

            // Update slider
            const slider = document.getElementById('slider-step');
            slider.max = history.length;
            slider.value = 1;
            currentStep = 1;
            document.getElementById('label-step').textContent = '+ 1';
        }

        // p5.js sketch
        function setup() {
            const canvas = createCanvas(GRID_SIZE * CELL_SIZE, GRID_SIZE * CELL_SIZE);
            canvas.parent('canvas-container');
            runPathfinding();
        }

        function draw() {
            background(255);

            // Draw grid
            stroke(200);
            strokeWeight(1);
            for (let i = 0; i <= GRID_SIZE; i++) {
                line(i * CELL_SIZE, 0, i * CELL_SIZE, height);
                line(0, i * CELL_SIZE, width, i * CELL_SIZE);
            }

            if (history.length === 0) return;

            const frame = history[Math.min(currentStep - 1, history.length - 1)];

            // Draw obstacles
            fill(0);
            noStroke();
            const obstacleType = document.getElementById('select-obstacles').value;
            const obstacles = obstaclePatterns[obstacleType];
            obstacles.forEach(obs => {
                rect(obs.x * CELL_SIZE, obs.y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
            });

            // Draw visited cells
            fill(0, 0, 205);
            frame.visited.forEach(key => {
                const [x, y] = key.split(',').map(Number);
                rect(x * CELL_SIZE, y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
            });

            // Draw frontier cells
            fill(189, 224, 254);
            frame.frontier.forEach(key => {
                const [x, y] = key.split(',').map(Number);
                rect(x * CELL_SIZE, y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
            });

            // Draw start and end points
            fill(52, 168, 83);
            const start = {
                x: Math.floor(startPoint.x * GRID_SIZE),
                y: Math.floor((1 - startPoint.y) * GRID_SIZE)
            };
            const end = {
                x: Math.floor(endPoint.x * GRID_SIZE),
                y: Math.floor((1 - endPoint.y) * GRID_SIZE)
            };
            rect(start.x * CELL_SIZE, start.y * CELL_SIZE, CELL_SIZE, CELL_SIZE);
            rect(end.x * CELL_SIZE, end.y * CELL_SIZE, CELL_SIZE, CELL_SIZE);

            // Draw path if at final step
            if (currentStep === history.length && frame.path && frame.path.length > 1) {
                stroke(255, 0, 0);
                strokeWeight(3);
                noFill();
                beginShape();
                frame.path.forEach(point => {
                    vertex(point.x * CELL_SIZE + CELL_SIZE / 2, point.y * CELL_SIZE + CELL_SIZE / 2);
                });
                endShape();
            }
        }
    </script>
</body>
</html>
