                <p id="display-y-pos">y position = 3.00 m</p>
                <p id="display-force">Electric force = 0.013 N</p>
            </div>
        </div>
    </div>

    <script src="https://cdn.jsdelivr.net/npm/p5@1.9.0/lib/p5.js"></script>
    <script>
        let Q = 2.0; // source charge
        let q = -0.0845; // test charge
        let testX = 2; // test charge x position
        let testY = 3; // test charge y position
        let isDragging = false;
        let scale = 40; // pixels per meter

        // Slider elements
        const sourceSlider = document.getElementById('slider-source-charge');
        const testSlider = document.getElementById('slider-test-charge');
        const sourceLabel = document.getElementById('label-source-charge');
        const testLabel = document.getElementById('label-test-charge');

        // Info panel elements
        const displayEMag = document.getElementById('display-E-mag');
        const displayEx = document.getElementById('display-Ex');
        const displayEy = document.getElementById('display-Ey');
        const displayXPos = document.getElementById('display-x-pos');
        const displayYPos = document.getElementById('display-y-pos');
        const displayForce = document.getElementById('display-force');

        // Slider event listeners
        sourceSlider.addEventListener('input', function() {
            Q = parseFloat(this.value);
            sourceLabel.textContent = Q.toFixed(1);
            updateCalculations();
        });

        testSlider.addEventListener('input', function() {
            q = parseFloat(this.value);
            testLabel.textContent = q.toFixed(4);
            updateCalculations();
        });

        function setup() {
            let canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');
            textAlign(CENTER, CENTER);
        }

        function draw() {
            background(255);

            // Set up coordinate system
            push();
            translate(width/2, height/2);
            scale(1, -1);

            // Draw grid
            stroke(220);
            strokeWeight(1);
            for (let i = -7; i <= 7; i++) {
                if (i % 2 === 0) {
                    strokeWeight(1);
                } else {
                    strokeWeight(0.5);
                }
                line(i * scale, -7 * scale, i * scale, 7 * scale);
                line(-7 * scale, i * scale, 7 * scale, i * scale);
            }

            // Draw axes
            stroke(0);
            strokeWeight(2);
            line(-7 * scale, 0, 7 * scale, 0);
            line(0, -7 * scale, 0, 7 * scale);

            // Draw axis labels
            push();
            scale(1, -1);
            fill(0);
            noStroke();
            textSize(12);
            for (let i = -6; i <= 6; i += 2) {
                if (i !== 0) {
                    text(i, i * scale, 15);
                    text(i, -15, -i * scale);
                }
            }
            pop();

            // Draw source charge at origin
            fill(0, 0, 255);
            noStroke();
            circle(0, 0, 20);

            // Draw test charge locator
            fill(0);
            circle(testX * scale, testY * scale, 15);

            // Calculate electric field and force
            let r = sqrt(testX * testX + testY * testY);
            if (r > 0) {
                let Ex = Q * testX / (r * r * r);
                let Ey = Q * testY / (r * r * r);
                let EMag = sqrt(Ex * Ex + Ey * Ey);
                let Fx = q * Ex;
                let Fy = q * Ey;
                let FMag = abs(q) * EMag;

                // Draw electric field vector (red)
                stroke(255, 0, 0);
                strokeWeight(3);
                drawArrow(createVector(testX * scale, testY * scale), 
                         createVector(Ex * 400, Ey * 400), 
                         color(255, 0, 0));

                // Draw electric force vector (blue) if q != 0
                if (abs(q) > 0.0001) {
                    stroke(0, 0, 255);
                    strokeWeight(3);
                    drawArrow(createVector(testX * scale, testY * scale), 
                             createVector(Fx * 400, Fy * 400), 
                             color(0, 0, 255));
                }
            }

            pop();
        }

        function drawArrow(base, vec, myColor) {
            push();
            stroke(myColor);
            strokeWeight(3);
            fill(myColor);
            let arrowSize = 7;
            translate(base.x, base.y);
            line(0, 0, vec.x, vec.y);
            rotate(vec.heading());
            let arrowLength = vec.mag();
            translate(arrowLength - arrowSize, 0);
            triangle(0, arrowSize / 2, 0, -arrowSize / 2, arrowSize, 0);
            pop();
        }

        function mousePressed() {
            let mx = mouseX - width/2;
            let my = -(mouseY - height/2);
            let d = dist(mx, my, testX * scale, testY * scale);
            if (d < 15) {
                isDragging = true;
            }
        }

        function mouseDragged() {
            if (isDragging) {
                testX = (mouseX - width/2) / scale;
                testY = -(mouseY - height/2) / scale;
                testX = constrain(testX, -7, 7);
                testY = constrain(testY, -7, 7);
                updateCalculations();
            }
        }

        function mouseReleased() {
            isDragging = false;
        }

        function updateCalculations() {
            let r = sqrt(testX * testX + testY * testY);
            if (r > 0) {
                let Ex = Q * testX / (r * r * r);
                let Ey = Q * testY / (r * r * r);
                let EMag = sqrt(Ex * Ex + Ey * Ey);
                let FMag = abs(q) * EMag;

                displayEMag.innerHTML = `|E| = ${EMag.toFixed(3)} N/C`;
                displayEx.innerHTML = `E<sub>x</sub> component = ${Ex.toFixed(3)} N/C`;
                displayEy.innerHTML = `E<sub>y</sub> component = ${Ey.toFixed(3)} N/C`;
                displayXPos.textContent = `x position = ${testX.toFixed(2)} m`;
                displayYPos.textContent = `y position = ${testY.toFixed(2)} m`;
                displayForce.textContent = `Electric force = ${FMag.toFixed(3)} N`;
            }
        }

        // Initial calculation
        updateCalculations();
    </script>
</body>
</html>
