<body>
    <h1>Fourier Transform of Radially Symmetric Potential Functions</h1>

    <div id="controls-container">
        <div id="function-selector">
            <input type="checkbox" id="checkbox-hertz" checked>
            <label for="checkbox-hertz">Hertz</label>
            <input type="checkbox" id="checkbox-overlap" checked>
            <label for="checkbox-overlap">overlap</label>
            <input type="checkbox" id="checkbox-gaussian" checked>
            <label for="checkbox-gaussian">Gaussian</label>
        </div>

        <div id="scale-selector">
            <label>axes</label>
            <button id="btn-linear-linear" class="active">linear-linear</button>
            <button id="btn-log-linear">log-linear</button>
            <button id="btn-log-log">log-log</button>
        </div>

        <div id="sliders-container">
            <div id="slider-r-control">
                <label for="slider-r">radius r</label>
                <input type="range" id="slider-r" min="0.1" max="5" step="0.1" value="2.5">
                <span id="span-r-value">2.5</span>
            </div>
            <div id="slider-k-control">
                <label for="slider-k">wave number k</label>
                <input type="range" id="slider-k" min="1" max="100" step="0.01" value="50">
                <span id="span-k-value">50</span>
            </div>
        </div>
    </div>

    <p id="hover-info">(Hover over the curves to see math labels.)</p>

    <div id="plots-container">
        <div id="plot-left"></div>
        <div id="plot-right"></div>
    </div>

    <script>
        // Function definitions
        function hertzF(r) {
            return r <= 1 ? Math.pow(1 - r * r, 2.5) : 0;
        }

        function overlapF(r) {
            return r <= 1 ? Math.pow(1 - r, 2) : 0;
        }

        function gaussianF(r) {
            return Math.exp(-Math.PI * r * r);
        }

        function hertzFourier(k) {
            if (k === 0) return 4 * Math.PI;
            return 15 * Math.PI * ((3 - k * k) * Math.sin(k) - 3 * k * Math.cos(k)) / Math.pow(k, 5);
        }

        function overlapFourier(k) {
            if (k === 0) return 8 * Math.PI / 3;
            return 8 * Math.PI * (2 - k * k - 2 * Math.cos(k) - 2 * k * Math.sin(k)) / Math.pow(k, 4);
        }

        function gaussianFourier(k) {
            return (1 / Math.sqrt(Math.PI)) * Math.exp(-k * k / (4 * Math.PI));
        }

        // Update plots function
        function updatePlots() {
            // Read control states
            const hertzChecked = document.getElementById('checkbox-hertz').checked;
            const overlapChecked = document.getElementById('checkbox-overlap').checked;
            const gaussianChecked = document.getElementById('checkbox-gaussian').checked;

            const linearLinearActive = document.getElementById('btn-linear-linear').classList.contains('active');
            const logLinearActive = document.getElementById('btn-log-linear').classList.contains('active');
            const logLogActive = document.getElementById('btn-log-log').classList.contains('active');

            const rMax = parseFloat(document.getElementById('slider-r').value);
            const kMax = parseFloat(document.getElementById('slider-k').value);

            // Update value displays
            document.getElementById('span-r-value').textContent = rMax.toFixed(1);
            document.getElementById('span-k-value').textContent = kMax.toFixed(2);

            // Generate data arrays
            const numPoints = 200;
            const rValues = Array.from({length: numPoints}, (_, i) => i * rMax / (numPoints - 1));
            const kValues = Array.from({length: numPoints}, (_, i) => i * kMax / (numPoints - 1));

            // Prepare traces for left plot
            const leftTraces = [];

            if (hertzChecked) {
                leftTraces.push({
                    x: rValues,
                    y: rValues.map(r => hertzF(r)),
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Hertz',
                    line: {color: '#ff0000'},
                    hovertemplate: 'Hertz<br>f(r) = (1 - r^2)^{5/2}, r ≤ 1<extra></extra>'
                });
            }

            if (overlapChecked) {
                leftTraces.push({
                    x: rValues,
                    y: rValues.map(r => overlapF(r)),
                    type: 'scatter',
                    mode: 'lines',
                    name: 'overlap',
                    line: {color: '#000000'},
                    hovertemplate: 'overlap<br>f(r) = (1 - r)^2, r ≤ 1<extra></extra>'
                });
            }

            if (gaussianChecked) {
                leftTraces.push({
                    x: rValues,
                    y: rValues.map(r => gaussianF(r)),
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Gaussian',
                    line: {color: '#0000ff'},
                    hovertemplate: 'Gaussian<br>f(r) = e^{-πr^2}<extra></extra>'
                });
            }

            // Prepare traces for right plot
            const rightTraces = [];

            if (hertzChecked) {
                rightTraces.push({
                    x: kValues,
                    y: kValues.map(k => hertzFourier(k)),
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Hertz',
                    line: {color: '#ff0000'},
                    hovertemplate: 'Hertz<br>F(k) = 15π[(3-k²)sin(k) - 3k cos(k)]/k⁵<extra></extra>'
                });
            }

            if (overlapChecked) {
                rightTraces.push({
                    x: kValues,
                    y: kValues.map(k => overlapFourier(k)),
                    type: 'scatter',
                    mode: 'lines',
                    name: 'overlap',
                    line: {color: '#000000'},
                    hovertemplate: 'overlap<br>F(k) = 8π[2 - k² - 2cos(k) - 2k sin(k)]/k⁴<extra></extra>'
                });
            }

            if (gaussianChecked) {
                rightTraces.push({
                    x: kValues,
                    y: kValues.map(k => gaussianFourier(k)),
                    type: 'scatter',
                    mode: 'lines',
                    name: 'Gaussian',
                    line: {color: '#0000ff'},
                    hovertemplate: 'Gaussian<br>F(k) = (1/√π)e^{-k²/(4π)}<extra></extra>'
                });
            }

            // Layout for left plot
            const leftLayout = {
                title: {
                    text: 'function',
                    font: {color: '#808080'}
                },
                xaxis: {
                    title: 'r',
                    range: [0, rMax]
                },
                yaxis: {
                    title: 'f(r)'
                },
                showlegend: false,
                margin: {t: 40, r: 20, b: 40, l: 60}
            };

            // Layout for right plot
            const yAxisType = linearLinearActive ? 'linear' : 'log';
            const xAxisType = logLogActive ? 'log' : 'linear';

            const rightLayout = {
                title: {
                    text: '3D Fourier transform (real part)',
                    font: {color: '#808080'}
                },
                xaxis: {
                    title: 'k',
                    range: xAxisType === 'log' ? [Math.log10(0.01), Math.log10(kMax)] : [0, kMax],
                    type: xAxisType
                },
                yaxis: {
                    title: 'F(k)',
                    type: yAxisType
                },
                showlegend: false,
                margin: {t: 40, r: 20, b: 40, l: 60}
            };

            // Update plots
            Plotly.react('plot-left', leftTraces, leftLayout);
            Plotly.react('plot-right', rightTraces, rightLayout);

            // Trigger MathJax to process any new formulas
            if (window.MathJax) {
                MathJax.typeset();
            }
        }

        // Initialize function
        function init() {
            // Set up event listeners
            document.getElementById('checkbox-hertz').addEventListener('change', updatePlots);
            document.getElementById('checkbox-overlap').addEventListener('change', updatePlots);
            document.getElementById('checkbox-gaussian').addEventListener('change', updatePlots);

            document.getElementById('btn-linear-linear').addEventListener('click', function() {
                document.querySelectorAll('#scale-selector button').forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                updatePlots();
            });

            document.getElementById('btn-log-linear').addEventListener('click', function() {
                document.querySelectorAll('#scale-selector button').forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                updatePlots();
            });

            document.getElementById('btn-log-log').addEventListener('click', function() {
                document.querySelectorAll('#scale-selector button').forEach(btn => btn.classList.remove('active'));
                this.classList.add('active');
                updatePlots();
            });

            document.getElementById('slider-r').addEventListener('input', updatePlots);
            document.getElementById('slider-k').addEventListener('input', updatePlots);

            // Initial plot
            updatePlots();
        }

        // Initialize when page loads
        window.addEventListener('load', init);
    </script>
</body>
</html>
