            });
        });

        // Scale switch handler
        document.querySelectorAll('input[name="scale"]').forEach(radio => {
            radio.addEventListener('change', function() {
                const popContainer = document.getElementById('population-container');
                if (this.value === 'population') {
                    popContainer.classList.remove('disabled');
                } else {
                    popContainer.classList.add('disabled');
                }
                if (myp5) myp5.redraw();
            });
        });

        // Type switch handler
        document.querySelectorAll('input[name="type"]').forEach(radio => {
            radio.addEventListener('change', function() {
                if (myp5) myp5.redraw();
            });
        });

        // Climate neutral buttons
        document.querySelectorAll('#climate-neutral-buttons button').forEach(button => {
            button.addEventListener('click', function() {
                document.querySelectorAll('#climate-neutral-buttons button').forEach(b => {
                    b.classList.remove('active');
                });
                this.classList.add('active');

                switch(this.id) {
                    case 'btn-neutral-now': neutral_year = 2020; break;
                    case 'btn-neutral-2030': neutral_year = 2030; break;
                    case 'btn-neutral-2040': neutral_year = 2040; break;
                    case 'btn-neutral-2050': neutral_year = 2050; break;
                    case 'btn-neutral-never': neutral_year = null; break;
                }
                if (myp5) myp5.redraw();
            });
        });

        // p5.js sketch
        const sketch = (p) => {
            let plotWidth, plotHeight;
            let marginLeft = 80;
            let marginRight = 40;
            let marginTop = 40;
            let marginBottom = 60;
            let budgetHeight = 150;

            p.setup = function() {
                const container = document.getElementById('main-plot-container');
                const canvas = p.createCanvas(container.offsetWidth, container.offsetHeight);
                canvas.parent('main-plot-container');
                p.noLoop();
                plotWidth = p.width - marginLeft - marginRight;
                plotHeight = p.height - marginBottom - marginTop - budgetHeight - 40;
            };

            p.windowResized = function() {
                const container = document.getElementById('main-plot-container');
                p.resizeCanvas(container.offsetWidth, container.offsetHeight);
                plotWidth = p.width - marginLeft - marginRight;
                plotHeight = p.height - marginBottom - marginTop - budgetHeight - 40;
                p.redraw();
            };

            p.draw = function() {
                p.background(255);
                const controls = getControlValues();

                // Calculate emissions data
                const years = [];
                const realEmissions = [];
                const compensatedEmissions = [];
                const effectiveEmissions = [];
                const annualCosts = [];
                let cumulativeEffective = [];
                let cumSum = 0;

                for (let year = 2020; year <= 2050; year++) {
                    years.push(year);

                    let realEmissionsPerson;
                    if (year < controls.startYear) {
                        realEmissionsPerson = controls.annualEmissions;
                    } else {
                        const yearsElapsed = year - controls.startYear;
                        realEmissionsPerson = controls.annualEmissions * Math.pow(0.5, yearsElapsed / controls.reductionTime);
                    }

                    const fractional = realEmissionsPerson * (controls.fractionalCompensation / 100);
                    const constant = controls.annualEmissions * (controls.constantCompensation / 100);
                    const compensatedEmissionsPerson = fractional + constant;
                    const effectiveEmissionsPerson = realEmissionsPerson - compensatedEmissionsPerson;

                    // Apply scale
                    let scaleFactor = 1;
                    if (controls.scale === 'population') {
                        scaleFactor = controls.population * 1000;
                    }

                    realEmissions.push(realEmissionsPerson * scaleFactor);
                    compensatedEmissions.push(compensatedEmissionsPerson * scaleFactor);
                    effectiveEmissions.push(effectiveEmissionsPerson * scaleFactor);
                    annualCosts.push(compensatedEmissionsPerson * controls.compensationCost * scaleFactor);

                    cumSum += effectiveEmissionsPerson;
                    cumulativeEffective.push(cumSum);
                }

                if (controls.type === 'emissions') {
                    drawEmissionsPlot(p, controls, years, realEmissions, effectiveEmissions, compensatedEmissions, cumulativeEffective);
                } else {
                    drawCostsPlot(p, controls, years, annualCosts);
                }

                drawBudgetPlot(p, controls, cumulativeEffective, effectiveEmissions);
            };

            function drawEmissionsPlot(p, controls, years, realEmissions, effectiveEmissions, compensatedEmissions, cumulativeEffective) {
                // Find max value for y-axis
                let maxY = Math.max(...realEmissions, 0);
                let minY = Math.min(...effectiveEmissions, 0);
                const yRange = maxY - minY;
                maxY += yRange * 0.1;
                minY -= yRange * 0.1;

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(marginLeft, marginTop + plotHeight, marginLeft + plotWidth, marginTop + plotHeight);
                p.line(marginLeft, marginTop, marginLeft, marginTop + plotHeight);

                // Y-axis label
                p.push();
                p.translate(20, marginTop + plotHeight/2);
                p.rotate(-p.PI/2);
                p.textAlign(p.CENTER);
                p.noStroke();
                p.fill(0);
                p.textSize(14);
                if (controls.scale === 'individual') {
                    p.text('annual CO₂ emissions per person (tons)', 0, 0);
                } else {
                    p.text('annual city emission of CO₂ (mil tons)', 0, 0);
                }
                p.pop();

                // X-axis labels
                p.textAlign(p.CENTER);
                p.textSize(12);
                for (let year = 2020; year <= 2050; year += 5) {
                    const x = marginLeft + ((year - 2020) / 30) * plotWidth;
                    p.text(year, x, marginTop + plotHeight + 20);
                }

                // Y-axis labels
                p.textAlign(p.RIGHT);
                const yTicks = 5;
                for (let i = 0; i <= yTicks; i++) {
                    const y = marginTop + plotHeight - (i / yTicks) * plotHeight;
                    const value = minY + (i / yTicks) * (maxY - minY);
                    p.text(value.toFixed(1), marginLeft - 10, y + 5);
                    p.stroke(200);
                    p.strokeWeight(1);
                    p.line(marginLeft, y, marginLeft + plotWidth, y);
                }

                // Draw zero line if needed
                if (minY < 0 && maxY > 0) {
                    const zeroY = marginTop + plotHeight - ((0 - minY) / (maxY - minY)) * plotHeight;
                    p.stroke(0);
                    p.strokeWeight(1);
                    p.line(marginLeft, zeroY, marginLeft + plotWidth, zeroY);
                }

                // Helper function to map values to screen coordinates
                const mapX = (year) => marginLeft + ((year - 2020) / 30) * plotWidth;
                const mapY = (value) => marginTop + plotHeight - ((value - minY) / (maxY - minY)) * plotHeight;

                // Draw filled areas
                p.noStroke();

                // Blue area (compensated emissions)
                p.fill(100, 150, 255, 100);
                p.beginShape();
                for (let i = 0; i < years.length; i++) {
                    p.vertex(mapX(years[i]), mapY(realEmissions[i]));
                }
                for (let i = years.length - 1; i >= 0; i--) {
                    p.vertex(mapX(years[i]), mapY(effectiveEmissions[i]));
                }
                p.endShape(p.CLOSE);

                // Red/Green areas (effective emissions)
                for (let i = 0; i < years.length - 1; i++) {
                    if (effectiveEmissions[i] > 0 || effectiveEmissions[i+1] > 0) {
                        p.fill(255, 100, 100, 100);
                    } else {
                        p.fill(100, 255, 100, 100);
                    }
                    p.beginShape();
                    p.vertex(mapX(years[i]), mapY(effectiveEmissions[i]));
                    p.vertex(mapX(years[i+1]), mapY(effectiveEmissions[i+1]));
                    p.vertex(mapX(years[i+1]), mapY(0));
                    p.vertex(mapX(years[i]), mapY(0));
                    p.endShape(p.CLOSE);
                }

                // Draw lines
                p.noFill();
                p.strokeWeight(2);

                // Real emissions (red line)
                p.stroke(255, 0, 0);
                p.beginShape();
                for (let i = 0; i < years.length; i++) {
                    p.vertex(mapX(years[i]), mapY(realEmissions[i]));
                }
                p.endShape();

                // Effective emissions (black line)
                p.stroke(0);
                p.beginShape();
                for (let i = 0; i < years.length; i++) {
                    p.vertex(mapX(years[i]), mapY(effectiveEmissions[i]));
                }
                p.endShape();

                // Climate neutral marker
                if (neutral_year) {
                    p.stroke(0, 150, 0);
                    p.strokeWeight(2);
                    const neutralX = mapX(neutral_year);
                    p.line(neutralX, marginTop, neutralX, marginTop + plotHeight);
                    p.noStroke();
                    p.fill(0, 150, 0);
                    p.textAlign(p.CENTER);
                    p.text(`climate neutral ${neutral_year}`, neutralX, marginTop - 10);

                    // Cumulative emissions lines
                    const neutralIndex = neutral_year - 2020;
                    const cumEmissions2050 = cumulativeEffective[30];
                    const cumEmissionsNeutral = cumulativeEffective[neutralIndex];

                    p.stroke(100);
                    p.strokeWeight(1);
                    p.drawingContext.setLineDash([5, 5]);

                    // Line for emissions until neutral year
                    const lineY1 = mapY(cumEmissionsNeutral);
                    p.line(marginLeft, lineY1, marginLeft + plotWidth, lineY1);
                    p.noStroke();
                    p.fill(100);
                    p.textAlign(p.LEFT);
                    p.text(`CO₂ emissions until ${neutral_year} = ${cumEmissionsNeutral.toFixed(1)} tons CO₂`, marginLeft + plotWidth + 10, lineY1);

                    // Line for emissions until 2050
                    p.stroke(100);
                    p.drawingContext.setLineDash([5, 5]);
                    const lineY2 = mapY(cumEmissions2050);
                    p.line(marginLeft, lineY2, marginLeft + plotWidth, lineY2);
                    p.noStroke();
                    p.fill(100);
                    p.text(`CO₂ emissions until 2050 = ${cumEmissions2050.toFixed(1)} tons CO₂`, marginLeft + plotWidth + 10, lineY2 + 20);

                    p.drawingContext.setLineDash([]);
                }
            }

            function drawCostsPlot(p, controls, years, annualCosts) {
                const maxY = Math.max(...annualCosts) * 1.2;
                const totalCosts = annualCosts.reduce((a, b) => a + b, 0);
                const avgCosts = totalCosts / 31;

                // Draw axes
                p.stroke(0);
                p.strokeWeight(2);
                p.line(marginLeft, marginTop + plotHeight, marginLeft + plotWidth, marginTop + plotHeight);
                p.line(marginLeft, marginTop, marginLeft, marginTop + plotHeight);

                // Y-axis label
                p.push();
                p.translate(20, marginTop + plotHeight/2);
                p.rotate(-p.PI/2);
                p.textAlign(p.CENTER);
                p.noStroke();
                p.fill(0);
                p.textSize(14);
                p.text('annual personal compensation costs ($)', 0, 0);
                p.pop();

                // X-axis labels
                p.textAlign(p.CENTER);
                p.textSize(12);
                for (let year = 2020; year <= 2050; year += 5) {
                    const x = marginLeft + ((year - 2020) / 30) * plotWidth;
                    p.text(year, x, marginTop + plotHeight + 20);
                }

                // Y-axis labels
                p.textAlign(p.RIGHT);
                const yTicks = 5;
                for (let i = 0; i <= yTicks; i++) {
                    const y = marginTop + plotHeight - (i / yTicks) * plotHeight;
                    const value = (i / yTicks) * maxY;
                    p.text(value.toFixed(0), marginLeft - 10, y + 5);
                    p.stroke(200);
                    p.strokeWeight(1);
                    p.line(marginLeft, y, marginLeft + plotWidth, y);
                }

                // Helper functions
                const mapX = (year) => marginLeft + ((year - 2020) / 30) * plotWidth;
                const mapY = (value) => marginTop + plotHeight - (value / maxY) * plotHeight;

                // Draw filled area
                p.fill(100, 150, 255, 100);
                p.noStroke();
                p.beginShape();
                p.vertex(mapX(2020), mapY(0));
                for (let i = 0; i < years.length; i++) {
                    p.vertex(mapX(years[i]), mapY(annualCosts[i]));
                }
                p.vertex(mapX(2050), mapY(0));
                p.endShape(p.CLOSE);

                // Draw line
                p.stroke(0, 0, 255);
                p.strokeWeight(2);
                p.noFill();
                p.beginShape();
                for (let i = 0; i < years.length; i++) {
                    p.vertex(mapX(years[i]), mapY(annualCosts[i]));
                }
                p.endShape();

                // Average line
                p.stroke(100);
                p.strokeWeight(1);
                p.drawingContext.setLineDash([5, 5]);
                const avgY = mapY(avgCosts);
                p.line(marginLeft, avgY, marginLeft + plotWidth, avgY);
                p.drawingContext.setLineDash([]);

                // Labels
                p.noStroke();
                p.fill(100);
                p.textAlign(p.LEFT);
                p.text(`annual personal compensation costs (average) = $${avgCosts.toFixed(0)} per year`, marginLeft + plotWidth + 10, avgY);

                p.fill(0);
                p.textAlign(p.CENTER);
                p.textSize(14);
                p.text(`total personal compensation costs (until 2050) = $${totalCosts.toFixed(0)}`, marginLeft + plotWidth/2, marginTop - 20);
            }

            function drawBudgetPlot(p, controls, cumulativeEffective, effectiveEmissions) {
                const budgetY = marginTop + plotHeight + 80;
                const budgetYears = [2020, 2030, 2040, 2050];
                const spacing = plotWidth / 4;
                const totalBudget = 30 * controls.annualBudget;

                p.noStroke();
                p.fill(0);
                p.textAlign(p.CENTER);
                p.textSize(16);
                if (controls.scale === 'individual') {
                    p.text('individual climate budget', marginLeft + plotWidth/2, budgetY - 30);
                } else {
                    p.text('population climate budget', marginLeft + plotWidth/2, budgetY - 30);
                }

                budgetYears.forEach((year, i) => {
                    const centerX = marginLeft + spacing/2 + i * spacing;
                    const yearIndex = year - 2020;
                    const remaining = totalBudget - cumulativeEffective[yearIndex];
                    const radius = Math.sqrt(Math.abs(remaining)) * 5;

                    // Draw circle
                    if (effectiveEmissions[yearIndex] <= 0) {
                        p.fill(100, 255, 100);
                        p.noStroke();
                    } else {
                        p.noFill();
                        p.stroke(100, 255, 100);
                        p.strokeWeight(2);
                        p.drawingContext.setLineDash([5, 5]);
                    }

                    p.circle(centerX, budgetY + 40, radius * 2);
                    p.drawingContext.setLineDash([]);

                    // Labels
                    p.noStroke();
                    p.fill(0);
                    p.textSize(12);
                    p.text(year, centerX, budgetY);
                    p.text(`${remaining.toFixed(1)} tons CO₂`, centerX, budgetY + 45);
                });
            }
        };

        // Initialize p5
        myp5 = new p5(sketch);
    </script>
</body>
</html>
