<body>
    <div id="main-container">
        <div id="control-panel">
            <h3>relative volatility</h3>
            <button id="btn-reset">+</button>
            <div class="slider-row">
                <label for="slider-ac">between A and C</label>
                <input type="range" id="slider-ac" min="0.1" max="5.0" step="0.01" value="0.6">
                <span id="value-ac">0.60</span>
            </div>
            <div class="slider-row">
                <label for="slider-bc">between B and C</label>
                <input type="range" id="slider-bc" min="0.1" max="5.0" step="0.01" value="3.93">
                <span id="value-bc">3.93</span>
            </div>
        </div>
        <div id="visualization-container">
            <canvas id="ternary-plot-canvas"></canvas>
        </div>
    </div>

    <script>
        // Get DOM elements
        const sliderAC = document.getElementById('slider-ac');
        const sliderBC = document.getElementById('slider-bc');
        const valueAC = document.getElementById('value-ac');
        const valueBC = document.getElementById('value-bc');
        const btnReset = document.getElementById('btn-reset');

        // Update value displays
        function updateValueDisplay() {
            valueAC.textContent = parseFloat(sliderAC.value).toFixed(2);
            valueBC.textContent = parseFloat(sliderBC.value).toFixed(2);
        }

        // Reset function
        function resetValues() {
            sliderAC.value = 0.6;
            sliderBC.value = 3.93;
            updateValueDisplay();
            if (window.redrawTernaryPlot) {
                window.redrawTernaryPlot();
            }
        }

        // Event listeners
        sliderAC.addEventListener('input', () => {
            updateValueDisplay();
            if (window.redrawTernaryPlot) {
                window.redrawTernaryPlot();
            }
        });

        sliderBC.addEventListener('input', () => {
            updateValueDisplay();
            if (window.redrawTernaryPlot) {
                window.redrawTernaryPlot();
            }
        });

        btnReset.addEventListener('click', resetValues);

        // p5.js sketch
        const sketch = (p) => {
            const canvasWidth = 600;
            const canvasHeight = 550;
            const padding = 50;
            const N = 10; // Grid divisions

            // Triangle vertices in canvas coordinates
            const vertices = {
                A: { x: canvasWidth / 2, y: padding },
                B: { x: padding, y: canvasHeight - padding },
                C: { x: canvasWidth - padding, y: canvasHeight - padding }
            };

            p.setup = () => {
                const canvas = p.createCanvas(canvasWidth, canvasHeight);
                canvas.parent('ternary-plot-canvas');
                drawTernaryPlot();
            };

            // Convert barycentric to Cartesian coordinates
            function barycentricToCartesian(xa, xb, xc) {
                const x = xa * vertices.A.x + xb * vertices.B.x + xc * vertices.C.x;
                const y = xa * vertices.A.y + xb * vertices.B.y + xc * vertices.C.y;
                return { x, y };
            }

            // Calculate vapor phase mole fractions
            function calculateVaporPhase(x1, x2, x3, alpha13, alpha23) {
                const alpha1 = alpha13;
                const alpha2 = alpha23;
                const alpha3 = 1;
                const D = alpha1 * x1 + alpha2 * x2 + alpha3 * x3;
                return {
                    y1: (alpha1 * x1) / D,
                    y2: (alpha2 * x2) / D,
                    y3: (alpha3 * x3) / D
                };
            }

            // Calculate function value
            function calculateF(x1, x2, x3, alpha13, alpha23) {
                const { y1, y2, y3 } = calculateVaporPhase(x1, x2, x3, alpha13, alpha23);
                return Math.pow(x1 - y1, 2) + Math.pow(x2 - y2, 2) + Math.pow(x3 - y3, 2);
            }

            // Color interpolation
            function getColorForValue(value) {
                const colorStops = [
                    { pos: 0.0, color: [44, 123, 182] },   // #2c7bb6
                    { pos: 0.25, color: [171, 217, 233] }, // #abd9e9
                    { pos: 0.5, color: [255, 255, 191] },  // #ffffbf
                    { pos: 0.75, color: [253, 174, 97] },  // #fdae61
                    { pos: 1.0, color: [215, 25, 28] }     // #d7191c
                ];

                let lower, upper;
                for (let i = 0; i < colorStops.length - 1; i++) {
                    if (value >= colorStops[i].pos && value <= colorStops[i + 1].pos) {
                        lower = colorStops[i];
                        upper = colorStops[i + 1];
                        break;
                    }
                }

                const t = (value - lower.pos) / (upper.pos - lower.pos);
                const r = p.lerp(lower.color[0], upper.color[0], t);
                const g = p.lerp(lower.color[1], upper.color[1], t);
                const b = p.lerp(lower.color[2], upper.color[2], t);
                return p.color(r, g, b);
            }

            function drawTernaryPlot() {
                p.background(255);

                const alpha13 = parseFloat(sliderAC.value);
                const alpha23 = parseFloat(sliderBC.value);

                // Draw colored triangular grid
                p.strokeWeight(0.5);
                p.stroke(0);

                for (let row = 0; row < N; row++) {
                    for (let col = 0; col <= row; col++) {
                        // Calculate vertices of small triangle
                        const i = row;
                        const j = col;

                        // Three vertices of the small triangle in grid coordinates
                        const v1 = { i: i, j: j };
                        const v2 = { i: i + 1, j: j };
                        const v3 = { i: i + 1, j: j + 1 };

                        // Convert to barycentric coordinates
                        const toBary = (v) => {
                            const xc = v.i / N;
                            const xb = v.j / N;
                            const xa = 1 - xb - xc;
                            return { xa, xb, xc };
                        };

                        const b1 = toBary(v1);
                        const b2 = toBary(v2);
                        const b3 = toBary(v3);

                        // Calculate centroid
                        const xa = (b1.xa + b2.xa + b3.xa) / 3;
                        const xb = (b1.xb + b2.xb + b3.xb) / 3;
                        const xc = (b1.xc + b2.xc + b3.xc) / 3;

                        // Calculate function value at centroid
                        const F = calculateF(xa, xb, xc, alpha13, alpha23);
                        const normalizedF = Math.min(F / 2.0, 1.0);

                        // Get color
                        const fillColor = getColorForValue(normalizedF);
                        p.fill(fillColor);

                        // Convert vertices to canvas coordinates and draw
                        const p1 = barycentricToCartesian(b1.xa, b1.xb, b1.xc);
                        const p2 = barycentricToCartesian(b2.xa, b2.xb, b2.xc);
                        const p3 = barycentricToCartesian(b3.xa, b3.xb, b3.xc);

                        p.triangle(p1.x, p1.y, p2.x, p2.y, p3.x, p3.y);

                        // Draw inverted triangle if not at edge
                        if (col < row) {
                            const v4 = { i: i, j: j + 1 };
                            const b4 = toBary(v4);

                            // Centroid of inverted triangle
                            const xa2 = (b2.xa + b3.xa + b4.xa) / 3;
                            const xb2 = (b2.xb + b3.xb + b4.xb) / 3;
                            const xc2 = (b2.xc + b3.xc + b4.xc) / 3;

                            const F2 = calculateF(xa2, xb2, xc2, alpha13, alpha23);
                            const normalizedF2 = Math.min(F2 / 2.0, 1.0);
                            const fillColor2 = getColorForValue(normalizedF2);
                            p.fill(fillColor2);

                            const p4 = barycentricToCartesian(b4.xa, b4.xb, b4.xc);
                            p.triangle(p2.x, p2.y, p3.x, p3.y, p4.x, p4.y);
                        }
                    }
                }

                // Draw main triangle outline
                p.strokeWeight(2);
                p.stroke(0);
                p.noFill();
                p.triangle(vertices.A.x, vertices.A.y, vertices.B.x, vertices.B.y, vertices.C.x, vertices.C.y);

                // Draw labels
                p.fill(0);
                p.strokeWeight(0);
                p.textSize(16);
                p.textAlign(p.CENTER);
                p.text('A', vertices.A.x, vertices.A.y - 10);
                p.text('B', vertices.B.x - 20, vertices.B.y + 20);
                p.text('C', vertices.C.x + 20, vertices.C.y + 20);

                // Draw axis ticks and labels
                p.textSize(12);
                for (let i = 0; i <= 10; i++) {
                    const frac = i / 10;

                    // A-B axis (left side)
                    const abPoint = barycentricToCartesian(1 - frac, frac, 0);
                    p.text(frac.toFixed(1), abPoint.x - 25, abPoint.y + 5);

                    // B-C axis (bottom)
                    const bcPoint = barycentricToCartesian(0, 1 - frac, frac);
                    p.text(frac.toFixed(1), bcPoint.x, bcPoint.y + 20);

                    // C-A axis (right side)
                    const caPoint = barycentricToCartesian(1 - frac, 0, frac);
                    p.text((1 - frac).toFixed(1), caPoint.x + 25, caPoint.y + 5);
                }
            }

            window.redrawTernaryPlot = () => {
                drawTernaryPlot();
            };
        };

        new p5(sketch);
    </script>
</body>
</html>
