                </div>
                <div>
                    <label>hour 2</label>
                    <input type="range" id="slider-uh-2" min="0" max="10" step="1" value="5">
                    <span id="value-uh-2">5</span>
                </div>
                <div>
                    <label>hour 3</label>
                    <input type="range" id="slider-uh-3" min="0" max="10" step="1" value="3">
                    <span id="value-uh-3">3</span>
                </div>
                <div>
                    <label>hour 4</label>
                    <input type="range" id="slider-uh-4" min="0" max="10" step="1" value="1">
                    <span id="value-uh-4">1</span>
                </div>
            </div>
        </div>

        <div id="plots-container">
            <div id="plot-canvas-1"></div>
            <div id="plot-canvas-2"></div>
            <div id="plot-canvas-3"></div>
            <div id="plot-canvas-4"></div>
            <div id="plot-canvas-5"></div>
            <div id="plot-canvas-6"></div>
        </div>
    </div>

    <script>
        // Global variables
        let P = [1, 0, 0];
        let U = [1, 5, 3, 1];
        let C1 = [], C2 = [], C3 = [], Q = [];
        let sketches = [];

        // Toggle controls functionality
        document.getElementById('toggle-controls').addEventListener('click', function() {
            const slidersArea = document.getElementById('sliders-area');
            if (slidersArea.style.display === 'none') {
                slidersArea.style.display = 'block';
                this.textContent = '-';
            } else {
                slidersArea.style.display = 'none';
                this.textContent = '+';
            }
        });

        // Add event listeners to all sliders
        for (let i = 1; i <= 3; i++) {
            const slider = document.getElementById(`slider-precip-${i}`);
            const valueSpan = document.getElementById(`value-precip-${i}`);
            slider.addEventListener('input', function() {
                valueSpan.textContent = this.value;
                update();
            });
        }

        for (let i = 1; i <= 4; i++) {
            const slider = document.getElementById(`slider-uh-${i}`);
            const valueSpan = document.getElementById(`value-uh-${i}`);
            slider.addEventListener('input', function() {
                valueSpan.textContent = this.value;
                update();
            });
        }

        // Update function
        function update() {
            // Read current values from sliders
            P = [
                parseInt(document.getElementById('slider-precip-1').value),
                parseInt(document.getElementById('slider-precip-2').value),
                parseInt(document.getElementById('slider-precip-3').value)
            ];

            U = [
                parseInt(document.getElementById('slider-uh-1').value),
                parseInt(document.getElementById('slider-uh-2').value),
                parseInt(document.getElementById('slider-uh-3').value),
                parseInt(document.getElementById('slider-uh-4').value)
            ];

            // Calculate component hydrographs
            C1 = U.map(u => P[0] * u);
            C2 = U.map(u => P[1] * u);
            C3 = U.map(u => P[2] * u);

            // Calculate composite hydrograph
            Q = [
                P[0] * U[0],
                P[0] * U[1] + P[1] * U[0],
                P[0] * U[2] + P[1] * U[1] + P[2] * U[0],
                P[0] * U[3] + P[1] * U[2] + P[2] * U[1],
                P[1] * U[3] + P[2] * U[2],
                P[2] * U[3]
            ];

            // Trigger redraw for all sketches
            sketches.forEach(sketch => {
                if (sketch && sketch.redraw) {
                    sketch.redraw();
                }
            });
        }

        // Drawing function for step charts
        function drawPlot(p, data, xLabel, yLabel, xMax, yMax, xTicks, yTicks, timeOffset = 0) {
            p.background(255);

            const margin = 50;
            const plotWidth = p.width - 2 * margin;
            const plotHeight = p.height - 2 * margin;

            // Draw axes
            p.stroke(0);
            p.strokeWeight(1);
            p.line(margin, margin + plotHeight, margin + plotWidth, margin + plotHeight); // x-axis
            p.line(margin, margin, margin, margin + plotHeight); // y-axis

            // Draw axis labels
            p.textAlign(p.CENTER, p.CENTER);
            p.text(xLabel, margin + plotWidth/2, p.height - 20);
            p.push();
            p.translate(20, margin + plotHeight/2);
            p.rotate(-p.PI/2);
            p.text(yLabel, 0, 0);
            p.pop();

            // Draw tick marks and labels
            p.textAlign(p.CENTER, p.TOP);
            for (let i = 0; i <= xMax; i += xTicks) {
                const x = margin + (i / xMax) * plotWidth;
                p.line(x, margin + plotHeight, x, margin + plotHeight + 5);
                p.text(i, x, margin + plotHeight + 10);
            }

            p.textAlign(p.RIGHT, p.CENTER);
            for (let i = 0; i <= yMax; i += yTicks) {
                const y = margin + plotHeight - (i / yMax) * plotHeight;
                p.line(margin - 5, y, margin, y);
                p.text(i, margin - 10, y);
            }

            // Draw step chart
            p.fill(212, 230, 241, 150); // Light blue with transparency
            p.stroke(46, 134, 193); // Darker blue
            p.strokeWeight(2);

            // Start drawing the filled area
            p.beginShape();
            p.vertex(margin + (timeOffset / xMax) * plotWidth, margin + plotHeight);

            for (let i = 0; i < data.length; i++) {
                const x1 = margin + ((i + timeOffset) / xMax) * plotWidth;
                const x2 = margin + ((i + 1 + timeOffset) / xMax) * plotWidth;
                const y = margin + plotHeight - (data[i] / yMax) * plotHeight;

                p.vertex(x1, y);
                p.vertex(x2, y);
            }

            // Close the shape
            const lastX = margin + ((data.length + timeOffset) / xMax) * plotWidth;
            p.vertex(lastX, margin + plotHeight);
            p.endShape(p.CLOSE);

            // Draw the outline
            p.noFill();
            p.beginShape();
            for (let i = 0; i < data.length; i++) {
                const x1 = margin + ((i + timeOffset) / xMax) * plotWidth;
                const x2 = margin + ((i + 1 + timeOffset) / xMax) * plotWidth;
                const y = margin + plotHeight - (data[i] / yMax) * plotHeight;

                p.vertex(x1, y);
                p.vertex(x2, y);

                if (i < data.length - 1) {
                    const nextY = margin + plotHeight - (data[i + 1] / yMax) * plotHeight;
                    p.vertex(x2, nextY);
                }
            }
            p.endShape();
        }

        // Create p5.js sketches for each plot
        function createSketch(containerId, plotFunction) {
            return new p5(function(p) {
                p.setup = function() {
                    const container = document.getElementById(containerId);
                    const canvas = p.createCanvas(container.offsetWidth - 20, 300);
                    canvas.parent(containerId);
                    p.noLoop();
                };

                p.draw = function() {
                    plotFunction(p);
                };

                p.windowResized = function() {
                    const container = document.getElementById(containerId);
                    p.resizeCanvas(container.offsetWidth - 20, 300);
                };
            });
        }

        // Initialize plots
        sketches[0] = createSketch('plot-canvas-1', (p) => drawPlot(p, P, 't (hr)', 'P (in)', 5, 10, 1, 2));
        sketches[1] = createSketch('plot-canvas-2', (p) => drawPlot(p, U, 't (hr)', 'Q (cfs/in)', 5, 10, 1, 2));
        sketches[2] = createSketch('plot-canvas-3', (p) => drawPlot(p, C1, 't (hr)', 'Q (cfs)', 8, 100, 2, 20, 0));
        sketches[3] = createSketch('plot-canvas-4', (p) => drawPlot(p, C2, 't (hr)', 'Q (cfs)', 8, 100, 2, 20, 1));
        sketches[4] = createSketch('plot-canvas-5', (p) => drawPlot(p, C3, 't (hr)', 'Q (cfs)', 8, 100, 2, 20, 2));
        sketches[5] = createSketch('plot-canvas-6', (p) => drawPlot(p, Q, 't (hr)', 'Q (cfs)', 8, 100, 2, 20, 0));

        // Initial update
        update();
    </script>
</body>
</html>
