                        const newValue = Math.min(10, parseInt(slider.value) + 1);
                        slider.value = newValue;
                        valueSpan.textContent = newValue;
                        updateMatrixValue(matrix, index, newValue);
                        updateVisualization();
                    });
                });
            });
        }

        function updateMatrixValue(matrix, index, value) {
            if (matrix === 'a') {
                if (index === '11') a11 = value;
                else if (index === '12') a12 = value;
                else if (index === '21') a21 = value;
                else if (index === '22') a22 = value;
            } else {
                if (index === '11') b11 = value;
                else if (index === '12') b12 = value;
                else if (index === '21') b21 = value;
                else if (index === '22') b22 = value;
            }
