        function updatePayoffTable() {
            document.getElementById('cell-11').textContent = `${a11}, ${b11}`;
            document.getElementById('cell-12').textContent = `${a12}, ${b12}`;
            document.getElementById('cell-21').textContent = `${a21}, ${b21}`;
            document.getElementById('cell-22').textContent = `${a22}, ${b22}`;
        }

        function gcd(a, b) {
            a = Math.abs(a);
            b = Math.abs(b);
            while (b) {
                let temp = b;
                b = a % b;
                a = temp;
            }
            return a;
        }

        function toFraction(decimal, tolerance = 1e-6) {
            if (Math.abs(decimal - Math.round(decimal)) < tolerance) {
                return Math.round(decimal).toString();
            }

            let sign = decimal < 0 ? -1 : 1;
            decimal = Math.abs(decimal);

            let numerator = 1;
            let denominator = 1;
            let fraction = numerator / denominator;

            while (Math.abs(fraction - decimal) > tolerance && denominator < 1000) {
                if (fraction < decimal) {
                    numerator++;
                } else {
                    denominator++;
                    numerator = Math.round(decimal * denominator);
                }
                fraction = numerator / denominator;
            }

            let g = gcd(numerator, denominator);
            numerator = numerator / g;
            denominator = denominator / g;

            if (denominator === 1) {
                return (sign * numerator).toString();
            }
            return `\\frac{${sign * numerator}}{${denominator}}`;
        }

        let equilibriaPoints = [];

        function findEquilibria() {
            equilibriaPoints = [];

            const den1 = a11 - a12 - a21 + a22;
            const den2 = b11 - b12 - b21 + b22;

            let y1_indiff = null;
            let x1_indiff = null;

            if (Math.abs(den1) > 1e-6) {
                y1_indiff = (a22 - a12) / den1;
            }

            if (Math.abs(den2) > 1e-6) {
                x1_indiff = (b22 - b21) / den2;
            }

            // Find intersections
            const blueSegments = getPlayer1BestResponse();
            const greenSegments = getPlayer2BestResponse();

            for (let blue of blueSegments) {
                for (let green of greenSegments) {
                    const intersections = findSegmentIntersections(blue, green);
                    equilibriaPoints.push(...intersections);
                }
            }

            // Remove duplicates
            equilibriaPoints = equilibriaPoints.filter((point, index, self) =>
                index === self.findIndex((p) => Math.abs(p.x - point.x) < 1e-6 && Math.abs(p.y - point.y) < 1e-6)
            );
        }

        function getPlayer1BestResponse() {
            const segments = [];
            const den1 = a11 - a12 - a21 + a22;

            if (Math.abs(den1) < 1e-6) {
                // Dominant strategy
                if (a12 - a22 > 0) {
                    segments.push({x1: 1, y1: 0, x2: 1, y2: 1});
                } else {
                    segments.push({x1: 0, y1: 0, x2: 0, y2: 1});
                }
            } else {
                const y1_indiff = (a22 - a12) / den1;

                if (y1_indiff > 0 && y1_indiff < 1) {
                    segments.push({x1: 0, y1: y1_indiff, x2: 1, y2: y1_indiff});
                    if (den1 > 0) {
                        segments.push({x1: 0, y1: 0, x2: 0, y2: y1_indiff});
                        segments.push({x1: 1, y1: y1_indiff, x2: 1, y2: 1});
                    } else {
                        segments.push({x1: 0, y1: y1_indiff, x2: 0, y2: 1});
                        segments.push({x1: 1, y1: 0, x2: 1, y2: y1_indiff});
                    }
                } else {
                    // Check preference at y1 = 0.5
                    const payoff1 = 0.5 * a11 + 0.5 * a21;
                    const payoff2 = 0.5 * a12 + 0.5 * a22;
                    if (payoff1 > payoff2) {
                        segments.push({x1: 1, y1: 0, x2: 1, y2: 1});
                    } else {
                        segments.push({x1: 0, y1: 0, x2: 0, y2: 1});
                    }
                }
            }

            return segments;
        }

        function getPlayer2BestResponse() {
            const segments = [];
            const den2 = b11 - b12 - b21 + b22;

            if (Math.abs(den2) < 1e-6) {
                // Dominant strategy
                if (b21 - b22 > 0) {
                    segments.push({x1: 0, y1: 1, x2: 1, y2: 1});
                } else {
                    segments.push({x1: 0, y1: 0, x2: 1, y2: 0});
                }
            } else {
                const x1_indiff = (b22 - b21) / den2;

                if (x1_indiff > 0 && x1_indiff < 1) {
                    segments.push({x1: x1_indiff, y1: 0, x2: x1_indiff, y2: 1});
                    if (den2 > 0) {
                        segments.push({x1: 0, y1: 0, x2: x1_indiff, y2: 0});
                        segments.push({x1: x1_indiff, y1: 1, x2: 1, y2: 1});
                    } else {
                        segments.push({x1: x1_indiff, y1: 0, x2: 1, y2: 0});
                        segments.push({x1: 0, y1: 1, x2: x1_indiff, y2: 1});
                    }
                } else {
                    // Check preference at x1 = 0.5
                    const payoff1 = 0.5 * b11 + 0.5 * b12;
                    const payoff2 = 0.5 * b21 + 0.5 * b22;
                    if (payoff1 > payoff2) {
                        segments.push({x1: 0, y1: 1, x2: 1, y2: 1});
                    } else {
                        segments.push({x1: 0, y1: 0, x2: 1, y2: 0});
                    }
                }
            }

            return segments;
        }

        function findSegmentIntersections(seg1, seg2) {
            const intersections = [];

            // Check if segments are parallel
            const dx1 = seg1.x2 - seg1.x1;
            const dy1 = seg1.y2 - seg1.y1;
            const dx2 = seg2.x2 - seg2.x1;
            const dy2 = seg2.y2 - seg2.y1;

            if (Math.abs(dx1 * dy2 - dy1 * dx2) < 1e-6) {
                // Parallel segments - check for overlap
                if (Math.abs(dx1) > 1e-6) {
                    // Horizontal segments
                    if (Math.abs(seg1.y1 - seg2.y1) < 1e-6) {
                        const left = Math.max(Math.min(seg1.x1, seg1.x2), Math.min(seg2.x1, seg2.x2));
                        const right = Math.min(Math.max(seg1.x1, seg1.x2), Math.max(seg2.x1, seg2.x2));
                        if (left <= right) {
                            intersections.push({x: left, y: seg1.y1});
                            if (right > left) intersections.push({x: right, y: seg1.y1});
                        }
                    }
                } else {
                    // Vertical segments
                    if (Math.abs(seg1.x1 - seg2.x1) < 1e-6) {
                        const bottom = Math.max(Math.min(seg1.y1, seg1.y2), Math.min(seg2.y1, seg2.y2));
                        const top = Math.min(Math.max(seg1.y1, seg1.y2), Math.max(seg2.y1, seg2.y2));
                        if (bottom <= top) {
                            intersections.push({x: seg1.x1, y: bottom});
                            if (top > bottom) intersections.push({x: seg1.x1, y: top});
                        }
                    }
                }
            } else {
                // Non-parallel segments
                const t = ((seg2.x1 - seg1.x1) * dy2 - (seg2.y1 - seg1.y1) * dx2) / (dx1 * dy2 - dy1 * dx2);
                const u = ((seg2.x1 - seg1.x1) * dy1 - (seg2.y1 - seg1.y1) * dx1) / (dx1 * dy2 - dy1 * dx2);

                if (t >= 0 && t <= 1 && u >= 0 && u <= 1) {
                    const x = seg1.x1 + t * dx1;
                    const y = seg1.y1 + t * dy1;
                    intersections.push({x, y});
                }
            }

            return intersections;
        }

        function updateEquilibriaDisplay() {
            findEquilibria();

            if (equilibriaPoints.length === 0) {
                document.getElementById('equilibria-display').innerHTML = '\\( \\emptyset \\)';
            } else {
                const pointStrings = equilibriaPoints.map(p => {
                    const xStr = toFraction(p.x);
                    const yStr = toFraction(p.y);
                    return `(${xStr}, ${yStr})`;
                });

                const latexStr = `\\( \\{ ${pointStrings.join(', ')} \\} \\)`;
                document.getElementById('equilibria-display').innerHTML = latexStr;
            }

            if (window.MathJax) {
                MathJax.typesetPromise([document.getElementById('equilibria-display')]);
            }
        }

        function updateVisualization() {
            updatePayoffTable();
            updateEquilibriaDisplay();
            redraw();
        }

        // p5.js sketch
        function setup() {
            const canvasContainer = document.getElementById('canvas-container');
            canvas = createCanvas(400, 400);
            canvas.parent(canvasContainer);
            noLoop();
            updateVisualization();
        }

        function draw() {
            background(255);

            // Set up coordinate system
            push();
            translate(40, height - 40);
            scale(320, -320);

            // Draw grid
            stroke(230);
            strokeWeight(0.002);
            for (let i = 0; i <= 5; i++) {
                line(0, i/5, 1, i/5);
                line(i/5, 0, i/5, 1);
            }

            // Draw axes
            stroke(0);
            strokeWeight(0.004);
            line(0, 0, 1, 0);
            line(0, 0, 0, 1);

            // Draw best response graphs
            strokeWeight(0.006);

            // Player 1 (Blue)
            stroke(0, 100, 255);
            const blueSegments = getPlayer1BestResponse();
            for (let seg of blueSegments) {
                line(seg.x1, seg.y1, seg.x2, seg.y2);
            }

            // Player 2 (Green)
            stroke(0, 200, 100);
            const greenSegments = getPlayer2BestResponse();
            for (let seg of greenSegments) {
                line(seg.x1, seg.y1, seg.x2, seg.y2);
            }

            // Draw equilibria (Red)
            stroke(255, 0, 0);
            strokeWeight(0.012);

            // Find intersection segments
            const intersectionSegments = [];
            for (let blue of blueSegments) {
                for (let green of greenSegments) {
                    const intersections = findSegmentIntersections(blue, green);
                    if (intersections.length === 2) {
                        intersectionSegments.push({
                            x1: intersections[0].x,
                            y1: intersections[0].y,
                            x2: intersections[1].x,
                            y2: intersections[1].y
                        });
                    }
                }
            }

            for (let seg of intersectionSegments) {
                line(seg.x1, seg.y1, seg.x2, seg.y2);
            }

            // Draw equilibrium points
            fill(255, 0, 0);
            noStroke();
            for (let point of equilibriaPoints) {
                ellipse(point.x, point.y, 0.025, 0.025);
            }

            pop();

            // Draw labels
            fill(0);
            noStroke();
            textAlign(CENTER, CENTER);
            textSize(14);
            text('x₁', width/2, height - 10);

            push();
            translate(10, height/2);
            rotate(-PI/2);
            text('y₁', 0, 0);
            pop();

            // Draw tick labels
            textSize(12);
            textAlign(CENTER, TOP);
            for (let i = 0; i <= 5; i++) {
                text((i/5).toFixed(1), 40 + i * 64, height - 30);
            }

            textAlign(RIGHT, CENTER);
            for (let i = 0; i <= 5; i++) {
                text((i/5).toFixed(1), 35, height - 40 - i * 64);
            }
        }

        // Initialize
        window.addEventListener('DOMContentLoaded', () => {
            setupSliders();

            // Configure MathJax
            window.MathJax = {
                tex: {
                    inlineMath: [['\\(', '\\)']],
                    displayMath: [['\\[', '\\]']]
                }
            };
        });
    </script>
</body>
</html>
