</head>
<body>
    <div id="demo-container">
        <div id="controls-container">
            <div id="slider-group">
                <label for="slider-t">choose quadratic</label>
                <input type="range" id="slider-t" min="-5" max="5" step="0.01" value="0.5">
                <input type="number" id="input-t" min="-5" max="5" step="0.01" value="0.5">
                <button id="btn-reset" style="font-family: 'Courier New', monospace;">↻</button>
            </div>
            <div id="checkbox-group">
                <input type="checkbox" id="checkbox-family">
                <label for="checkbox-family">family</label>
            </div>
        </div>
        <div id="canvas-container"></div>
    </div>

    <script>
        let slider, input, checkbox, resetBtn;
        let locators = [
            { x: -4, y: 1 },
            { x: -2, y: 1.8 },
            { x: 1, y: -1 },
            { x: 4, y: -1 }
        ];
        let defaultLocators = JSON.parse(JSON.stringify(locators));
        let selectedLocator = -1;
        let cubicCoeffs = { a: 0, b: 0, c: 0, d: 0 };
        let scale = 50;
        let familyColors = [
            [255, 107, 107, 100], [255, 193, 7, 100], [76, 175, 80, 100],
            [33, 150, 243, 100], [156, 39, 176, 100], [96, 125, 139, 100],
            [255, 152, 0, 100], [0, 188, 212, 100], [139, 195, 74, 100],
            [103, 58, 183, 100]
        ];

        function setup() {
            let canvas = createCanvas(600, 500);
            canvas.parent('canvas-container');

            slider = select('#slider-t');
            input = select('#input-t');
            checkbox = select('#checkbox-family');
            resetBtn = select('#btn-reset');

            slider.input(onSliderChange);
            input.input(onInputChange);
            checkbox.changed(redrawCanvas);
            resetBtn.mousePressed(resetDemo);

            updateCubicCoefficients();
            redrawCanvas();
        }

        function draw() {
            // All drawing happens in redrawCanvas
        }

        function redrawCanvas() {
            background(255);
            push();
            translate(width / 2, height / 2);
            scale(1, -1);

            drawAxes();

            if (checkbox.checked()) {
                drawQuadraticFamily();
            } else {
                drawCubic();
            }

            drawCurrentQuadratic();
            drawLocators();

            pop();
        }

        function drawAxes() {
            stroke(200);
            strokeWeight(1);

            // X-axis
            line(-width/2, 0, width/2, 0);
            // Y-axis
            line(0, -height/2, 0, height/2);

            // Tick marks and labels
            push();
            scale(1, -1);
            textAlign(CENTER, TOP);
            fill(100);
            noStroke();

            for (let x = -6; x <= 6; x++) {
                if (x !== 0) {
                    stroke(200);
                    strokeWeight(1);
                    line(x * scale, -5, x * scale, 5);
                    noStroke();
                    text(x, x * scale, 10);
                }
            }

            textAlign(RIGHT, CENTER);
            for (let y = -5; y <= 5; y++) {
                if (y !== 0) {
                    stroke(200);
                    strokeWeight(1);
                    line(-5, -y * scale, 5, -y * scale);
                    noStroke();
                    text(y, -10, -y * scale);
                }
            }
            pop();
        }

        function drawCubic() {
            stroke(63, 81, 181);
            strokeWeight(3);
            noFill();

            beginShape();
            for (let px = -width/2; px <= width/2; px++) {
                let x = px / scale;
                let y = cubicCoeffs.a * x * x * x + cubicCoeffs.b * x * x + 
                       cubicCoeffs.c * x + cubicCoeffs.d;
                vertex(px, y * scale);
            }
            endShape();
        }

        function drawCurrentQuadratic() {
            let t = parseFloat(slider.value());
            let quadCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);

            stroke(245, 124, 0);
            strokeWeight(3);
            noFill();

            beginShape();
            for (let px = -width/2; px <= width/2; px++) {
                let x = px / scale;
                let y = quadCoeffs.p * x * x + quadCoeffs.q * x + quadCoeffs.r;
                vertex(px, y * scale);
            }
            endShape();
        }

        function drawQuadraticFamily() {
            let steps = 20;
            let tMin = -5;
            let tMax = 5;
            let tStep = (tMax - tMin) / steps;

            for (let i = 0; i <= steps; i++) {
                let t = tMin + i * tStep;
                let quadCoeffs = getOsculatingQuadraticCoeffs(t, cubicCoeffs);
                let colorIndex = i % familyColors.length;

                fill(familyColors[colorIndex]);
                noStroke();

                beginShape();
                let boundary = quadCoeffs.p > 0 ? -height/2 : height/2;
                vertex(-width/2, boundary);

                for (let px = -width/2; px <= width/2; px++) {
                    let x = px / scale;
                    let y = quadCoeffs.p * x * x + quadCoeffs.q * x + quadCoeffs.r;
                    vertex(px, y * scale);
                }

                vertex(width/2, boundary);
                endShape(CLOSE);
            }
        }

        function drawLocators() {
            for (let i = 0; i < locators.length; i++) {
                push();
                translate(locators[i].x * scale, locators[i].y * scale);

                fill(255);
                stroke(0);
                strokeWeight(2);
                circle(0, 0, 16);

                strokeWeight(1);
                line(-6, 0, 6, 0);
                line(0, -6, 0, 6);

                pop();
            }
        }

        function mousePressed() {
            let mx = (mouseX - width/2) / scale;
            let my = -(mouseY - height/2) / scale;

            for (let i = 0; i < locators.length; i++) {
                let d = dist(mx, my, locators[i].x, locators[i].y);
                if (d < 0.3) {
                    selectedLocator = i;
                    break;
                }
            }
        }

        function mouseDragged() {
            if (selectedLocator >= 0) {
                locators[selectedLocator].x = (mouseX - width/2) / scale;
                locators[selectedLocator].y = -(mouseY - height/2) / scale;
                updateCubicCoefficients();
                redrawCanvas();
            }
        }

        function mouseReleased() {
            selectedLocator = -1;
        }

        function onSliderChange() {
            input.value(slider.value());
            redrawCanvas();
        }

        function onInputChange() {
            slider.value(input.value());
            redrawCanvas();
        }

        function resetDemo() {
            slider.value(0.5);
            input.value(0.5);
            checkbox.checked(false);
            locators = JSON.parse(JSON.stringify(defaultLocators));
            updateCubicCoefficients();
            redrawCanvas();
        }

        function updateCubicCoefficients() {
            cubicCoeffs = solveCubicCoefficients(locators);
        }

        function solveCubicCoefficients(points) {
            // Solve the system: M * [a, b, c, d]^T = [y1, y2, y3, y4]^T
            // where M[i][j] = x[i]^(3-j)

            let n = 4;
            let M = [];
            let y = [];

            for (let i = 0; i < n; i++) {
                M[i] = [
                    Math.pow(points[i].x, 3),
                    Math.pow(points[i].x, 2),
                    points[i].x,
                    1
                ];
                y[i] = points[i].y;
            }

            // Gaussian elimination
            for (let i = 0; i < n; i++) {
                // Find pivot
                let maxRow = i;
                for (let k = i + 1; k < n; k++) {
                    if (Math.abs(M[k][i]) > Math.abs(M[maxRow][i])) {
                        maxRow = k;
                    }
                }

                // Swap rows
                [M[i], M[maxRow]] = [M[maxRow], M[i]];
                [y[i], y[maxRow]] = [y[maxRow], y[i]];

                // Make all rows below this one 0 in current column
                for (let k = i + 1; k < n; k++) {
                    let c = M[k][i] / M[i][i];
                    for (let j = i; j < n; j++) {
                        if (i === j) {
                            M[k][j] = 0;
                        } else {
                            M[k][j] -= c * M[i][j];
                        }
                    }
                    y[k] -= c * y[i];
                }
            }

            // Back substitution
            let x = new Array(n);
            for (let i = n - 1; i >= 0; i--) {
                x[i] = y[i] / M[i][i];
                for (let k = i - 1; k >= 0; k--) {
                    y[k] -= M[k][i] * x[i];
                }
            }

            return { a: x[0], b: x[1], c: x[2], d: x[3] };
        }

        function getOsculatingQuadraticCoeffs(t, cubicCoeffs) {
            // p = 3at + b
            // q = -3at² + c
            // r = at³ + d
            return {
                p: 3 * cubicCoeffs.a * t + cubicCoeffs.b,
                q: -3 * cubicCoeffs.a * t * t + cubicCoeffs.c,
                r: cubicCoeffs.a * t * t * t + cubicCoeffs.d
            };
        }
    </script>
</body>
</html>
