<body>
    <div id="demo-container">
        <div id="control-panel">
            <div>
                <label for="slider-v0">initial volume v₀</label>
                <input type="range" id="slider-v0" min="100" max="500" value="211" step="1">
                <span id="v0-value">211</span>
            </div>
            <div>
                <label for="slider-T">temperature T</label>
                <input type="range" id="slider-T" min="-273" max="727" value="270" step="1">
                <span id="T-value">270°C</span>
            </div>
            <div>
                <label>temperature units</label>
                <div class="radio-group">
                    <input type="radio" id="radio-kelvin" name="temp-units" value="K">
                    <label for="radio-kelvin">Kelvin</label>
                    <input type="radio" id="radio-celsius" name="temp-units" value="C" checked>
                    <label for="radio-celsius">Celsius</label>
                </div>
            </div>
        </div>
        <div id="visualization-area">
            <div id="plot-container"></div>
            <div id="piston-container"></div>
        </div>
    </div>

    <script>
        // Global variables
        let v0 = 211;
        let T_current = 270; // in current units
        let isCelsius = true;
        let k = v0 / 273.15;
        let V_current = 0;
        let pistonSketch;

        // Temperature conversion functions
        function celsiusToKelvin(c) {
            return c + 273.15;
        }

        function kelvinToCelsius(k) {
            return k - 273.15;
        }

        // Get temperature in Kelvin
        function getTemperatureInKelvin() {
            return isCelsius ? celsiusToKelvin(T_current) : T_current;
        }

        // Calculate volume based on Charles's Law
        function calculateVolume() {
            const T_K = getTemperatureInKelvin();
            return k * T_K;
        }

        // Temperature to color mapping
        function temperatureToColor(T_K) {
            const normalized = T_K / 1000;
            const r = Math.min(255, Math.floor(255 * normalized * 2));
            const g = Math.min(255, Math.floor(255 * (1 - Math.abs(normalized - 0.5) * 2)));
            const b = Math.max(0, Math.floor(128 * (1 - normalized * 2)));
            return `rgb(${r},${g},${b})`;
        }

        // Update plot
        function updatePlot() {
            const xMin = isCelsius ? -273 : 0;
            const xMax = isCelsius ? 727 : 1000;
            const numPoints = 100;

            let xData = [];
            let yData = [];
            let colors = [];

            for (let i = 0; i <= numPoints; i++) {
                const x = xMin + (xMax - xMin) * i / numPoints;
                const T_K = isCelsius ? celsiusToKelvin(x) : x;
                if (T_K > 0) {
                    xData.push(x);
                    yData.push(k * T_K);
                    colors.push(temperatureToColor(T_K));
                }
            }

            const T_K_current = getTemperatureInKelvin();
            const markerColor = temperatureToColor(T_K_current);

            const lineTrace = {
                x: xData,
                y: yData,
                mode: 'lines',
                line: {
                    color: colors[0],
                    width: 3
                },
                showlegend: false
            };

            const markerTrace = {
                x: [T_current],
                y: [V_current],
                mode: 'markers',
                marker: {
                    size: 12,
                    color: markerColor
                },
                showlegend: false
            };

            const layout = {
                xaxis: {
                    title: isCelsius ? 'temperature °C' : 'temperature K',
                    range: [xMin, xMax]
                },
                yaxis: {
                    title: 'volume',
                    range: [0, 2000]
                },
                margin: { t: 20, r: 20, b: 50, l: 60 }
            };

            Plotly.newPlot('plot-container', [lineTrace, markerTrace], layout);
        }

        // p5.js sketch for piston animation
        const pistonAnimation = function(p) {
            let canvasWidth = 400;
            let canvasHeight = 480;

            p.setup = function() {
                p.createCanvas(canvasWidth, canvasHeight);
            };

            p.draw = function() {
                p.background(255);

                // Constants for cylinder
                const cylinderLeft = 100;
                const cylinderRight = 300;
                const cylinderBottom = 400;
                const cylinderTop = 80;
                const cylinderWidth = cylinderRight - cylinderLeft;

                // Calculate piston position based on volume
                const maxVolume = 2000;
                const minVolume = 0;
                const volumeRatio = (V_current - minVolume) / (maxVolume - minVolume);
                const pistonY = cylinderBottom - volumeRatio * (cylinderBottom - cylinderTop - 40);

                // Draw gas
                const T_K = getTemperatureInKelvin();
                const gasColor = p.lerpColor(
                    p.color(128, 0, 128),
                    T_K < 500 ? p.color(255, 255, 0) : p.color(255, 0, 0),
                    T_K < 500 ? T_K / 500 : (T_K - 500) / 500
                );
                p.fill(gasColor);
                p.noStroke();
                p.rect(cylinderLeft, pistonY, cylinderWidth, cylinderBottom - pistonY);

                // Draw cylinder walls
                p.stroke(0, 0, 255);
                p.strokeWeight(3);
                p.line(cylinderLeft, cylinderTop, cylinderLeft, cylinderBottom);
                p.line(cylinderRight, cylinderTop, cylinderRight, cylinderBottom);
                p.line(cylinderLeft, cylinderBottom, cylinderRight, cylinderBottom);

                // Draw piston
                p.fill(150);
                p.stroke(0);
                p.strokeWeight(2);
                p.rect(cylinderLeft - 10, pistonY - 5, cylinderWidth + 20, 10);
                p.rect(cylinderLeft + cylinderWidth/2 - 20, pistonY - 45, 40, 40);

                // Draw pressure arrows
                p.stroke(255, 140, 0);
                p.strokeWeight(2);
                for (let i = 0; i < 5; i++) {
                    const arrowX = cylinderLeft + 20 + i * 40;
                    const arrowY = pistonY - 50;
                    p.line(arrowX, arrowY - 20, arrowX, arrowY);
                    p.line(arrowX - 5, arrowY - 5, arrowX, arrowY);
                    p.line(arrowX + 5, arrowY - 5, arrowX, arrowY);
                }
            };
        };

        // Initialize
        document.addEventListener('DOMContentLoaded', function() {
            // Initial calculations
            V_current = calculateVolume();

            // Create p5 sketch
            pistonSketch = new p5(pistonAnimation, 'piston-container');

            // Initial plot
            updatePlot();

            // Event listeners
            document.getElementById('slider-v0').addEventListener('input', function(e) {
                v0 = parseFloat(e.target.value);
                document.getElementById('v0-value').textContent = v0;
                k = v0 / 273.15;
                V_current = calculateVolume();
                updatePlot();
            });

            document.getElementById('slider-T').addEventListener('input', function(e) {
                T_current = parseFloat(e.target.value);
                const unit = isCelsius ? '°C' : 'K';
                document.getElementById('T-value').textContent = T_current + unit;
                V_current = calculateVolume();
                updatePlot();
            });

            document.getElementById('radio-kelvin').addEventListener('change', function() {
                if (this.checked) {
                    // Convert from Celsius to Kelvin
                    T_current = celsiusToKelvin(T_current);
                    isCelsius = false;
                    document.getElementById('slider-T').min = 0;
                    document.getElementById('slider-T').max = 1000;
                    document.getElementById('slider-T').value = T_current;
                    document.getElementById('T-value').textContent = Math.round(T_current) + 'K';
                    updatePlot();
                }
            });

            document.getElementById('radio-celsius').addEventListener('change', function() {
                if (this.checked) {
                    // Convert from Kelvin to Celsius
                    T_current = kelvinToCelsius(T_current);
                    isCelsius = true;
                    document.getElementById('slider-T').min = -273;
                    document.getElementById('slider-T').max = 727;
                    document.getElementById('slider-T').value = T_current;
                    document.getElementById('T-value').textContent = Math.round(T_current) + '°C';
                    updatePlot();
                }
            });
        });
    </script>
</body>
</html>
