    <script>
        let angleA = 306;
        let angleB = 54;
        let angleC = 180;
        let selectedVertex = 'C';
        let draggingVertex = null;

        const centerX = 200;
        const centerY = 200;
        const radius = 150;

        function setup() {
            let canvas = createCanvas(400, 400);
            canvas.parent('canvas-container');
            angleMode(DEGREES);

            // Set up event listeners
            document.getElementById('slider-a').addEventListener('input', function() {
                angleA = parseFloat(this.value);
                updateVisualization();
            });

            document.getElementById('slider-b').addEventListener('input', function() {
                angleB = parseFloat(this.value);
                updateVisualization();
            });

            document.getElementById('slider-c').addEventListener('input', function() {
                angleC = parseFloat(this.value);
                updateVisualization();
            });

            document.querySelectorAll('input[name="vertex-select"]').forEach(radio => {
                radio.addEventListener('change', function() {
                    selectedVertex = this.value;
                    updateVisualization();
                });
            });

            updateVisualization();
        }

        function draw() {
            background(255);

            // Draw colored sectors
            push();
            noStroke();

            // Arc AB sector (green)
            fill(224, 240, 224);
            arc(centerX, centerY, radius * 2, radius * 2, angleA, angleB, PIE);

            // Arc BC sector (yellow)
            fill(245, 245, 213);
            arc(centerX, centerY, radius * 2, radius * 2, angleB, angleC, PIE);

            // Arc CA sector (blue)
            fill(224, 232, 248);
            arc(centerX, centerY, radius * 2, radius * 2, angleC, angleA, PIE);
            pop();

            // Draw circle
            push();
            fill(170, 204, 222);
            stroke(102, 102, 102);
            strokeWeight(2);
            circle(centerX, centerY, radius * 2);
            pop();

            // Draw center point
            push();
            fill(102, 102, 102);
            noStroke();
            circle(centerX, centerY, 8);
            pop();

            // Calculate vertex positions
            let xA = centerX + radius * cos(angleA);
            let yA = centerY + radius * sin(angleA);
            let xB = centerX + radius * cos(angleB);
            let yB = centerY + radius * sin(angleB);
            let xC = centerX + radius * cos(angleC);
            let yC = centerY + radius * sin(angleC);

            // Draw triangle
            push();
            fill(173, 216, 230, 127);
            stroke(102, 102, 102);
            strokeWeight(2);
            beginShape();
            vertex(xA, yA);
            vertex(xB, yB);
            vertex(xC, yC);
            endShape(CLOSE);
            pop();

            // Draw vertex labels
            push();
            textAlign(CENTER, CENTER);
            textSize(20);
            fill(0, 0, 139);
            noStroke();
            textStyle(BOLD);

            let labelDist = 25;
            text('A', xA + labelDist * cos(angleA), yA + labelDist * sin(angleA));
            text('B', xB + labelDist * cos(angleB), yB + labelDist * sin(angleB));
            text('C', xC + labelDist * cos(angleC), yC + labelDist * sin(angleC));
            pop();

            // Draw highlighted angles if a vertex is selected
            if (selectedVertex !== 'None') {
                drawHighlightedAngles();
            }
        }

        function drawHighlightedAngles() {
            let arcStart, arcEnd, vertexX, vertexY, vertexAngle, arcAngle;

            if (selectedVertex === 'A') {
                arcStart = angleB;
                arcEnd = angleC;
                vertexX = centerX + radius * cos(angleA);
                vertexY = centerY + radius * sin(angleA);
                vertexAngle = angleA;
            } else if (selectedVertex === 'B') {
                arcStart = angleC;
                arcEnd = angleA;
                vertexX = centerX + radius * cos(angleB);
                vertexY = centerY + radius * sin(angleB);
                vertexAngle = angleB;
            } else if (selectedVertex === 'C') {
                arcStart = angleA;
                arcEnd = angleB;
                vertexX = centerX + radius * cos(angleC);
                vertexY = centerY + radius * sin(angleC);
                vertexAngle = angleC;
            }

            arcAngle = (arcEnd - arcStart + 360) % 360;

            // Draw central angle (θ)
            push();
            stroke(0, 128, 0);
            strokeWeight(3);
            noFill();
            arc(centerX, centerY, 80, 80, arcStart, arcEnd);

            // Draw arrowhead
            let arrowAngle = arcEnd;
            let arrowX = centerX + 40 * cos(arrowAngle);
            let arrowY = centerY + 40 * sin(arrowAngle);
            push();
            translate(arrowX, arrowY);
            rotate(arrowAngle + 90);
            fill(0, 128, 0);
            noStroke();
            triangle(0, -5, -5, 5, 5, 5);
            pop();

            // Draw θ label
            textAlign(CENTER, CENTER);
            fill(0, 128, 0);
            noStroke();
            textSize(16);
            let labelAngle = arcStart + arcAngle / 2;
            text('θ = ' + arcAngle.toFixed(0) + '°', 
                 centerX + 60 * cos(labelAngle), 
                 centerY + 60 * sin(labelAngle));
            pop();

            // Draw inscribed angle (θ/2)
            push();
            stroke(255, 0, 0);
            strokeWeight(3);
            noFill();

            let inscribedStart = vertexAngle - arcAngle / 4;
            let inscribedEnd = vertexAngle + arcAngle / 4;

            arc(vertexX, vertexY, 60, 60, inscribedStart, inscribedEnd);

            // Draw θ/2 label
            textAlign(CENTER, CENTER);
            fill(255, 0, 0);
            noStroke();
            textSize(16);
            text('θ/2 = ' + (arcAngle / 2).toFixed(1) + '°', 
                 vertexX + 50 * cos(vertexAngle), 
                 vertexY + 50 * sin(vertexAngle));
            pop();
        }

        function mousePressed() {
            let vertices = [
                {name: 'A', x: centerX + radius * cos(angleA), y: centerY + radius * sin(angleA)},
                {name: 'B', x: centerX + radius * cos(angleB), y: centerY + radius * sin(angleB)},
                {name: 'C', x: centerX + radius * cos(angleC), y: centerY + radius * sin(angleC)}
            ];

            for (let v of vertices) {
                if (dist(mouseX, mouseY, v.x, v.y) < 20) {
                    draggingVertex = v.name;
                    break;
                }
            }
        }

        function mouseDragged() {
            if (draggingVertex) {
                let angle = atan2(mouseY - centerY, mouseX - centerX);
                angle = (angle + 360) % 360;

                if (draggingVertex === 'A') {
                    angleA = angle;
                    document.getElementById('slider-a').value = angle;
                } else if (draggingVertex === 'B') {
                    angleB = angle;
                    document.getElementById('slider-b').value = angle;
                } else if (draggingVertex === 'C') {
                    angleC = angle;
                    document.getElementById('slider-c').value = angle;
                }

                updateVisualization();
            }
        }

        function mouseReleased() {
            draggingVertex = null;
        }

        function updateVisualization() {
            // Calculate arc lengths
            let arcAB = (angleB - angleA + 360) % 360;
            let arcBC = (angleC - angleB + 360) % 360;
            let arcCA = (angleA - angleC + 360) % 360;

            // Calculate vertex angles
            let vertexA = arcBC / 2;
            let vertexB = arcCA / 2;
            let vertexC = arcAB / 2;

            // Update info panel
            if (selectedVertex === 'None') {
                // Show all values
                document.getElementById('arc-label-1').textContent = 'Arc AB';
                document.getElementById('arc-value-1').textContent = arcAB.toFixed(1) + '°';
                document.getElementById('vertex-label-1').textContent = 'Vertex C';
                document.getElementById('vertex-value-1').textContent = vertexC.toFixed(1) + '°';

                document.getElementById('arc-label-2').textContent = 'Arc BC';
                document.getElementById('arc-value-2').textContent = arcBC.toFixed(1) + '°';
                document.getElementById('vertex-label-2').textContent = 'Vertex A';
                document.getElementById('vertex-value-2').textContent = vertexA.toFixed(1) + '°';

                document.getElementById('arc-label-3').textContent = 'Arc CA';
                document.getElementById('arc-value-3').textContent = arcCA.toFixed(1) + '°';
                document.getElementById('vertex-label-3').textContent = 'Vertex B';
                document.getElementById('vertex-value-3').textContent = vertexB.toFixed(1) + '°';

                document.getElementById('arc-total').textContent = '360°';
                document.getElementById('vertex-total').textContent = '180°';
            } else {
                // Show only selected vertex
                let arcLabel, arcValue, vertexLabel, vertexValue;

                if (selectedVertex === 'A') {
                    arcLabel = 'Arc BC';
                    arcValue = arcBC.toFixed(1) + '°';
                    vertexLabel = 'Vertex A';
                    vertexValue = vertexA.toFixed(1) + '°';
                } else if (selectedVertex === 'B') {
                    arcLabel = 'Arc CA';
                    arcValue = arcCA.toFixed(1) + '°';
                    vertexLabel = 'Vertex B';
                    vertexValue = vertexB.toFixed(1) + '°';
                } else if (selectedVertex === 'C') {
                    arcLabel = 'Arc AB';
                    arcValue = arcAB.toFixed(1) + '°';
                    vertexLabel = 'Vertex C';
                    vertexValue = vertexC.toFixed(1) + '°';
                }

                document.getElementById('arc-label-1').textContent = arcLabel;
                document.getElementById('arc-value-1').textContent = arcValue;
                document.getElementById('vertex-label-1').textContent = vertexLabel;
                document.getElementById('vertex-value-1').textContent = vertexValue;

                document.getElementById('arc-label-2').textContent = '';
                document.getElementById('arc-value-2').textContent = '';
                document.getElementById('vertex-label-2').textContent = '';
                document.getElementById('vertex-value-2').textContent = '';

                document.getElementById('arc-label-3').textContent = '';
                document.getElementById('arc-value-3').textContent = '';
                document.getElementById('vertex-label-3').textContent = '';
                document.getElementById('vertex-value-3').textContent = '';

                document.getElementById('arc-total').textContent = '?';
                document.getElementById('vertex-total').textContent = '?';
            }
        }
    </script>
</body>
</html>
