<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>PDE Reaction-Diffusion Visualization</title>
    <script src="https://cdn.plot.ly/plotly-latest.min.js"></script>
    <style>
        body {
            margin: 0;
            padding: 0;
            font-family: sans-serif;
        }
        .control-group {
            margin-bottom: 15px;
        }
        .control-group label {
            display: inline-block;
            width: 30px;
            font-size: 16px;
        }
        .control-group button {
            width: 30px;
            height: 30px;
            font-size: 16px;
            cursor: pointer;
        }
        .control-group input[type="range"] {
            width: 100px;
            vertical-align: middle;
        }
        .control-group span {
            display: inline-block;
            width: 50px;
            text-align: right;
            font-family: monospace;
        }
    </style>
</head>
<body>
    <div id="main-container" style="display: flex; font-family: sans-serif;">
        <div id="control-panel" style="width: 250px; padding: 20px;">
            <h3>Parameters</h3>

            <div class="control-group">
                <label>&tau;</label>
                <button id="btn-dec-tau">-</button>
                <input type="range" id="slider-tau" min="0.01" max="5" step="0.01" value="1.0">
                <button id="btn-inc-tau">+</button>
                <span id="value-tau">1.00</span>
            </div>

            <div class="control-group">
                <label>&phi;</label>
                <button id="btn-dec-phi">-</button>
                <input type="range" id="slider-phi" min="0.1" max="10" step="0.1" value="2.0">
                <button id="btn-inc-phi">+</button>
                <span id="value-phi">2.0</span>
            </div>

            <div class="control-group">
                <label>&beta;</label>
                <button id="btn-dec-beta">-</button>
                <input type="range" id="slider-beta" min="-0.5" max="0.5" step="0.01" value="0.2">
                <button id="btn-inc-beta">+</button>
                <span id="value-beta">0.20</span>
            </div>

            <div class="control-group">
                <label>&gamma;</label>
                <button id="btn-dec-gamma">-</button>
                <input type="range" id="slider-gamma" min="0" max="40" step="1" value="20">
                <button id="btn-inc-gamma">+</button>
                <span id="value-gamma">20</span>
            </div>

            <div class="control-group">
                <label>&xi;</label>
                <button id="btn-dec-xi">-</button>
                <input type="range" id="slider-xi" min="0.001" max="1.0" step="0.001" value="0.4">
                <button id="btn-inc-xi">+</button>
                <span id="value-xi">0.400</span>
            </div>
        </div>

        <div id="visualization-panel" style="flex-grow: 1;">
            <div id="plot-concentration" style="height: 400px;"></div>
            <div id="plot-temperature" style="height: 400px;"></div>
        </div>
    </div>

    <script>
        // Grid parameters
        const N = 100;
        const dxi = 1.0 / (N - 1);
        const xi_grid = Array.from({length: N}, (_, i) => i * dxi);

        // Global variables for current state
        let c_profile = new Array(N).fill(0);
        let theta_profile = new Array(N).fill(1);

        // PDE solver using RK4
        function solvePDE(tau_final, phi, beta, gamma) {
            // Initialize arrays
            let c = new Array(N).fill(0);
            let theta = new Array(N).fill(1);

            // Set boundary conditions at xi=1
            c[N-1] = 1;
            theta[N-1] = 1;

            // Time stepping parameters
            const dt = 0.0001;
            const n_steps = Math.ceil(tau_final / dt);
            const dt_actual = tau_final / n_steps;

            // RK4 integration
            for (let step = 0; step < n_steps; step++) {
                // Compute RK4 stages
                const [k1_c, k1_theta] = computeDerivatives(c, theta, phi, beta, gamma);

                const c_temp1 = c.map((val, i) => val + 0.5 * dt_actual * k1_c[i]);
                const theta_temp1 = theta.map((val, i) => val + 0.5 * dt_actual * k1_theta[i]);
                const [k2_c, k2_theta] = computeDerivatives(c_temp1, theta_temp1, phi, beta, gamma);

                const c_temp2 = c.map((val, i) => val + 0.5 * dt_actual * k2_c[i]);
                const theta_temp2 = theta.map((val, i) => val + 0.5 * dt_actual * k2_theta[i]);
                const [k3_c, k3_theta] = computeDerivatives(c_temp2, theta_temp2, phi, beta, gamma);

                const c_temp3 = c.map((val, i) => val + dt_actual * k3_c[i]);
                const theta_temp3 = theta.map((val, i) => val + dt_actual * k3_theta[i]);
                const [k4_c, k4_theta] = computeDerivatives(c_temp3, theta_temp3, phi, beta, gamma);

                // Update solution
                for (let i = 0; i < N - 1; i++) {
                    c[i] += dt_actual * (k1_c[i] + 2*k2_c[i] + 2*k3_c[i] + k4_c[i]) / 6;
                    theta[i] += dt_actual * (k1_theta[i] + 2*k2_theta[i] + 2*k3_theta[i] + k4_theta[i]) / 6;
                }
            }

            return [c, theta];
        }

        function computeDerivatives(c, theta, phi, beta, gamma) {
            const dc_dt = new Array(N).fill(0);
            const dtheta_dt = new Array(N).fill(0);

            // Interior points (central differences)
            for (let i = 1; i < N - 1; i++) {
                const d2c_dxi2 = (c[i+1] - 2*c[i] + c[i-1]) / (dxi * dxi);
                const d2theta_dxi2 = (theta[i+1] - 2*theta[i] + theta[i-1]) / (dxi * dxi);

                const reaction = phi * phi * c[i] * Math.exp(gamma * (1 - 1/theta[i]));

                dc_dt[i] = d2c_dxi2 - reaction;
                dtheta_dt[i] = d2theta_dxi2 + beta * reaction;
            }

            // Boundary conditions at xi=0 (symmetry)
            dc_dt[0] = 2 * (c[1] - c[0]) / (dxi * dxi) - phi * phi * c[0] * Math.exp(gamma * (1 - 1/theta[0]));
            dtheta_dt[0] = 2 * (theta[1] - theta[0]) / (dxi * dxi) + beta * phi * phi * c[0] * Math.exp(gamma * (1 - 1/theta[0]));

            // Boundary conditions at xi=1 (fixed)
            dc_dt[N-1] = 0;
            dtheta_dt[N-1] = 0;

            return [dc_dt, dtheta_dt];
        }

        // Linear interpolation
        function interpolate(xi_val, profile) {
            const idx = xi_val / dxi;
            const i = Math.floor(idx);
            const frac = idx - i;

            if (i >= N - 1) return profile[N-1];
            if (i < 0) return profile[0];

            return profile[i] * (1 - frac) + profile[i+1] * frac;
        }

        // Update plots
        function updatePlots() {
            const tau = parseFloat(document.getElementById('slider-tau').value);
            const xi = parseFloat(document.getElementById('slider-xi').value);

            const c_at_xi = interpolate(xi, c_profile);
            const theta_at_xi = interpolate(xi, theta_profile);

            // Concentration plot
            const c_trace = {
                x: xi_grid,
                y: c_profile,
                type: 'scatter',
                mode: 'lines',
                line: {color: 'blue', width: 2},
                name: 'c(ξ)'
            };

            const c_marker = {
                x: [xi, xi, 0],
                y: [0, c_at_xi, c_at_xi],
                type: 'scatter',
                mode: 'lines',
                line: {color: '#333333', width: 2, dash: 'dot'},
                showlegend: false
            };

            const c_layout = {
                xaxis: {title: 'ξ', range: [0, 1]},
                yaxis: {title: 'c', range: [0, 1.1]},
                margin: {t: 20, r: 20, b: 40, l: 40},
                annotations: [{
                    x: 0.7,
                    y: 0.8,
                    xref: 'paper',
                    yref: 'paper',
                    text: `τ = ${tau.toFixed(2)}<br>ξ = ${xi.toFixed(3)}<br>c = ${c_at_xi.toFixed(5)}`,
                    showarrow: false,
                    font: {family: 'monospace', size: 12},
                    bgcolor: 'rgba(255,255,255,0.8)',
                    borderpad: 4
                }],
                showlegend: false
            };

            Plotly.react('plot-concentration', [c_trace, c_marker], c_layout);

            // Temperature plot
            const theta_min = Math.min(...theta_profile);
            const theta_max = Math.max(...theta_profile);

            const theta_trace = {
                x: xi_grid,
                y: theta_profile,
                type: 'scatter',
                mode: 'lines',
                line: {color: 'red', width: 2},
                name: 'θ(ξ)'
            };

            const theta_marker = {
                x: [xi, xi, 0],
                y: [theta_min, theta_at_xi, theta_at_xi],
                type: 'scatter',
                mode: 'lines',
                line: {color: '#333333', width: 2, dash: 'dot'},
                showlegend: false
            };

            const theta_layout = {
                xaxis: {title: 'ξ', range: [0, 1]},
                yaxis: {title: 'θ', autorange: true},
                margin: {t: 20, r: 20, b: 40, l: 40},
                annotations: [{
                    x: 0.7,
                    y: 0.8,
                    xref: 'paper',
                    yref: 'paper',
                    text: `τ = ${tau.toFixed(2)}<br>ξ = ${xi.toFixed(3)}<br>θ = ${theta_at_xi.toFixed(5)}`,
                    showarrow: false,
                    font: {family: 'monospace', size: 12},
                    bgcolor: 'rgba(255,255,255,0.8)',
                    borderpad: 4
                }],
                showlegend: false
            };

            Plotly.react('plot-temperature', [theta_trace, theta_marker], theta_layout);
        }

        // Update marker position only
        function updateMarkerOnly() {
            const tau = parseFloat(document.getElementById('slider-tau').value);
            const xi = parseFloat(document.getElementById('slider-xi').value);

            const c_at_xi = interpolate(xi, c_profile);
            const theta_at_xi = interpolate(xi, theta_profile);
            const theta_min = Math.min(...theta_profile);

            // Update concentration marker
            Plotly.restyle('plot-concentration', {
                x: [[xi, xi, 0]],
                y: [[0, c_at_xi, c_at_xi]]
            }, 1);

            Plotly.relayout('plot-concentration', {
                'annotations[0].text': `τ = ${tau.toFixed(2)}<br>ξ = ${xi.toFixed(3)}<br>c = ${c_at_xi.toFixed(5)}`
            });

            // Update temperature marker
            Plotly.restyle('plot-temperature', {
                x: [[xi, xi, 0]],
                y: [[theta_min, theta_at_xi, theta_at_xi]]
            }, 1);

            Plotly.relayout('plot-temperature', {
                'annotations[0].text': `τ = ${tau.toFixed(2)}<br>ξ = ${xi.toFixed(3)}<br>θ = ${theta_at_xi.toFixed(5)}`
            });
        }

        // Run simulation and update plots
        function runSimulation() {
            const tau = parseFloat(document.getElementById('slider-tau').value);
            const phi = parseFloat(document.getElementById('slider-phi').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);
            const gamma = parseFloat(document.getElementById('slider-gamma').value);

            [c_profile, theta_profile] = solvePDE(tau, phi, beta, gamma);
            updatePlots();
        }

        // Event handlers
        function setupEventHandlers() {
            // Slider input handlers
            ['tau', 'phi', 'beta', 'gamma'].forEach(param => {
                const slider = document.getElementById(`slider-${param}`);
                const value = document.getElementById(`value-${param}`);

                slider.addEventListener('input', function() {
                    value.textContent = parseFloat(this.value).toFixed(param === 'phi' ? 1 : param === 'gamma' ? 0 : 2);
                    runSimulation();
                });
            });

            // Xi slider (marker only)
            const xiSlider = document.getElementById('slider-xi');
            const xiValue = document.getElementById('value-xi');

            xiSlider.addEventListener('input', function() {
                xiValue.textContent = parseFloat(this.value).toFixed(3);
                updateMarkerOnly();
            });

            // Increment/decrement buttons
            ['tau', 'phi', 'beta', 'gamma', 'xi'].forEach(param => {
                const slider = document.getElementById(`slider-${param}`);
                const decBtn = document.getElementById(`btn-dec-${param}`);
                const incBtn = document.getElementById(`btn-inc-${param}`);

                decBtn.addEventListener('click', function() {
                    slider.value = Math.max(parseFloat(slider.min), parseFloat(slider.value) - parseFloat(slider.step));
                    slider.dispatchEvent(new Event('input'));
                });

                incBtn.addEventListener('click', function() {
                    slider.value = Math.min(parseFloat(slider.max), parseFloat(slider.value) + parseFloat(slider.step));
                    slider.dispatchEvent(new Event('input'));
                });
            });
        }

        // Initialize on page load
        window.addEventListener('DOMContentLoaded', function() {
            setupEventHandlers();
            runSimulation();
        });
    </script>
</body>
</html>
