                        <tr><td>35</td><td>445</td></tr>
                        <tr><td>40</td><td>565</td></tr>
                        <tr><td>45</td><td>704</td></tr>
                        <tr><td>50</td><td>833</td></tr>
                        <tr><td>55</td><td>924</td></tr>
                    </tbody>
                </table>
            </div>
        </div>
        <div id="results-display">
            <p>rejuvenation bias u = <span id="u-value">0.0000</span></p>
            <p>site productivity p = <span id="p-value">0.0000</span></p>
        </div>
    </div>

    <script>
        // Static data
        const ages = [30, 35, 40, 45, 50, 55];
        const volumes = [289, 445, 565, 704, 833, 924];

        // Initialize plot
        const layout = {
            title: 'forest growth curve',
            xaxis: {
                title: 'stand age (years)',
                range: [15, 80],
                gridcolor: 'lightgrey',
                gridwidth: 1,
                griddash: 'dash',
                zeroline: false
            },
            yaxis: {
                title: 'wood volume, m³ ha⁻¹<br>Richards curve vs 4/5 law',
                range: [0, 1600],
                gridcolor: 'lightgrey',
                gridwidth: 1,
                griddash: 'dash',
                zeroline: false
            },
            plot_bgcolor: '#FFFFF0',
            showlegend: false
        };

        // Data points trace
        const dataTrace = {
            x: ages,
            y: volumes,
            mode: 'markers',
            type: 'scatter',
            marker: {
                color: 'blue',
                size: 8
            },
            name: 'Data'
        };

        // Initialize empty traces for curves
        const richardsTrace = {
            x: [],
            y: [],
            mode: 'lines',
            type: 'scatter',
            line: {
                color: 'orange',
                width: 2
            },
            name: 'Richards'
        };

        const fourFifthsTrace = {
            x: [],
            y: [],
            mode: 'lines',
            type: 'scatter',
            line: {
                color: 'blue',
                width: 2
            },
            name: '4/5 Law'
        };

        Plotly.newPlot('plot-container', [dataTrace, richardsTrace, fourFifthsTrace], layout, {responsive: true});

        // Update function
        function update() {
            // Read slider values
            const b_max = parseFloat(document.getElementById('slider-bmax').value);
            const b_0 = parseFloat(document.getElementById('slider-b0').value);
            const r = parseFloat(document.getElementById('slider-r').value);
            const beta = parseFloat(document.getElementById('slider-beta').value);

            // Update value displays
            document.getElementById('value-bmax').textContent = b_max;
            document.getElementById('value-b0').textContent = b_0;
            document.getElementById('value-r').textContent = r.toFixed(3);
            document.getElementById('value-beta').textContent = beta.toFixed(3);

            // Calculate M
            const M = Math.pow(b_max / b_0, beta) - 1;

            // Generate t values
            const t_values = [];
            for (let t = 15; t <= 80; t += 0.5) {
                t_values.push(t);
            }

            // Calculate Richards curve
            const richards_values = t_values.map(t => {
                return b_max / Math.pow(1 + M * Math.exp(-r * t), 1 / beta);
            });

            // Calculate inflection point parameters
            const t_i = Math.log(M * beta) / r;
            const V_i = b_max * Math.pow((beta + 1) / (2 * beta), 1 / beta);
            const V_prime_i = (r / beta) * V_i * (1 - Math.pow(V_i / b_max, beta));

            // Calculate u and p
            const u = t_i - (4 / 5) * V_i / V_prime_i;
            const p = V_i / Math.pow(t_i - u, 4 / 5);

            // Calculate 4/5 law curve
            const fourFifths_values = t_values.map(t => {
                if (t > u) {
                    return p * Math.pow(t - u, 4 / 5);
                }
                return null;
            });

            // Filter out null values for 4/5 law
            const validIndices = fourFifths_values.map((v, i) => v !== null ? i : -1).filter(i => i >= 0);
            const fourFifths_t = validIndices.map(i => t_values[i]);
            const fourFifths_y = validIndices.map(i => fourFifths_values[i]);

            // Update results display
            document.getElementById('u-value').textContent = u.toFixed(4);
            document.getElementById('p-value').textContent = p.toFixed(4);

            // Update plot
            Plotly.react('plot-container', [
                dataTrace,
                {
                    ...richardsTrace,
                    x: t_values,
                    y: richards_values
                },
                {
                    ...fourFifthsTrace,
                    x: fourFifths_t,
                    y: fourFifths_y
                }
            ], layout);
        }

        // Add event listeners to sliders
        document.getElementById('slider-bmax').addEventListener('input', update);
        document.getElementById('slider-b0').addEventListener('input', update);
        document.getElementById('slider-r').addEventListener('input', update);
        document.getElementById('slider-beta').addEventListener('input', update);

        // Initial update
        update();

        // Configure MathJax
        window.MathJax = {
            tex: {
                inlineMath: [['\\(', '\\)']],
                displayMath: [['\\[', '\\]']]
            }
        };
    </script>
