<body>
    <div id="control-panel">
        <div>
            <label>base polygon</label>
            <div class="radio-group">
                <label><input type="radio" name="base-polygon" id="radio-poly-3" value="3"> 3</label>
                <label><input type="radio" name="base-polygon" id="radio-poly-4" value="4"> 4</label>
                <label><input type="radio" name="base-polygon" id="radio-poly-5" value="5"> 5</label>
                <label><input type="radio" name="base-polygon" id="radio-poly-6" value="6"> 6</label>
                <label><input type="radio" name="base-polygon" id="radio-poly-7" value="7" checked> 7</label>
                <label><input type="radio" name="base-polygon" id="radio-poly-8" value="8"> 8</label>
                <label><input type="radio" name="base-polygon" id="radio-poly-9" value="9"> 9</label>
                <label><input type="radio" name="base-polygon" id="radio-poly-10" value="10"> 10</label>
            </div>
        </div>

