            <input type="range" id="slider-units" min="10" max="500" step="1" value="200">
            <button id="btn-units-plus">+</button>
        </div>

        <div>
            <label for="slider-angle">angular advance</label>
            <input type="range" id="slider-angle" min="120" max="150" step="0.1" value="137.5">
            <button id="btn-angle-plus">+</button>
        </div>

        <div>
            <label for="slider-conical">make conical</label>
            <input type="range" id="slider-conical" min="0" max="2" step="0.01" value="0">
            <button id="btn-conical-plus">+</button>
        </div>

        <div>
            <label for="slider-rotate-x">rotate x</label>
            <input type="range" id="slider-rotate-x" min="-90" max="90" step="1" value="30">
            <button id="btn-rotate-x-plus">+</button>
        </div>

        <div>
            <label for="slider-rotate-z">rotate z</label>
            <input type="range" id="slider-rotate-z" min="-180" max="180" step="1" value="0">
            <button id="btn-rotate-z-plus">+</button>
        </div>
    </div>

    <div id="canvas-container">
        <canvas id="main-canvas"></canvas>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r128/three.min.js"></script>
    <script>
        let scene, camera, renderer, mainGroup;

        function init() {
            // Scene setup
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xffffff);

            // Camera setup
            camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
            camera.position.set(0, 0, 300);
            camera.lookAt(0, 0, 0);

            // Renderer setup
            renderer = new THREE.WebGLRenderer({ 
                canvas: document.getElementById('main-canvas'),
                antialias: true 
            });
            const container = document.getElementById('canvas-container');
            renderer.setSize(container.clientWidth, container.clientHeight);

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.4);
            directionalLight.position.set(50, 50, 50);
            scene.add(directionalLight);

            // Main group for all segments
            mainGroup = new THREE.Group();
            scene.add(mainGroup);

            // Set up controls
            setupControls();

            // Initial render
            redraw();

            // Handle window resize
            window.addEventListener('resize', onWindowResize);
        }

        function setupControls() {
            // Radio buttons
            document.querySelectorAll('input[name="base-polygon"]').forEach(radio => {
                radio.addEventListener('change', redraw);
            });

            // Sliders
            document.getElementById('slider-units').addEventListener('input', redraw);
            document.getElementById('slider-angle').addEventListener('input', redraw);
            document.getElementById('slider-conical').addEventListener('input', redraw);
            document.getElementById('slider-rotate-x').addEventListener('input', redraw);
            document.getElementById('slider-rotate-z').addEventListener('input', redraw);

            // Plus buttons
            document.getElementById('btn-units-plus').addEventListener('click', () => {
                const slider = document.getElementById('slider-units');
                slider.value = Math.min(parseInt(slider.value) + 1, slider.max);
                redraw();
            });

            document.getElementById('btn-angle-plus').addEventListener('click', () => {
                const slider = document.getElementById('slider-angle');
                slider.value = Math.min(parseFloat(slider.value) + 0.1, slider.max);
                redraw();
            });

            document.getElementById('btn-conical-plus').addEventListener('click', () => {
                const slider = document.getElementById('slider-conical');
                slider.value = Math.min(parseFloat(slider.value) + 0.01, slider.max);
                redraw();
            });

            document.getElementById('btn-rotate-x-plus').addEventListener('click', () => {
                const slider = document.getElementById('slider-rotate-x');
                slider.value = Math.min(parseInt(slider.value) + 1, slider.max);
                redraw();
            });

            document.getElementById('btn-rotate-z-plus').addEventListener('click', () => {
                const slider = document.getElementById('slider-rotate-z');
                slider.value = Math.min(parseInt(slider.value) + 1, slider.max);
                redraw();
            });
        }

        function createPolygonGeometry(sides, radius) {
            const shape = new THREE.Shape();
            for (let i = 0; i <= sides; i++) {
                const angle = (i / sides) * Math.PI * 2;
                const x = Math.cos(angle) * radius;
                const y = Math.sin(angle) * radius;
                if (i === 0) {
                    shape.moveTo(x, y);
                } else {
                    shape.lineTo(x, y);
                }
            }
            return new THREE.ShapeGeometry(shape);
        }

        function redraw() {
            // Get control values
            const sides = parseInt(document.querySelector('input[name="base-polygon"]:checked').value);
            const numUnits = parseInt(document.getElementById('slider-units').value);
            const angularAdvance = parseFloat(document.getElementById('slider-angle').value);
            const conical = parseFloat(document.getElementById('slider-conical').value);
            const rotateX = parseFloat(document.getElementById('slider-rotate-x').value);
            const rotateZ = parseFloat(document.getElementById('slider-rotate-z').value);

            // Clear existing segments
            while (mainGroup.children.length > 0) {
                mainGroup.remove(mainGroup.children[0]);
            }

            // Create base geometry
            const baseRadius = 10;
            const baseGeometry = createPolygonGeometry(sides, baseRadius);

            // Generate segments
            const angularAdvanceRad = angularAdvance * (Math.PI / 180);

            for (let i = 0; i < numUnits; i++) {
                const angle = i * angularAdvanceRad;
                const radius = 4 * Math.sqrt(i);
                const scale = 0.5 * Math.log(i + 2);

                const x = radius * Math.cos(angle);
                const y = radius * Math.sin(angle);
                const z = conical * radius;

                // Create material with color based on angle
                const material = new THREE.MeshStandardMaterial({
                    color: new THREE.Color().setHSL((angle / (2 * Math.PI)) % 1.0, 0.8, 0.6)
                });

                // Create mesh
                const mesh = new THREE.Mesh(baseGeometry, material);
                mesh.position.set(x, y, z);
                mesh.scale.set(scale, scale, 1);
                mesh.rotation.z = angle;

                mainGroup.add(mesh);
            }

            // Apply group rotations
            mainGroup.rotation.x = rotateX * (Math.PI / 180);
            mainGroup.rotation.z = rotateZ * (Math.PI / 180);

            // Render
            renderer.render(scene, camera);
        }

        function onWindowResize() {
            const container = document.getElementById('canvas-container');
            camera.aspect = container.clientWidth / container.clientHeight;
            camera.updateProjectionMatrix();
            renderer.setSize(container.clientWidth, container.clientHeight);
            renderer.render(scene, camera);
        }

        // Initialize on load
        init();
    </script>
</body>
</html>
