<body>
    <div id="main-container">
        <div id="control-panel">
            <div id="lattice-controls">
                <label>lattice</label>
                <button id="btn-sc" class="active">SC</button>
                <button id="btn-bcc">BCC</button>
                <button id="btn-fcc">FCC</button>
            </div>

            <div id="miller-controls">
                <label>Miller indices</label>
                <label for="input-h">h</label>
                <input type="number" id="input-h" value="1" min="-10" max="10" step="1">
                <label for="input-k">k</label>
                <input type="number" id="input-k" value="1" min="-10" max="10" step="1">
                <label for="input-l">l</label>
                <input type="number" id="input-l" value="1" min="-10" max="10" step="1">
            </div>

            <div id="opacity-controls">
                <label for="slider-opacity">plane opacity</label>
                <input type="range" id="slider-opacity" value="0.8" min="0" max="1" step="0.01">
            </div>
        </div>

        <div id="canvas-container"></div>
    </div>

    <script src="https://cdnjs.cloudflare.com/ajax/libs/three.js/r150/three.min.js"></script>
    <script src="https://cdn.jsdelivr.net/npm/three@0.150.0/examples/js/controls/OrbitControls.js"></script>

    <script>
        // Scene setup
        const scene = new THREE.Scene();
        scene.background = new THREE.Color(0xf0f0f0);

        const camera = new THREE.PerspectiveCamera(
            75,
            window.innerWidth / 600,
            0.1,
            1000
        );
        camera.position.set(2, 2, 2);

        const renderer = new THREE.WebGLRenderer({ antialias: true });
        const container = document.getElementById('canvas-container');
        renderer.setSize(container.clientWidth, container.clientHeight);
        container.appendChild(renderer.domElement);

        const controls = new THREE.OrbitControls(camera, renderer.domElement);
        controls.enableDamping = true;
        controls.dampingFactor = 0.05;

        // Lighting
        const ambientLight = new THREE.AmbientLight(0xffffff, 0.6);
        scene.add(ambientLight);

        const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.4);
        directionalLight1.position.set(5, 5, 5);
        scene.add(directionalLight1);

        const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.2);
        directionalLight2.position.set(-5, -5, -5);
        scene.add(directionalLight2);

        // Groups for organization
        const atomsGroup = new THREE.Group();
        const planesGroup = new THREE.Group();
        scene.add(atomsGroup);
        scene.add(planesGroup);

        // Unit cell wireframe
        const cellGeometry = new THREE.BoxGeometry(1, 1, 1);
        const cellEdges = new THREE.EdgesGeometry(cellGeometry);
        const cellLine = new THREE.LineBasicMaterial({ color: 0x333333 });
        const cellWireframe = new THREE.LineSegments(cellEdges, cellLine);
        scene.add(cellWireframe);

        // Coordinate axes
        const axesLength = 0.8;
        const xAxis = new THREE.ArrowHelper(
            new THREE.Vector3(1, 0, 0),
            new THREE.Vector3(-0.5, -0.5, -0.5),
            axesLength,
            0xff0000
        );
        const yAxis = new THREE.ArrowHelper(
            new THREE.Vector3(0, 1, 0),
            new THREE.Vector3(-0.5, -0.5, -0.5),
            axesLength,
            0x00ff00
        );
        const zAxis = new THREE.ArrowHelper(
            new THREE.Vector3(0, 0, 1),
            new THREE.Vector3(-0.5, -0.5, -0.5),
            axesLength,
            0x0000ff
        );
        scene.add(xAxis);
        scene.add(yAxis);
        scene.add(zAxis);

        // State
        let currentLattice = 'SC';

        // Helper functions
        function gcd(a, b) {
            a = Math.abs(a);
            b = Math.abs(b);
            while (b) {
                let t = b;
                b = a % b;
                a = t;
            }
            return a || 1;
        }

        function gcd3(a, b, c) {
            return gcd(gcd(a, b), c);
        }

        function createAtom(x, y, z) {
            const geometry = new THREE.SphereGeometry(0.08, 16, 16);
            const material = new THREE.MeshPhongMaterial({
                color: 0x1a4d4d,
                specular: 0x44ffff,
                shininess: 100
            });
            const atom = new THREE.Mesh(geometry, material);
            atom.position.set(x, y, z);
            return atom;
        }

        function getPlaneIntersections(h, k, l, n) {
            const intersections = [];
            const edges = [
                // Bottom edges
                [[-0.5, -0.5, -0.5], [0.5, -0.5, -0.5]],
                [[0.5, -0.5, -0.5], [0.5, 0.5, -0.5]],
                [[0.5, 0.5, -0.5], [-0.5, 0.5, -0.5]],
                [[-0.5, 0.5, -0.5], [-0.5, -0.5, -0.5]],
                // Top edges
                [[-0.5, -0.5, 0.5], [0.5, -0.5, 0.5]],
                [[0.5, -0.5, 0.5], [0.5, 0.5, 0.5]],
                [[0.5, 0.5, 0.5], [-0.5, 0.5, 0.5]],
                [[-0.5, 0.5, 0.5], [-0.5, -0.5, 0.5]],
                // Vertical edges
                [[-0.5, -0.5, -0.5], [-0.5, -0.5, 0.5]],
                [[0.5, -0.5, -0.5], [0.5, -0.5, 0.5]],
                [[0.5, 0.5, -0.5], [0.5, 0.5, 0.5]],
                [[-0.5, 0.5, -0.5], [-0.5, 0.5, 0.5]]
            ];

            edges.forEach(edge => {
                const [p1, p2] = edge;
                const d1 = h * p1[0] + k * p1[1] + l * p1[2] - n;
                const d2 = h * p2[0] + k * p2[1] + l * p2[2] - n;

                if (Math.abs(d1) < 1e-10) {
                    intersections.push([...p1]);
                } else if (Math.abs(d2) < 1e-10) {
                    intersections.push([...p2]);
                } else if (d1 * d2 < 0) {
                    const t = -d1 / (d2 - d1);
                    const x = p1[0] + t * (p2[0] - p1[0]);
                    const y = p1[1] + t * (p2[1] - p1[1]);
                    const z = p1[2] + t * (p2[2] - p1[2]);
                    intersections.push([x, y, z]);
                }
            });

            // Remove duplicates
            const unique = [];
            intersections.forEach(p => {
                if (!unique.some(u => 
                    Math.abs(u[0] - p[0]) < 1e-10 &&
                    Math.abs(u[1] - p[1]) < 1e-10 &&
                    Math.abs(u[2] - p[2]) < 1e-10
                )) {
                    unique.push(p);
                }
            });

            return unique;
        }

        function sortPointsAngularly(points) {
            if (points.length < 3) return points;

            // Calculate centroid
            const centroid = points.reduce((acc, p) => {
                return [acc[0] + p[0], acc[1] + p[1], acc[2] + p[2]];
            }, [0, 0, 0]).map(v => v / points.length);

            // Find two vectors to define the plane
            const v1 = [
                points[1][0] - points[0][0],
                points[1][1] - points[0][1],
                points[1][2] - points[0][2]
            ];

            let v2Index = 2;
            let v2;
            for (let i = 2; i < points.length; i++) {
                v2 = [
                    points[i][0] - points[0][0],
                    points[i][1] - points[0][1],
                    points[i][2] - points[0][2]
                ];
                // Check if v1 and v2 are not parallel
                const cross = [
                    v1[1] * v2[2] - v1[2] * v2[1],
                    v1[2] * v2[0] - v1[0] * v2[2],
                    v1[0] * v2[1] - v1[1] * v2[0]
                ];
                if (Math.sqrt(cross[0]**2 + cross[1]**2 + cross[2]**2) > 1e-10) {
                    break;
                }
            }

            // Calculate normal vector
            const normal = [
                v1[1] * v2[2] - v1[2] * v2[1],
                v1[2] * v2[0] - v1[0] * v2[2],
                v1[0] * v2[1] - v1[1] * v2[0]
            ];
            const normalLength = Math.sqrt(normal[0]**2 + normal[1]**2 + normal[2]**2);
            normal[0] /= normalLength;
            normal[1] /= normalLength;
            normal[2] /= normalLength;

            // Sort points by angle
            return points.sort((a, b) => {
                const va = [a[0] - centroid[0], a[1] - centroid[1], a[2] - centroid[2]];
                const vb = [b[0] - centroid[0], b[1] - centroid[1], b[2] - centroid[2]];

                const angleA = Math.atan2(
                    va[0] * v1[0] + va[1] * v1[1] + va[2] * v1[2],
                    va[0] * v2[0] + va[1] * v2[1] + va[2] * v2[2]
                );
                const angleB = Math.atan2(
                    vb[0] * v1[0] + vb[1] * v1[1] + vb[2] * v1[2],
                    vb[0] * v2[0] + vb[1] * v2[1] + vb[2] * v2[2]
                );

                return angleA - angleB;
            });
        }

        function updateScene() {
            // Clear previous atoms and planes
            atomsGroup.clear();
            planesGroup.clear();

            // Get current state
            const h = parseInt(document.getElementById('input-h').value) || 0;
            const k = parseInt(document.getElementById('input-k').value) || 0;
            const l = parseInt(document.getElementById('input-l').value) || 0;
            const opacity = parseFloat(document.getElementById('slider-opacity').value);

            // Draw atoms based on lattice type
            if (currentLattice === 'SC') {
                // Simple cubic - 8 corner atoms
                for (let i = -1; i <= 1; i += 2) {
                    for (let j = -1; j <= 1; j += 2) {
                        for (let m = -1; m <= 1; m += 2) {
                            atomsGroup.add(createAtom(i * 0.5, j * 0.5, m * 0.5));
                        }
                    }
                }
            } else if (currentLattice === 'BCC') {
                // Body-centered cubic - 8 corners + 1 center
                for (let i = -1; i <= 1; i += 2) {
                    for (let j = -1; j <= 1; j += 2) {
                        for (let m = -1; m <= 1; m += 2) {
                            atomsGroup.add(createAtom(i * 0.5, j * 0.5, m * 0.5));
                        }
                    }
                }
                atomsGroup.add(createAtom(0, 0, 0));
            } else if (currentLattice === 'FCC') {
                // Face-centered cubic - 8 corners + 6 face centers
                for (let i = -1; i <= 1; i += 2) {
                    for (let j = -1; j <= 1; j += 2) {
                        for (let m = -1; m <= 1; m += 2) {
                            atomsGroup.add(createAtom(i * 0.5, j * 0.5, m * 0.5));
                        }
                    }
                }
                // Face centers
                atomsGroup.add(createAtom(0.5, 0, 0));
                atomsGroup.add(createAtom(-0.5, 0, 0));
                atomsGroup.add(createAtom(0, 0.5, 0));
                atomsGroup.add(createAtom(0, -0.5, 0));
                atomsGroup.add(createAtom(0, 0, 0.5));
                atomsGroup.add(createAtom(0, 0, -0.5));
            }

            // Draw planes
            if (h !== 0 || k !== 0 || l !== 0) {
                const g = gcd3(h, k, l);
                const hp = h / g;
                const kp = k / g;
                const lp = l / g;

                // Find range of n values
                const vertices = [
                    [-0.5, -0.5, -0.5], [0.5, -0.5, -0.5],
                    [-0.5, 0.5, -0.5], [0.5, 0.5, -0.5],
                    [-0.5, -0.5, 0.5], [0.5, -0.5, 0.5],
                    [-0.5, 0.5, 0.5], [0.5, 0.5, 0.5]
                ];

                const values = vertices.map(v => hp * v[0] + kp * v[1] + lp * v[2]);
                const minVal = Math.min(...values);
                const maxVal = Math.max(...values);

                for (let n = Math.floor(minVal); n <= Math.ceil(maxVal); n++) {
                    const intersections = getPlaneIntersections(hp, kp, lp, n);

                    if (intersections.length >= 3) {
                        const sorted = sortPointsAngularly(intersections);

                        const shape = new THREE.Shape();
                        shape.moveTo(sorted[0][0], sorted[0][1]);

                        for (let i = 1; i < sorted.length; i++) {
                            shape.lineTo(sorted[i][0], sorted[i][1]);
                        }
                        shape.lineTo(sorted[0][0], sorted[0][1]);

                        const geometry = new THREE.ShapeGeometry(shape);

                        // Calculate proper position for the shape
                        const positions = geometry.attributes.position;
                        for (let i = 0; i < positions.count; i++) {
                            const x = positions.getX(i);
                            const y = positions.getY(i);
                            // Calculate z from plane equation
                            const z = (n - hp * x - kp * y) / lp;
                            positions.setZ(i, z);
                        }
                        positions.needsUpdate = true;

                        const material = new THREE.MeshBasicMaterial({
                            color: 0x0066cc,
                            transparent: true,
                            opacity: opacity,
                            side: THREE.DoubleSide
                        });

                        const plane = new THREE.Mesh(geometry, material);
                        planesGroup.add(plane);
                    }
                }
            }
        }

        // Event listeners
        document.getElementById('btn-sc').addEventListener('click', function() {
            document.querySelectorAll('#lattice-controls button').forEach(btn => {
                btn.classList.remove('active');
            });
            this.classList.add('active');
            currentLattice = 'SC';
            updateScene();
        });

        document.getElementById('btn-bcc').addEventListener('click', function() {
            document.querySelectorAll('#lattice-controls button').forEach(btn => {
                btn.classList.remove('active');
            });
            this.classList.add('active');
            currentLattice = 'BCC';
            updateScene();
        });

        document.getElementById('btn-fcc').addEventListener('click', function() {
            document.querySelectorAll('#lattice-controls button').forEach(btn => {
                btn.classList.remove('active');
            });
            this.classList.add('active');
            currentLattice = 'FCC';
            updateScene();
        });

        document.getElementById('input-h').addEventListener('change', updateScene);
        document.getElementById('input-k').addEventListener('change', updateScene);
        document.getElementById('input-l').addEventListener('change', updateScene);

        document.getElementById('slider-opacity').addEventListener('input', function() {
            const opacity = parseFloat(this.value);
            planesGroup.children.forEach(plane => {
                plane.material.opacity = opacity;
            });
        });

        // Handle window resize
        window.addEventListener('resize', function() {
            const width = container.clientWidth;
            const height = container.clientHeight;
            camera.aspect = width / height;
            camera.updateProjectionMatrix();
            renderer.setSize(width, height);
        });

        // Animation loop
        function animate() {
            requestAnimationFrame(animate);
            controls.update();
            renderer.render(scene, camera);
        }

        // Initialize
        updateScene();
        animate();
    </script>
</body>
</html>
