                  [-0.723607, 0.525731, -0.447214], [-0.723607, -0.525731, -0.447214]],
            "20": [[0, 0, 1], [0.666667, 0, 0.745356], [-0.333333, 0.577350, 0.745356], 
                   [-0.333333, -0.577350, 0.745356], [0.934172, 0, 0.356822], 
                   [0.467086, 0.809017, 0.356822], [-0.467086, 0.809017, 0.356822], 
                   [-0.934172, 0, 0.356822], [-0.467086, -0.809017, 0.356822], 
                   [0.467086, -0.809017, 0.356822], [0.794654, 0.577350, -0.187082], 
                   [-0.794654, 0.577350, -0.187082], [0, -0.934172, -0.356822], 
                   [0, 0.934172, -0.356822], [-0.794654, -0.577350, -0.187082], 
                   [0.794654, -0.577350, -0.187082], [0.333333, 0.577350, -0.745356], 
                   [0.333333, -0.577350, -0.745356], [-0.666667, 0, -0.745356], 
                   [0, 0, -1]]
        };

        // Generate random points on unit sphere if not in pre-calculated data
        function generateRandomSpherePoints(n) {
            const points = [];
            for (let i = 0; i < n; i++) {
                const theta = Math.random() * 2 * Math.PI;
                const phi = Math.acos(2 * Math.random() - 1);
                const x = Math.sin(phi) * Math.cos(theta);
                const y = Math.sin(phi) * Math.sin(theta);
                const z = Math.cos(phi);
                points.push([x, y, z]);
            }
            return points;
        }

        // Get points for given number
        function getPoints(n) {
            if (THOMSON_POINTS[n.toString()]) {
                return THOMSON_POINTS[n.toString()];
            }
            return generateRandomSpherePoints(n);
        }

        // Three.js setup
        let scene, camera, renderer, controls;
        let mainSphere, pointsGroup;
        let pointMeshes = [];

        function init3D() {
            scene = new THREE.Scene();
            scene.background = new THREE.Color(0xf0f0f0);

            const canvas = document.getElementById('canvas-3d');
            renderer = new THREE.WebGLRenderer({ canvas: canvas, antialias: true });
            renderer.setSize(800, 600);

            camera = new THREE.PerspectiveCamera(45, 800/600, 0.1, 1000);
            camera.position.set(0, 0, 4);

            controls = new THREE.OrbitControls(camera, renderer.domElement);
            controls.enablePan = false;
            controls.enableZoom = true;
            controls.minDistance = 2;
            controls.maxDistance = 10;

            // Lighting
            const ambientLight = new THREE.AmbientLight(0xcccccc);
            scene.add(ambientLight);

            const directionalLight = new THREE.DirectionalLight(0xffffff, 0.7);
            directionalLight.position.set(1, 1, 1);
            scene.add(directionalLight);

            // Main sphere
            const sphereGeometry = new THREE.SphereGeometry(1, 32, 32);
            const sphereMaterial = new THREE.MeshLambertMaterial({
                color: 0xFFFFE0,
                transparent: true,
                opacity: 0.6
            });
            mainSphere = new THREE.Mesh(sphereGeometry, sphereMaterial);
            scene.add(mainSphere);

            pointsGroup = new THREE.Group();
            scene.add(pointsGroup);

            updatePoints3D(20);
            animate();
        }

        function updatePoints3D(n) {
            // Clear existing points
            pointMeshes.forEach(pair => {
                pointsGroup.remove(pair.sphere);
                pointsGroup.remove(pair.ring);
            });
            pointMeshes = [];

            const points = getPoints(n);

            points.forEach(coord => {
                // Solid sphere
                const sphereGeom = new THREE.SphereGeometry(0.025, 16, 16);
                const sphereMat = new THREE.MeshBasicMaterial({ color: 0x0000ff });
                const sphere = new THREE.Mesh(sphereGeom, sphereMat);
                sphere.position.set(coord[0], coord[1], coord[2]);

                // Hollow ring
                const ringGeom = new THREE.RingGeometry(0.02, 0.025, 32);
                const ringMat = new THREE.MeshBasicMaterial({ 
                    color: 0x0000ff, 
                    side: THREE.DoubleSide 
                });
                const ring = new THREE.Mesh(ringGeom, ringMat);
                ring.position.set(coord[0], coord[1], coord[2]);

                pointsGroup.add(sphere);
                pointsGroup.add(ring);

                pointMeshes.push({ sphere, ring, position: coord });
            });
        }

        function animate() {
            requestAnimationFrame(animate);
            controls.update();

            // Update point visibility based on position
            pointMeshes.forEach(pair => {
                const worldPos = new THREE.Vector3();
                pair.sphere.getWorldPosition(worldPos);

                const cameraDir = new THREE.Vector3();
                camera.getWorldDirection(cameraDir);

                const dotProduct = worldPos.normalize().dot(cameraDir.normalize());

                if (dotProduct < -0.1) {
                    pair.sphere.visible = true;
                    pair.ring.visible = false;
                } else {
                    pair.sphere.visible = false;
                    pair.ring.visible = true;
                    pair.ring.lookAt(camera.position);
                }
            });

            renderer.render(scene, camera);
        }

        // 2D Canvas setup
        function draw2D(n) {
            const canvas = document.getElementById('canvas-2d');
            const ctx = canvas.getContext('2d');
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            const points = getPoints(n);

            // Draw points
            ctx.fillStyle = '#0000ff';
            points.forEach(coord => {
                const theta = Math.atan2(coord[1], coord[0]);
                const phi = Math.acos(coord[2]);

                const x = (theta + Math.PI) / (2 * Math.PI) * canvas.width;
                const y = phi / Math.PI * canvas.height;

                ctx.beginPath();
                ctx.arc(x, y, 4, 0, 2 * Math.PI);
                ctx.fill();
            });

            // Calculate distances
            let minDist = Infinity;
            for (let i = 0; i < points.length; i++) {
                for (let j = i + 1; j < points.length; j++) {
                    const dx = points[i][0] - points[j][0];
                    const dy = points[i][1] - points[j][1];
                    const dz = points[i][2] - points[j][2];
                    const dist = Math.sqrt(dx*dx + dy*dy + dz*dz);
                    if (dist < minDist) minDist = dist;
                }
            }

            const textDiv = document.getElementById('div-2d-text');
            textDiv.innerHTML = `minimum distance between points = ${minDist.toFixed(5)}<br>maximum distance between points = 2.00000`;
        }

        // Event handlers
        document.getElementById('slider-points').addEventListener('input', function(e) {
            const n = parseInt(e.target.value);
            document.getElementById('label-points').textContent = `+ ${n}`;

            if (document.getElementById('btn-3d').classList.contains('active')) {
                updatePoints3D(n);
            } else {
                draw2D(n);
            }
        });

        document.getElementById('btn-3d').addEventListener('click', function() {
            this.classList.add('active');
            document.getElementById('btn-2d').classList.remove('active');
            document.getElementById('canvas-3d').style.display = 'block';
            document.getElementById('div-2d-plot').style.display = 'none';

            const n = parseInt(document.getElementById('slider-points').value);
            updatePoints3D(n);
        });

        document.getElementById('btn-2d').addEventListener('click', function() {
            this.classList.add('active');
            document.getElementById('btn-3d').classList.remove('active');
            document.getElementById('canvas-3d').style.display = 'none';
            document.getElementById('div-2d-plot').style.display = 'block';

            const n = parseInt(document.getElementById('slider-points').value);
            draw2D(n);
        });

        // Initialize
        init3D();
    </script>
</body>
</html>
