    <script>
        let sliderAngle, sliderLongitude, sliderEccentricity;
        let valueAngle, valueLongitude, valueEccentricity;
        let orbitSketch, eotPlot;

        // Get slider elements
        sliderAngle = document.getElementById('slider-angle');
        sliderLongitude = document.getElementById('slider-longitude');
        sliderEccentricity = document.getElementById('slider-eccentricity');

        valueAngle = document.getElementById('value-angle');
