        // Add event listeners
        sliderAngle.addEventListener('input', updateValues);
        sliderLongitude.addEventListener('input', updateValues);
        sliderEccentricity.addEventListener('input', updateValues);

        function updateValues() {
            valueAngle.textContent = parseFloat(sliderAngle.value).toFixed(2);
            valueLongitude.textContent = parseFloat(sliderLongitude.value).toFixed(2);
            valueEccentricity.textContent = parseFloat(sliderEccentricity.value).toFixed(6);

            if (orbitSketch) orbitSketch.redraw();
            if (eotPlot) eotPlot.redraw();
        }

        // Orbit Sketch
        new p5(function(p) {
            p.setup = function() {
                let canvas = p.createCanvas(400, 300);
                canvas.parent('orbit-canvas');
                p.noLoop();
                orbitSketch = p;
            };

            p.draw = function() {
                p.background(240);

                let e = parseFloat(sliderEccentricity.value);
                let omega = parseFloat(sliderLongitude.value) * p.PI / 180;

                let a = p.width * 0.4;
                let b = a * Math.sqrt(1 - e * e);

                p.push();
                p.translate(p.width / 2, p.height / 2);
                p.rotate(omega);
                p.noFill();
                p.stroke(160, 82, 45);
                p.strokeWeight(2);
                p.ellipse(0, 0, a * 2, b * 2);
                p.pop();
            };
        });

        // Equation of Time Plot
        new p5(function(p) {
            p.setup = function() {
                let canvas = p.createCanvas(600, 400);
                canvas.parent('plot-canvas');
                p.noLoop();
                eotPlot = p;
            };

            p.draw = function() {
                p.background(255);

                let epsilon = parseFloat(sliderAngle.value) * p.PI / 180;
                let e = parseFloat(sliderEccentricity.value);
                let omega = parseFloat(sliderLongitude.value) * p.PI / 180;

                // Calculate EOT values
                let points = [];
                let minEOT = Infinity;
                let maxEOT = -Infinity;
                let prevEOT = 0;

                for (let i = 0; i <= 200; i++) {
                    let M = (i / 200) * 2 * p.PI;

                    // Solve Kepler's equation
                    let E = M;
                    for (let j = 0; j < 10; j++) {
                        E = E - (E - e * Math.sin(E) - M) / (1 - e * Math.cos(E));
                    }

                    // Calculate true anomaly
                    let nu = 2 * Math.atan2(Math.sqrt(1 + e) * Math.sin(E / 2), 
                                           Math.sqrt(1 - e) * Math.cos(E / 2));

                    // Calculate true longitude
                    let lambda = nu + omega;

                    // Calculate right ascension
                    let alpha = Math.atan2(Math.cos(epsilon) * Math.sin(lambda), 
                                          Math.cos(lambda));

                    // Calculate EOT in radians
                    let EOT_rad = M - alpha;

                    // Phase unwrapping
                    if (i > 0 && Math.abs(EOT_rad - prevEOT) > p.PI) {
                        if (EOT_rad - prevEOT > p.PI) {
                            EOT_rad -= 2 * p.PI;
                        } else {
                            EOT_rad += 2 * p.PI;
                        }
                    }
                    prevEOT = EOT_rad;

                    // Convert to minutes
                    let EOT_minutes = EOT_rad * (720 / p.PI);

                    points.push({M: M, EOT: EOT_minutes});
                    minEOT = Math.min(minEOT, EOT_minutes);
                    maxEOT = Math.max(maxEOT, EOT_minutes);
                }

                // Set up coordinate system
                let margin = 50;
                let plotWidth = p.width - 2 * margin;
                let plotHeight = p.height - 2 * margin;

                // Add padding to y range
                let yPadding = (maxEOT - minEOT) * 0.1;
                minEOT -= yPadding;
                maxEOT += yPadding;

                // Draw axes
                p.stroke(0);
                p.strokeWeight(1);
                p.line(margin, p.height - margin, p.width - margin, p.height - margin); // x-axis
                p.line(margin, margin, margin, p.height - margin); // y-axis

                // Draw gridlines and labels
                p.textAlign(p.CENTER, p.TOP);
                p.textSize(12);

                // X-axis labels and gridlines
                for (let i = 0; i <= 6; i++) {
                    let x = margin + (i / 6) * plotWidth;
                    p.stroke(200);
                    p.line(x, margin, x, p.height - margin);
                    p.stroke(0);
                    p.noStroke();
                    p.text(i, x, p.height - margin + 5);
                }

                // Y-axis labels and gridlines
                p.textAlign(p.RIGHT, p.CENTER);
                let yTicks = 5;
                for (let i = 0; i <= yTicks; i++) {
                    let y = p.height - margin - (i / yTicks) * plotHeight;
                    let value = minEOT + (i / yTicks) * (maxEOT - minEOT);
                    p.stroke(200);
                    p.line(margin, y, p.width - margin, y);
                    p.stroke(0);
                    p.noStroke();
                    p.text(value.toFixed(1), margin - 5, y);
                }

                // Axis labels
                p.textAlign(p.CENTER, p.CENTER);
                p.text("Mean Anomaly", p.width / 2, p.height - 15);
                p.push();
                p.translate(15, p.height / 2);
                p.rotate(-p.HALF_PI);
                p.text("minutes", 0, 0);
                p.pop();

                // Draw curve
                p.noFill();
                p.stroke(255, 140, 0);
                p.strokeWeight(2);
                p.beginShape();
                for (let point of points) {
                    let x = margin + (point.M / (2 * p.PI)) * plotWidth;
                    let y = p.height - margin - ((point.EOT - minEOT) / (maxEOT - minEOT)) * plotHeight;
                    p.vertex(x, y);
                }
                p.endShape();
            };
        });

        // Initial draw
        updateValues();
    </script>
</body>
</html>
