        </div>
    </div>

    <script>
        let c = 4;
        let h_C = 1.9;
        let delta = 0.5;
        let plotRange = 1;
        let currentStep = 5;
        let showVerification = true;

        function setup() {
            let canvas = createCanvas(800, 600);
            canvas.parent('canvas-container');
            noLoop();

            // Set up event listeners
            document.getElementById('slider-c').addEventListener('input', updateC);
            document.getElementById('slider-h_C').addEventListener('input', updateHC);
            document.getElementById('slider-delta').addEventListener('input', updateDelta);
            document.getElementById('slider-plot-range').addEventListener('input', updatePlotRange);
            document.getElementById('check-verification').addEventListener('change', updateVerification);

            // Radio buttons
            document.querySelectorAll('input[name="steps"]').forEach(radio => {
                radio.addEventListener('change', updateStep);
            });

            redrawScene();
        }

        function updateC(e) {
            c = parseFloat(e.target.value);
            document.getElementById('c-value').textContent = c.toFixed(1);
            redrawScene();
        }

        function updateHC(e) {
            h_C = parseFloat(e.target.value);
            document.getElementById('h_C-value').textContent = h_C.toFixed(1);
            redrawScene();
        }

        function updateDelta(e) {
            delta = parseFloat(e.target.value);
            document.getElementById('delta-value').textContent = delta.toFixed(2);
            redrawScene();
        }

        function updatePlotRange(e) {
            plotRange = parseFloat(e.target.value);
            document.getElementById('plot-range-value').textContent = plotRange.toFixed(2);
            redrawScene();
        }

        function updateStep(e) {
            currentStep = parseInt(e.target.value);
            redrawScene();
        }

        function updateVerification(e) {
            showVerification = e.target.checked;
            redrawScene();
        }

        function redrawScene() {
            background('#E0E0E0');

            // Set up coordinate system
            push();
            translate(width/2, height * 0.8);
            scale(1, -1);

            const baseScale = 50 * plotRange;
            scale(baseScale, baseScale);

            // Calculate geometry
            const F = {x: 0, y: 0};
            const C = {x: 0, y: h_C};
            const D = {x: c/2, y: 0};
            const K = {x: c, y: 0};

            // Solve for M
            const tanDelta = Math.tan(delta);
            const a = 1 + tanDelta * tanDelta;
            const b = -tanDelta * c / 2 - (3 * c * tanDelta * tanDelta) / 2;
            const cCoeff = (h_C * h_C - c * c / 4) / (2 * h_C) * tanDelta + (c * c * tanDelta * tanDelta) / 2;

            const discriminant = b * b - 4 * a * cCoeff;
            const y_M = (-b - Math.sqrt(discriminant)) / (2 * a);
            const x_M = (2 * h_C * y_M - h_C * h_C + c * c / 4) / c;
            const M = {x: x_M, y: y_M};

            // Calculate radius and other points
            const R = Math.sqrt((M.x - C.x)**2 + (M.y - C.y)**2);
            const x_B = 2 * x_M - c/2;
            const B = {x: x_B, y: 0};
            const A = {x: x_B - c, y: 0};
            const E = {x: c - x_B, y: 0};

            // Drawing
            strokeWeight(1/baseScale);
            stroke(0);
            fill(0);

            // Step 1: Initial setup
            if (currentStep >= 1) {
                // Draw line λ
                line(-width/baseScale, 0, width/baseScale, 0);

                // Draw points F, C, D
                drawPointWithLabel(F, 'F', baseScale);
                drawPointWithLabel(C, 'C', baseScale);
                drawPointWithLabel(D, 'D', baseScale);

                // Draw FC
                line(F.x, F.y, C.x, C.y);
            }

            // Step 2: Construction
            if (currentStep >= 2) {
                drawPointWithLabel(K, 'K', baseScale);

                // Dashed lines
                drawDashedLine(C, D, baseScale);
                drawDashedLine(D, K, baseScale);

                drawPointWithLabel(M, 'M', baseScale);

                drawDashedLine(M, C, baseScale);
                drawDashedLine(M, D, baseScale);
                drawDashedLine(M, K, baseScale);

                // Draw arc κ
                noFill();
                strokeWeight(1/baseScale);
                arc(M.x, M.y, 2*R, 2*R, 0, TWO_PI);
            }

            // Step 3: Point B
            if (currentStep >= 3) {
                drawPointWithLabel(B, 'B', baseScale);
            }

            // Step 4: Point A
            if (currentStep >= 4) {
                drawPointWithLabel(A, 'A', baseScale);
            }

            // Step 5: Final triangle
            if (currentStep >= 5) {
                strokeWeight(3/baseScale);
                line(A.x, A.y, B.x, B.y);
                line(B.x, B.y, C.x, C.y);
                line(C.x, C.y, A.x, A.y);
            }

            // Verification
            if (showVerification && currentStep >= 3) {
                drawPointWithLabel(E, 'E', baseScale);
                drawDashedLine(C, E, baseScale);
                drawDashedLine(K, B, baseScale);
            }

            pop();
        }

        function drawPointWithLabel(point, label, scale) {
            fill(0);
            noStroke();
            ellipse(point.x, point.y, 6/scale, 6/scale);

            push();
            scale(1, -1);
            textAlign(CENTER, TOP);
            textSize(14/scale);
            text(label, point.x, -point.y + 10/scale);
            pop();
        }

        function drawDashedLine(p1, p2, scale) {
            push();
            drawingContext.setLineDash([5/scale, 5/scale]);
            strokeWeight(1/scale);
            line(p1.x, p1.y, p2.x, p2.y);
            drawingContext.setLineDash([]);
            pop();
        }
    </script>
</body>
</html>
