<body>
    <div id="main-container">
        <div id="control-panel">
            <label for="slider-z">z (m)</label>
            <input type="range" id="slider-z" min="0" max="32" step="0.01" value="0">
            <span id="label-z">0.</span>
        </div>
        <div id="plots-container">
            <div id="plot-intensity"></div>
            <div id="plot-population"></div>
            <div id="plot-coherence"></div>
        </div>
    </div>

    <script>
        // Generate time array
        const t_values = Array.from({length: 301}, (_, i) => i - 50);

        // Red curve functions (z=0)
        function I_red(t) {
            if (t < -15 || t > 170) return 0;
            if (t > 15) return 1;
            return 0.5 * (1 + Math.tanh(t/4));
        }

        function P_red(t) {
            if (t < -15) return 0;
            return 4.5 * (1 - Math.exp(-(t+15)/50));
        }

        function C_red(t) {
            if (t < -15) return 0;
            return 2.2 * (1 - Math.exp(-(t+15)/50));
        }

        // Calculate red curves once
        const I_red_data = t_values.map(I_red);
        const P_red_data = t_values.map(P_red);
        const C_red_data = t_values.map(C_red);

        // Blue curve functions (dynamic, function of z)
        function calculateBlueData(z) {
            const t_delay = z / 0.3;

            const I_blue_data = t_values.map(t => {
                const t_prime = t - t_delay;
                if (t > 170 + t_delay) return null;

                let I_base;
                if (t_prime < -15) I_base = 0;
                else if (t_prime > 15) I_base = 1;
                else I_base = 0.5 * (1 + Math.tanh(t_prime/4));

                const envelope = (t_prime > 0) ? Math.exp(-0.035 * z * t_prime / 32) : 1;
                return I_base * envelope;
            });

            const scaling_factor_p = 1 - 0.45 * (z/32);
            const P_blue_data = t_values.map(t => {
                const t_prime = t - t_delay;
                if (t > 170 + t_delay) return null;
                if (t_prime < -15) return 0;
                return scaling_factor_p * 4.5 * (1 - Math.exp(-(t_prime+15)/50));
            });

            const scaling_factor_c = 1 - 0.45 * (z/32);
            const C_blue_data = t_values.map(t => {
                const t_prime = t - t_delay;
                if (t > 170 + t_delay) return null;
                if (t_prime < -15) return 0;
                return scaling_factor_c * 2.2 * (1 - Math.exp(-(t_prime+15)/50));
            });

            return { I_blue_data, P_blue_data, C_blue_data };
        }

        // Initial blue data (z=0)
        let currentZ = 0;
        let { I_blue_data, P_blue_data, C_blue_data } = calculateBlueData(currentZ);

        // Create plots
        const intensityData = [
            {
                x: t_values,
                y: I_red_data,
                name: 'z = 0 meters',
                mode: 'lines',
                line: { color: 'red', width: 2 }
            },
            {
                x: t_values,
                y: I_blue_data,
                name: 'z = 0. meters',
                mode: 'lines',
                line: { color: 'blue', width: 2, dash: 'dash' }
            }
        ];

        const populationData = [
            {
                x: t_values,
                y: P_red_data,
                name: 'z = 0 meters',
                mode: 'lines',
                line: { color: 'red', width: 2 }
            },
            {
                x: t_values,
                y: P_blue_data,
                name: 'z = 0. meters',
                mode: 'lines',
                line: { color: 'blue', width: 2, dash: 'dash' }
            }
        ];

        const coherenceData = [
            {
                x: t_values,
                y: C_red_data,
                name: 'z = 0 meters',
                mode: 'lines',
                line: { color: 'red', width: 2 }
            },
            {
                x: t_values,
                y: C_blue_data,
                name: 'z = 0. meters',
                mode: 'lines',
                line: { color: 'blue', width: 2, dash: 'dash' }
            }
        ];

        // Common layout properties
        function createLayout(yTitle, yRange, label) {
            return {
                xaxis: {
                    title: 'time (ns)',
                    range: [-50, 250],
                    zeroline: true
                },
                yaxis: {
                    title: yTitle,
                    range: yRange,
                    zeroline: true
                },
                margin: { l: 80, r: 20, t: 40, b: 40 },
                showlegend: true,
                legend: { x: 0.7, y: 0.9 },
                shapes: [
                    {
                        type: 'line',
                        x0: 0,
                        y0: 0,
                        x1: 0,
                        y1: 1,
                        yref: 'paper',
                        line: { color: 'black', width: 2, dash: 'dash' }
                    },
                    {
                        type: 'line',
                        x0: 0,
                        y0: 0,
                        x1: 0,
                        y1: 1,
                        yref: 'paper',
                        line: { color: 'black', width: 2, dash: 'dash' }
                    }
                ],
                annotations: [
                    {
                        x: -45,
                        y: yRange[1] * 0.9,
                        xref: 'x',
                        yref: 'y',
                        text: label,
                        showarrow: false,
                        font: { size: 14 }
                    },
                    {
                        x: 0,
                        y: yRange[1] * 0.7,
                        xref: 'x',
                        yref: 'y',
                        text: 'pulse<br>midpoint',
                        showarrow: false,
                        xanchor: 'left',
                        yanchor: 'bottom',
                        font: { size: 12 }
                    }
                ]
            };
        }

        const intensityLayout = createLayout('intensity (arb)', [-0.1, 1.1], '(a)');
        const populationLayout = createLayout('excited-state population × 10<sup>4</sup>', [-0.5, 5.5], '(b)');
        const coherenceLayout = createLayout('coherence × 10<sup>2</sup>', [-0.2, 2.4], '(c)');

        // Plot the graphs
        Plotly.newPlot('plot-intensity', intensityData, intensityLayout);
        Plotly.newPlot('plot-population', populationData, populationLayout);
        Plotly.newPlot('plot-coherence', coherenceData, coherenceLayout);

        // Slider interaction
        const slider = document.getElementById('slider-z');
        const label = document.getElementById('label-z');

        slider.addEventListener('input', function() {
            currentZ = parseFloat(this.value);

            // Update label
            if (currentZ === Math.floor(currentZ)) {
                label.textContent = currentZ + '.';
            } else {
                label.textContent = currentZ.toFixed(5);
            }

            // Calculate new blue data
            const { I_blue_data, P_blue_data, C_blue_data } = calculateBlueData(currentZ);

            // Update blue traces
            Plotly.restyle('plot-intensity', {
                y: [I_blue_data],
                name: `z = ${currentZ === Math.floor(currentZ) ? currentZ + '.' : currentZ} meters`
            }, [1]);

            Plotly.restyle('plot-population', {
                y: [P_blue_data],
                name: `z = ${currentZ === Math.floor(currentZ) ? currentZ + '.' : currentZ} meters`
            }, [1]);

            Plotly.restyle('plot-coherence', {
                y: [C_blue_data],
                name: `z = ${currentZ === Math.floor(currentZ) ? currentZ + '.' : currentZ} meters`
            }, [1]);

            // Update moving vertical line position
            const t_delay = currentZ / 0.3;
            const updateShapes = {
                'shapes[1].x0': t_delay,
                'shapes[1].x1': t_delay
            };

            Plotly.relayout('plot-intensity', updateShapes);
            Plotly.relayout('plot-population', updateShapes);
            Plotly.relayout('plot-coherence', updateShapes);
        });
    </script>
</body>
</html>
